DROP TABLE IF EXISTS admin;
DROP TABLE IF EXISTS zodiac;
DROP TABLE IF EXISTS tarot;

CREATE TABLE IF NOT EXISTS tarot (
  id INT(11) NOT NULL AUTO_INCREMENT,
  contextLove TEXT,
  contextCareer TEXT,
  contextMoney TEXT,
  contextHealth TEXT,
  imgLink VARCHAR(255) DEFAULT NULL,
  PRIMARY KEY (id)
);

CREATE TABLE IF NOT EXISTS zodiac (
  id INT(11) NOT NULL AUTO_INCREMENT,
  context TEXT,
  imgLink VARCHAR(255) DEFAULT NULL,
  zodiacType VARCHAR(255) DEFAULT NULL,
  PRIMARY KEY (id)
);

CREATE TABLE IF NOT EXISTS admin (
  id INT(11) NOT NULL AUTO_INCREMENT,
  adminId VARCHAR(255) DEFAULT NULL,
  adminPw VARCHAR(255) DEFAULT NULL,
  PRIMARY KEY (id)
);


INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('싱글이고, 파트너를 찾고 있다면 바보 카드는 좋은 징조이다. 현재 매우 쉽게 연애를 시작할 수 있다는 것을 의미하는데, 여러분의 미래의 파트너는 재미있고, 매력적이며, 흥미진진하고 근심 걱정 없는 사람이 되기 쉽다. 비록 그것이 더 진지하고 장기적인 연애의 기초가 되지는 않을지라도, 아주 즐거울 것이다.  이미 연애를 하고 있다면, 바보 카드는 여러분이뭔가 충동적인 일을 하도록제안할 수 있다. 예를들어, 도망치거나 혹은 결혼하는 것이다.', '바보 카드는 새로운 직장이나 사업을 시작할 때 나올 수 있는 좋은 카드인데, 단점은 일에 너무 열심인 나머지 과하게 전문가처럼 행동하게 될 수 있다는 것이다. 업무에 있어서 너무 창의적이고 재밌는 것만 쫓지 말고, 지루하더라도 노력해야 하는 일을 잘 처리해야 한다. 이 카드는 여러분이 자신에 대한 의심이나 두려움을 떨쳐 버리고, 새로운 직업이나 방향에 완전히 전념한다면, 위대한 것들이 당신을 기다릴 것임을 나타낸다.', '바보 카드는 재정적인 면에서 매우 긍정적인 카드이다. 곧 새로운 프로젝트가 성과를 거두거나, 직장에서 임금 인상이 기다리고 있는 것을 의미할 수 있다. 본질적으로 이 카드는 돈과 직업의 발전을 위한큰 기회를 상징한다.', '바보 카드는 건강과 활력과 관련이 있다. 만약 당신이 아팠다면, 다시 회복될 것이다.', 'https://blog.kakaocdn.net/dn/c50n3V/btq0c1T9dKm/v5OvJ3kJz1e684tUNql9Sk/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('능숙하고 매끄럽고 유혹적인 마술사는 지적이면서도 충동적이다. 사랑에 빠졌다면, 여러분은 그것을 보여주고 기술과 요령으로 반응할 줄 알아야 하고, 어떤 식의 장난기와 상상력은 일상의 단조로움을 깨뜨릴 것이다. 만약 싱글이라면, 새로운 친구나 연인을 찾을 수 있을 것이다.', '여러분의 일이나 사업은 더욱 쉬워질 것이고,가까운 미래에 더 바빠질 것이다. 일자리를 찾고 있는 사람들은 일자리 제안이나 기회를 얻게 될 것이며, 여러분은 에너지와 자신감으로 이 새로운 출발을 할 수 있을 것이다. 직장이 있는 사람들은 곧 승진할 수 있으며, 기술적으로 새로운 책임을 맡게 될 수 있다. 당신의 잠재력은 당신이 새로운 사업을 시작할 기회를 잡을 수 있게 해 줄 것이며, 이미 사업을 진행 중이라면 개발, 확장할 수 있는 가능성이 있다.', '당신의 노하우로 인해 부업으로 돈을 벌 수 있을 뿐만 아니라, 수입이 늘어날 수 있다. 금전적으로는 위험한 것은 아니지만 변화할 수 있는 성가신 상황에 처할 수 있다.', '마법사 카드는 신체 활력과 관련이 있다. 만약 당신이 아프다면 다시 회복될 것이다.', 'https://blog.kakaocdn.net/dn/OGLzZ/btq0iBs7MnT/6iWlEKEQpoDkSIOLn1DR0k/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('이 카드는 비밀리에 열애가 이루어 진다는 것을 의미할 수 있다. 또한 예상치 못한 행복한 일이 곧 일어날 것을 의미할 수도 있다. 만약 연애 중이라면, 당신은 항상 당신의 감정을 표현하지 않는다. 당신이나 당신의 파트너가 모두 상대방을 의심하거나 불신하는 것은 아니지만 내성적이며, 조용하다.', '비밀리에 일이 꼬이고 있을 수 있고, 혹은 새로운 업무적 기회나 승진의 기회가 비밀리에 전개되고 있다. 인내심을 가지고 기다리면 결국 마땅한 일을 하게 될 것이다. 만약 당신이 사업을 하거나 직장에 다니고 있다면, 새로운 장기 프로젝트나 제품이 비밀리에 개발되고 있을 수 있다.', '금전은 좀 빠듯했거나 예상대로 들어오지 않았을 수도 있다. 당신의 미래와 재정적인 부분을 다른 사람과 상의할 때는 조심해야 한다.', '환경의 변화로 인해 건강 균형이 깨질 수 있다. 또한 아직 밝혀지지 않은 건강상 문제가 있을 수 있으니, 평소에 건강 관리에 신경쓰고 건강 검진을 통해 문제가 있는지 파악하고 적절한 조치를 취하는 것이 좋다.', 'https://blog.kakaocdn.net/dn/dakFQl/btq0dnbvENI/kOItjXDZTP1uKBQrMnj5lk/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('사랑에 있어서 여황제 카드가 나왔다면 이보다 더 긍정적일 수 없다. 연애 중이라면, 여황제의 영향을 받아 더욱 사랑스럽고 사랑이 강렬해질 것이다. 만약 싱글이라면, 조만간 진정한 인연이 당신을 찾기로 결정할 지도 모른다. 그 순간을 즐기면서 순조롭게 진행하면 될 것이다.', '일과 경력에 관해서도 여황제 카드는 매우 긍정적이다. 여황제 카드는 가정이나 패션, 미용 산업 직종을 의미한다. 현재 직업을 찾고 있는 사람이라면, 곧 좋은 직장이 생기며, 직장 안에서리더 역할을 하게 되거나, 특정 프로젝트를 맡게 될 것이다. 만약 지금 직장 생활에 어려움을 겪고 있다면, 여황제는 고민에 사로 잡혀있지 말고, 흐름에 맡기라고 말하고 있는데, 당신의 상황은 이미 상당히 좋아지고 있는데, 당신이 그것을 아직 깨닫지 못했을 수도 있다.', '여러분의 재정은 좋아질 것이며, 여러분의 부를 조금씩 나누기 시작할 때이다. 일반적인 자선 사업을 통해서나, 아니면 단순히 가족이나 친척에게 금전적인 도움을 줌으로써 나타날 수 있다. 또한 지금이 투자하기 좋은 시기이며, 자신이 상대적으로 깊은 수준에서 자신의 본능을 인식할 수 있다면, 자신의 본능을 따르는 것이 좋다.', '여러분이 임신을 계획하고 있다면, 그것은 정말로 임신이 임박했다는 것을 강하게 암시하기 때문에 매우 긍정적이라 할 수 있다. 그러나 만약 임신을 피하고 싶다면 피임에 관해 각별히 주의해야 한다.', 'https://blog.kakaocdn.net/dn/ruFM3/btq0fk6vsO3/m89KsnJHsEIiiUzr0kxGNK/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('만약 당신이 새로운 관계를 맺는다면 황제 카드는 매우 반가운 징조이다. 그 사람은 안정적이며, 결혼이 개인적인 목표하면 당신이편안하게 정착할 수 있는 사람이라는 것을 나타낸다. 이미 연인이 있다면, 당신은 재산을 모으기 위해 연인과 함께 일하고 있다는 것을 나타내며, 함께 아주 편안한 노후를 즐길 수 있다는 것을 암시한다. 싱글이라면, 황제 카드는 당신만큼 헌신적인연인을 곧 만나게 될 것이라는 것을 나타낸다.', '황제 카드는 당신의 모든 노력이 결실을 맺을 것임을 나타낸다. 이것은 승진이나 임금 인상의 형태일 수도 있지만, 새로운 주요 고객이나 사업 기회로 작용할 수도 있다. 일반적으로는 자영업이나 고위직의 리더십을 나타낸다.', '여러분의 재정적인 미래는안정적일 것이다. 만약 돈과 관련된 중요한 결정을 내려야 한다면, 이 카드는 지금이 아주 좋은 시기임을 나타낸다. 그러나 돈에 대해 조금 더 책임감을 가질 필요가 있는데, 당신의 소비 습관에 더 세심한 주의를 기울이고, 재정에 너무 많은 신경을 쓴 것은 아닌지 살펴봐야 한다.', '건강에 대해 너무 걱정하는 것은 오히려 건강에 안좋을 수 있다. 만약 최근에 건강상 문제를 겪고 있다고 해도, 황제 카드는 당신이 괜찮을 거라고 말하고 있다. 대체요법 보다는 의사들의 충고를 잘 따르는 것이 좋다.', 'https://blog.kakaocdn.net/dn/bISGLW/btq0g1yYbGc/L7YfH8OfrZcICsi8esqKi1/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('만약 당신이 사회가 정의하는 전통적인 관계를 찾고 있다면 이 카드는 매우 긍정적인 신호이다. 싱글이라면 누군가가 당신을 좋아하고 있을 수 있다는 것을 나타내며, 이미 연인이 있다면, 여러분은 시간이 지날수록 관계가 더 좋아질 것이다.', '업무적으로 당신은 새로운 단계로 발전할 것이다. 새로운 사람들과 함께 일하게 될 것이고, 그들 중 한 명은 당신에게 꼭 필요한 멘토가 될 수 있을 것이다. 직장에서는 조직적이며 효율적인 것을 유지해야 하며 모든 규칙을 잘 따르는 것이 좋다.', '교황 카드는 지금 당장은 어떤 위험한 상품에 돈을 투자하지 말 것을 경고하고 있다. 금전에 관한 한, 보수적인 통념을 따르는 것이 좋다. 과도한 지출을 피하고 어려울 때를 대비해 저축해두며 금전에 대해 현명하고 신중해야 한다.', '여러분의 건강은 괜찮은 편이지만, 규칙적으로 운동하고 좋은 식습관을 유지하는 것은 지키는 것이 좋다. 만약 건강에 이상이 생긴 것 같은 느낌이 들기 시작하면 의사의 진료와 처방약을 따르는 것이 좋다. 지금은 대체 요법이나 다른 요법들을 실험할 적절한 시기가 아니다.', 'https://blog.kakaocdn.net/dn/IsE9k/btq0dnW3IXM/Y7z3eM7pvclURg2R0fUpQk/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('애정 관계에서조화, 매력, 그리고완벽함을 상징한다. 당신과 당신의 파트너는 솔메이트이며, 그 관계는 깊은 사랑과 신뢰에 바탕을 두고 있어, 장애물을 함께 극복하고 서로에게 힘을 실어줄 수 있다. 또한 연인 카드는 이성과의 관계에서 어떤 성장을 한다는 것을 의미할 수도 있다. 열린 마음의 의사소통이 있는 한 진정한 친밀감으로 발전할 수 있는 잠재력을 가지고 있다.', '직업에 관한중요한 결정을 앞두고 있을 때 종종 연인 카드가 나오는데, 이것은 당신의 선택이 가볍게 이루어지면 안 된다는 것을 말한다. 왜냐하면 그 선택은 상당한 결과를 가져올 것이기 때문이다. 당신의 마음과 열정을 따르는 것이 좋으며, 장기적으로 행복과 성공으로 이어질 것이다. 처음에는 더 어려워 보이는 선택이 옳은 선택인 경우가 많다.  또한 이 카드는 비즈니스 파트너십이 유익한 관계가 될 것이며, 여러분과 여러분의 연인이 오랫동안 그런 좋은 관계를 맺고 있다는 신호일 수 있다.', '연인 카드는 상호간의 매력을 상징한다. 사람들 사이뿐만 아니라, 당신과 돈 사이에서도 그렇다. 최근에 돈이 모자랐다면, 도움을 요청하고 자신의 문제를 다른 사람에게 알렸을 때만 지원을 받을 수 있는 것이다. 지금은 창업, 대출 요청, 복학과 같은 경제적 위험을 감수하기 좋은 시기이다.', '여러분의 심장은 곧 힘을 얻을 것이다. 이것은 여러분의 신체적인 심장이 될 수도 있고, 여러분의 감정적인 심장인 슬픔에서 회복될 징조가 될 수도 있다.', 'https://blog.kakaocdn.net/dn/bBOBWH/btq0c2eswxl/QKkKkv0IQnCOINBp5QMUn0/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('전차는 일반적으로자신을 사랑하는 것의 중요성을 나타낸다. 진지한 연인 관계를 추구하기 전에 여러분의 초점을 자신에게로 옮기고 스스로의 감정을 평가하는데 시간을 가져야 한다. 만약 여러분이 이미 연애 중이라고 한다면, 여러분자신을 돌볼 수 있는 시간을 찾도록 하는 것이 좋다. 가까운 미래에 자신의 목표를 우선시하는 것이 중요해질 수도 있다.  전차 카드는 과도한 진압의 표시로 해석되기도 하는데, 아마도 당신은 누군가를 너무 억지로 쫓고 있거나, 당신의 중요한 다른 사람을 질식할 정도로 억압하고 있을 수 있다. 한 걸음 뒤로 물러서서 여러분의 행동과 목표를 다시 평가해야 한다.', '동료들이 당신을 어떻게 생각하는지 지나치게 신경 쓰는 것은 당신을 망설이고 주저하게 만든다. 여러분의 야심찬 아이디어는 동료들에 의해 방해받을 수도 있지만, 자신의 입장을 견지하는 것을 두려워할 필요가 없다. 지금 당신의 생각에 자신감을 갖는 것은 결국 결실을 맺을 것이다. 현재 직업을 찾고 있는 경우 통신, 소프트웨어 또는 품질 관리 관련 직업을 고려해 보는 것이 좋다.', '당신의 재정은 횡재나 임금인상의 형태로 좋아지려고 한다. 빚이 있다면 지금이 갚을 수 있는 절호의 기회이며, 빚이 없어지면 자전거나, 자동차와 같은 이동수단이나 비행기표를 사는 것을 고려해 볼 수 있다.', '지금과 같은 당신의 높은 에너지 수준은 영원히 지속되는 것은 아니다. 당신의 늘어난 체력과 활력으로 꾸준히 운동을 하면 기대했던 것 보다 건강이 더 좋아질 것이다.', 'https://blog.kakaocdn.net/dn/b8LwGv/btq0iWw7xXd/1qyZdBEbOVAKGbhu91Auqk/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('싱글이라면 지금이 새로운 누군가를 만나기에완벽한 시기라는 것을 보여준다. 여러분의 자신감은 흥미진진하고 파격적인 누군가와의 즐거운 연애로 보상받을 것이다. 이미 연애 중인 사람이라면 이 카드는 연인 관계가 매우 견고하다는 좋은 지표이다. 어떤 문제가 발생하더라도 함께 극복하면서 유대감을 강화할 수 있을 뿐 아니라, 앞으로도 지속적으로 좋은 관계가 이어질 것임을 나타내기 때문이다.', '지금이 당신의 경력을 더 발전시키기 위한 조치를 취해야 할 때이다. 자신감 있는 태도로승진이나 임금 인상을 요구하는 것도 좋다. 만약 여러분이 벤처 사업을 고려하고 있다면, 힘 카드는 여러분의 도약을 응원한다. 두 발로 뛰어드는 것은 위험해 보일 수 있지만, 지나치게 조심한다면 몰락만을 가져올 뿐이다.  만약 현재 직업을 찾고 있다면, 힘 카드는 성공을 위해 열정을 따를 것을 강조한다. 다른 사람들, 특히 취약 계층 사람들을 돕는 직업이 잘 맞을 수 있으며, 혹은 동물들을 보살피는 직업이 좋을 수 있다.', '당분간은 금전적 흐름이 좋을 것 같다. 횡재를 의미하는 것은 아니지만, 안정적인 재정 증가, 예를 들면 임금 인상 등이 곧 다가올 것이다. 지금은 평소보다 저축을 늘리는 것이 좋으며, 가까운 장래에 거액의 돈을 빌리지 말아야 한다.', '정방향 힘 카드는 여러분의몸과 마음이 모두 좋아질 것이라는 환상적인 지표이다. 이런 시기에 나쁜 습관을 버리고, 운동 같은 새로운 건강한 습관을 들이도록 노력하면 효과는 더욱 좋을 것이며, 예상보다 쉬울 것이다.', 'https://blog.kakaocdn.net/dn/c7DGw6/btq0mxQRBfs/fh9Jpnuy0z3jFucw5xMKck/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('정방향 은둔자 카드는 당신이 주로 자기애에 관심이 있다는 것을 나타낸다. 여러분은 개인적인 여행을 하고 있다. 명상을 통해 자신이 누구인지에 대해 스스로 감사하는 법을 배울 것이다. 이영적인 탐구를 하는 동안 여러분이 자신을 어떻게 보고 있었는지 생각하고, 개인적인 장점을 인식하면서 시간을 보내는 것이 좋다.  오래된 감정적 단절과 해결되지 않은 갈등은 그냥 놔두어라. 여러분의 유대감을 강화하고, 함께 깨달음에 도달하기 위해 여러분이나 여러분의 파트너가 가지고 있는 감정적인 짐들을 서로에게 말함으로써 진실을 찾아야 한다.', '당신은 현재 직업이 당신에게 적합한지, 혹은 이직을 해야 할 지에 대해 고민하고 있을 수 있다. 은둔자 카드는 지금이진실을 추구하고, 개인적인 발전을 위한 좋은 시기라고 말한다. 현재 직업이 자신에게 잘 맞는지, 또는 직장에서의 문제를 가장 잘 해결할 수 있는 방법이 무엇인지 생각해보라. 그 해답은 동료와 지식을 공유하는 것에서 찾을 수도 있다.', '당분간은 돈에 대해 보수적이어야 한다. 자신에게 투자하고, 사치적인 것에 지출을 하려는 유혹을 뿌리쳐야 한다. 물질적 산만함을 피하고, 돈의 진정한 합리적 목적에 대해 인식해야 한다.', '자신을 위한 시간을 갖는 것은 당신의 건강에 휴식을 주고 스트레스를 완화시켜 줄 것이다. 걱정을 덜고, 잠을 더 잘 잘수 있도록 명상이나 산책 또는 요가를 해보는 것도 도움이 될 것이다.', 'https://blog.kakaocdn.net/dn/d1oJXS/btq0g0Uj1kF/VUe5rQzHMTjQEIkcCnDQsK/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('이 카드는 여러분의 연애 생활과 운명에 긍정적인 변화를 의미한다. 만약 여러분이 미혼이라면, 이 카드는 여러분이 곧 진정한 사랑을 만날 것임을 의미할 수 있기 때문에, 지금이야말로 자신을 밖으로 드러낼 필요가 있는 좋은 시기이다. 만약 이미 연인이 있다면, 이 카드는 당신과 당신의 파트너가 힘든 시기를 겪고 있으며, 곧 변화가 생길 것임을 보여준다. 여러분은 연인과의 의사소통이 향상될 수 있고, 관계가 개선될 수 있으며, 혹은 결별을 하게 될 수도 있다.', '운명의 수레바퀴 카드는 직업 변화에 대한 좋은 징조이기 때문에 지금이 바로 여러분이 꿈에 그리던 직업에 지원해야 할 때이다. 또한 이 카드는 승진이 여러분에게 오고 있다는 것을 의미할 수 있으니 정신을 차리고 열심히 일해야 한다.', '금전적으로 문제가 있었다면 이제 끝이 보인다. 직장에서 임금을 받게 되거나, 친구나 가족의 도움을 받게 될 수도 있다. 그러나 현재 당신이 경제적으로 안정되어 있다면, 당신은 곧 돈이 부족하게 될 수 있으니, 당분간 저축을 더 늘리고 재정적인 문제가 생기면 빨리 해결하는 것이 좋다.', '이 카드는 변화를 상징한다. 만약 여러분이 건강 문제로 고통 받고 있었다면, 곧 회복될 것이다. 만약 최근에 계속 건강했다면, 곧 부상을 입거나 병에 걸릴 수 있으니 주의해야 한다. 그러나 그 질병들은 심각하지는 않을 것이다. 다만 이 카드는 자신을 더 잘 돌보고 너무 무리하지 말라는 경고의 표시이다.', 'https://blog.kakaocdn.net/dn/bE8P2n/btq0jN2btVp/P7dtim9R5m7SYeyTrWJM50/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('애정면에서 정의 카드는 과거에 당신이 사람들을 어떻게 대했느냐에 달려 있다. 만약 여러분이 다른 사람들을 친절과 존경으로 대했다면, 우주는 그 대가로 여러분에게 친절과 존경을 보낼 것이다. 그러나 반대로 남을 냉대하거나 멸시했다면 정의의 분노를 느낄 것이다.', '여러분은 더 나은 일과 삶의 균형을 찾아야 한다. 여러분이 사무실에서 너무 많은 시간을 보내고 있거나, 자신의 직업에 너무 집중하고 있어 사랑하는 사람들과 충분한 시간을 갖지 못하고 있다. 정의 카드는 일이 인생에서 가장 중요한 것이 아니라는 것을 상기시켜 준다.', '빚이 있다면 곧 갚을 수 있게 되는 좋은 징조이다. 재정 상태가 좋아 질 수 있기 때문에, 만약 당신이 최근 금전적으로 너무 인색했다면, 다른 사람에게 도움을 주는 것도 좋다. 지금은 유언장 작성, 양육비 신청, 소액 청구 등 법적 문제를 다루기에 좋은 시기이다.', '정의 카드는 균형과 업보에 관한 모든 것을 의미한다. 만약 여러분이 제대로 먹고 운동을 해왔다면 여러분은 계속해서 열심히 노력한 결과를 보게 될 것이다. 만약 여러분이 게으름을 피웠다면 생활 방식을 개선하여 건강을 최우선으로 삼아야 한다.', 'https://blog.kakaocdn.net/dn/cjgKUs/btq0qA05IoK/VteGxWkMtw3yziyOblEbt0/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('만약 당신이 연애를 하고 있다면, 이 카드는 당신이 알고 있는 것보다 당신이 파트너에게 더 불만이 있을 수도 있다는 것을 나타낸다. 고통스러울 수도 있지만, 연인과의 관계를 재고해 애인에게 애정 어린 작별을 고해야 할 때이다.동시에 이 카드는 파트너가 여러분을 이렇게 생각하고, 관계를 계속 진행할 가치가 있는지 여부를 결정한다는 경고일 수 있다. 이것은 반드시 부정적인 것만은 아니다. 왜냐하면 여러분의 파트너는 그들이 과거에 여러분에게 잘못했다는 것을 깨닫고 로맨스에 다시 불을 붙이고 싶어 할 수 있기 때문이다.싱글인 경우, 이 카드는 여러분을 불행하게 만들고 있는 사람들, 생각, 또는 상황을 놓아야 한다는 것을 암시할 수 있다. 만약 여러분이 여전히 옛 애인을 갈망한다면, 교수형 남자는 여러분에게 이 감정들을 버릴 때라고 말하고 있다. 그것들은 여러분에게 고통을 가져다 줄 뿐이다.', '당신은 틀에 박혀 있는 당신의 직업에 질렸을 지도 모른다. 이제는 상황을 바꾸기 위해 현실적으로 할 수 있는 일이 있는지 살펴봐야 할 때이다. 만약 그렇지 않다면, 여러분은 한 발 물러서서 잠시 시간을 끌 필요가 있다. 결국 상황이 더 나아질 것이지만, 여러분이 바라는 만큼 그렇게 빨리는 아닐 수도 있으며, 지금은 새로운 직장이나 직종을 찾기 시작할 좋은 시기일 수 있다.', '지금이 바로 돈에 대한 자신의 생각을 재평가할 때이다. 만약 여러분이 인색했다면, 부를 나눌 줄도 알아야 한다. 그렇게 하는 것이 여러분이 생각하는 것보다 더 유익할 수 있다. 반대로 자신이 그동안 금전적으로 너무 안일하게 관리해 왔다면, 그 또한 재평가해봐야 한다. 본질적으로, 여러분은 금전적으로 해왔던 것과는 반대로 행동할 필요가 있다.', '재정과 마찬가지로, 이제는 건강과 건강관리에 다른 관점에서 접근해야 할 때이다. 예를 들어, 만약 여러분이 체육관에서 역기를 가지고 운동을 하고 있었다면, 아마도 여러분의 일상에 요가나 타이치 같은 것을 추가할 때일 것이다. 당신의 육체적 건강과 관련하여, 이 카드는 당신에게 모든 것이 괜찮으며, 당신이 통제할 수 없는 것에 대해 걱정하지 말라고 말하고 있다.', 'https://blog.kakaocdn.net/dn/c23cmx/btq0qA7RMHm/hPJhX5GY67qfOTzDkGJB31/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('애정면에서 죽음 카드가 의미하는 것은 매우 부정적이다. 그러나 여러분의 다음 관계는 과거에 경험했던 어떤 관계와도 근본적으로 다를 것이라고 말하고 있다.이 잠재적으로 위대한 로맨스를 보기 위해서, 여러분은 먼저 짐을 잃어버리고 새로운 누군가를 여러분의 삶에 들여놓을 수 있도록 여러분의 습관, 태도, 패턴을 바꿀 필요가 있다.', '일에 너무 의존하거나 현실에 안주하지 말라는 경고이다. 삶의 상황은 영원히 변한다. 그리고 여러분은 새로운 직업을 찾도록 강요 받는 자신을 발견할 수도 있을 것이다.만약 여러분이 새로운 직업을 생각 중이거나 새로운 사업을 시작하려고 생각했지만 현재 고용의 안전 때문에 꺼려했다면, 죽음 카드는 여러분에게 지금이 도약할 때라고 말하고 있다. 그 변화는 긍정적일 것이다.', '다가오는 재정 문제를 해결하기 위해 몇 가지 희생을 감수해야 한다. 이런 일이 일어날 때 너무 초조해 하지 말고, 상황을 정면으로 직시하고 이러한 어려움에서 살아남기 위해 해야 할 일을 찾아야 한다. 궁극적으로, 여러분은 이 기간 동안 배우게 되는 모든 것에서 이익을 얻을 것이다.', '인생의 고난에 대해 당신의 스트레스 수준을 관리할 수 있도록 가능한 모든 것을 하라. 이러한 어려움에 대처하기 위해 음주나 약물에 의존하지 말고, 내면을 들여다보고, 당신의 에너지를 영적인 면에 집중시키는 것이 좋다.매일 운동하고, 명상하고, 요가를 시도해 보는 것도 좋을 것이다. 여러분의 몰락으로 이어질 수 있는 물질들을 통해 여러분의 신체적, 정신적 건강을 남용하지 말아야 한다.', 'https://blog.kakaocdn.net/dn/G1RkN/btq0iW57FhZ/MKRR7ggq3djjg5cKPWsAw1/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('연인 사이에 절제 보다 더 좋은 카드를 뽑기를 바랄 수 없을 것이다. 그것은 사랑, 헌신, 그리고 소울메이트들 사이의상호 존중의 완벽한 균형을 상징합니다.  최근에 연애에 어려움을 겪었다면, 절제 카드는 여러분과 여러분의 파트너가 모든 문제를 조화롭게 해결할 것이며 여러분의 조합이 확고한 기반을 형성하고 있다는 것을 나타낸다.만약 여러분이 미혼이라면, 이 카드는 여러분의 삶의 다른 부분들을 균형 있게 하는 법을 배우게 될 것이고, 여러분의 세상에 새롭고 특별한 연인이 들어올 것임을 나타낸다.', '지금은 당신이 원하는 모든 것을 성취할 수 있는 인내심과 끈기가 있기 때문에 새로운 직업 목표를 세우기에 완벽한 시기이다.여러분의 노력과 헌신이 윗사람들에 의해 적절히 기록되었다는 것에 안심할 수 있지만, 만약 여러분이 승진을 원한다면 여러분은 인내심을 가지고 여러분에게 주어진 첫 번째 기회에 본능적으로 뛰어서는 안 된다는 것을 기억해야 한다. 모퉁이를 돌면 훨씬 더 좋은 것이 있을 것이다.', '당신의 재정 상태가 마침내 안정되기 시작하고 있고, 만약 당신이 빚을 졌었다면, 머지않아 갚을 수 있을 것이다. 이것은 여러분이 복권에 당첨되거나 10억 달러의 상속금을 받게 될 것이라는 것을 의미하는 것이 아니다. 단지 여러분이 전형적으로 그랬던 것보다 경제적으로 좀 더 편안해질 것임을 나타낸다.그럼에도 불구하고, 당신은 당신이 좀더 검소해질 필요가 있다는 것을 깨닫고 받아들이기 위해 여전히 돈과의 관계를 재평가할 필요가 있다.', '만약 여러분이 건강을 해칠 수 있는 나쁜 습관이 있다면, 지금은 그것들을 고치기 위한 시도를 하기 좋은 때이다. 운동을 더 자주 하거나, 담배를 끊거나, 균형 잡힌 식사를 해야 한다.', 'https://blog.kakaocdn.net/dn/zITY7/btq0oK4dRqh/ik2FeBkiwrsm0bnBw3Ury0/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('사랑에서 데빌 카드는 여러분의 관계가 정체되고 있다는 것을 나타낼 수 있다. 여러분이나 여러분의 파트너는 강한 분노가 형성될 수 있을 정도로 갇혀 있다고 느끼고 있다. 지금은 여러분이 여러분의 관계를 회복하고 싶은지 아니면 단순히 끊고 싶은지 결정할 때이며, 어느 쪽이든현상유지는 불가능하다.싱글이라면, 당신이 파트너들과 만족스럽지 못한 관계에 있다는 것을 나타낸다. 연인 중 한 명 이상이 위험함을 나타낼 수도 있다.', '악마 카드는 여러분이 완전히 경멸하는 완전히 성취되지 못한 일에 갇혀 있다는 것을 나타낸다. 첨단 기술과 IT 분야의 직업은 직장 내 괴롭힘과 마찬가지로 악마 카드와 밀접한 관련이 있다.직장 내 괴롭힘의 희생자가 되는 것이 여러분이 자신의 직업을 그토록 싫어하는 근본적인 이유가 될 수 있다.', '돈은 곧 부족하겠지만 당신은 그 상황에 영향을 미칠 힘이 전혀 없다. 악마 카드는 여러분이 소비 습관을 억제하기 시작하고 예측 가능한 미래를 위해 매우 보수적일 필요가 있다는 경고로 받아들여질 수 있다.유혹을 피하고, 경제적으로 어려운 이 기간 동안 더욱 조심하라.', '건강면에서 데빌 카드는 여러분이 현재우울증이나 중독과 같은 절망적인 질병의 위험에 처해 있다는 것을 보여주는 강력한 지표이다. 당신은 지금 당장 당신의 나쁜 습관에 대해 각별히 주의할 필요가 있다. 만약 여러분이 중독에 빠진다면, 그것은 통제 불능의 상태가 될 것이기 때문에 심각해지기 전에 도움을 요청하는 것이 좋다.악마는 또한고혈압과 같은 무언의 질병에 걸릴 위험을 암시할 수 있다. 특히 최근에 특별히 몸이 좋지 않았다면 지금은 건강 검진을 받으러 가기에 좋은 시기이다.', 'https://blog.kakaocdn.net/dn/cQ5rtu/btq0n94dzkM/nFPfwYBRPpS5SRxL0jqE6k/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('애정 면에서 탑 카드는 주로 갈등이나 불건전한 관계를 상징하며,보통 예상치 못한 일이 일어나고 있다는 것을 암시한다. 예를 들어, 여러분의 애인이 여러분의 친구 중 한 명과 바람을 피우고 있다는 것을 발견하는 것과 같다. 마찬가지로, 만약 당신이 당신의 파트너를 속여왔다면 진실은 곧 밝혀질 것이며, 당신의 부정행위의 결과에 직면하게 될 것이다.만약 여러분이 싱글이라면, 이 카드는 여러분이 외로움을 느끼고 있다는 것을 나타내며,여러분의 과거 연인과의 관계에서 비롯된 문제로 여전히 고통을 겪고 있을 수도 있다.', '직업상 탑 카드는 부정적인 환경이나 적대적인 환경에서의 작업이나 소방관, 용접공, 군인 등 잠재적으로 위험한 고용을 의미한다. 그것은 종종 고용 안정성의 결여나 실제 고용 손실을 나타낸다.동시에, 이것은 스트레스와 같은 감정적인 변화가 일어나려고 한다는 것을 나타낸다.', '이 카드가 나온다면 특히 지출에 신중해야 한다. 가장 부정적인 것은 타워가 실패와 파산을 예고한다는 것인데, 그것은 아마도 반복적인 부실 투자의 결과일 것이다. 최악의 경우에 대비해야 한다. 기다리고 있는 재정적인 재앙은 전적으로 당신의 통제에서 벗어날 것이기 때문이다. 시장이 붕괴될 수도 있고, 국세청의 감사를 받게 될 수도 있고, 많은 돈을 빚지게 될 수도 있다.', '여러분은 특히 질병에 취약하다고 느끼고 있으며, 건강하게 살기 위해 노력해야 한다. 비록 여러분이 현재는 힘겹거나 아프다고 느끼지 않더라도, 혈액 검사와 엑스레이를 포함한 전체 건강 검진을 계획하는 것이 좋을 것이다.', 'https://blog.kakaocdn.net/dn/vjTB3/btq0kDLNh5c/vYt0pjie4PNYGcsMXMy1rK/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('연애하는 사람들은 이제한 단계 더 나아가야 할 때이다. 같이 살거나, 새로운 취미를 함께 할 때가 될 수도 있다. 특히 여러분 둘 다 전에 시도해보지 않았던 것을 선호한다.만약 당신이 미혼이라면, 당신은 오래 걸리지 않아 좋은 연인을 만날 것이다. 그러나 여러분의 원래 이상형은 아닐 수도 있다. 당신의 연애 생활에 있어서 변화가 필요하며, 연인을 만나는 것을 두려워하지 않는 것이 좋다.', '새로운기회에 열려 있으니, 새로운 기회가 나타났을 때 그 기회를 활용할 준비가 되어 있어야 한다. 이것은 승진, 잠재 고객, 또는 해외 취업일 수 있다.만약 여러분이 자신의 상황에 만족하지 않았다면, 지금이 더 푸른 목초지를 찾기 시작할 좋은 때이다. 엔터테인먼트 산업, 공공 서비스 또는 디지털 커뮤니케이션 분야의 업종을 생각해 보는 것이 적합하다.', '지금은 경제적으로 좋은 시기이다. 적은 투자를 몇 번 하는 것은 결국 결실을 맺을 것이다. 전반적으로 별 카드는 재정적인 행운의 상징이지만 무한한 행운은 아니고,작은 도약이다. 위험을 잘 계산하여 재정을 관리하는 것이 좋다.', '별 카드는 건강에 관한 한 가장 좋은 카드 중 하나이다. 사소한 의학적 고민이 있었다면 간단한 검진만으로도 안심할 수 있다. 당신은 모든 신체검사를 당당히 통과할 것이다.      ', 'https://blog.kakaocdn.net/dn/ZE9Et/btq0pxctK5c/aMqayaJLsNYqJiAU5lhGNK/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('연애 중이든, 싱글이든 상관없이 달은변화에 대한 갈망을 나타낸다. 이것은 여러분의 목표에 대해 파트너와 진지한 대화를 나누는 것을 의미할 수도 있고, 변화를 원하지 않는 사람과 헤어지는 것을 의미할 수도 있다. 겉으로 보기에 여러분의 연애 생활을 다른 사람들이 부러워할지 모르지만, 완벽한 베일로 가리기 위해 여러분의 파트너의 단점을 눈감아 주지 말아야 한다.만약 여러분이 미혼이라면, 지나치게 사랑에 대해 열망하지 말아야 한다. 여러분이 관심을 두고 있는 사람에게는 숨겨진 의도가 있을지도 모른다.', '당신은 의사소통이 잘 되지 않아 당신의직업에 불만이 있을 수 있다. 주변 사람들과 솔직한 대화를 시작하라. 전체를 맑게 하는 것은 어렵지만, 직장에서 자신만의 행복을 찾는 것은 중요하다.만약 당신이 일자리를 찾고 있다면, 상당한상실감을 느낄지도 모른다. 아마도 당신은 직업을 위해 학교 교육이나 훈련을 받았지만, 적당한 일을 찾을 수 없었을 것이다. 새로운 길을 탐험하는 것을 두려워하지 마라. 여러분은 기쁨과 성취감을 느낄 수 있는 무언가를 발견하게 될 것이다.', '가까운 누군가가 당신의 재정 상태를 이용하려고 하기 때문에, 대규모 투자는 역효과를 낼 가능성이 있다. 그들의 충고를 듣는 것은 당신을 곤경에 빠뜨릴 것이다.', '달 카드는 전형적으로 정신 상태가 좋지 않음을 나타낸다. 여러분의불안감이나우울함이 여러분의 인생 여행을 방해할 수 있기 때문에, 정신 건강 전문가와 상담해 봐야 할 수도 있다.      ', 'https://blog.kakaocdn.net/dn/Xpj9z/btq0iWrwHaa/M6WcRV1GypeRkQD5OGJXQK/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('태양과 같은 자신감으로, 여러분은 자신과 주변 사람들 모두를 사랑한다. 여러분은 완벽하지 않을 수도 있지만, 자기 성찰과 긍정적인 변화를 위한 능력이 여러분의 삶에 흥미로운 새로운 사람들을 끌어들이고, 여러분에게 새로운 낭만적인 관계를 탐구할 기회를 제공할 것이다.항상 곁에 있어준 사람들과 함께 시간을 보내는 것도 잊지 말고, 그들이 얼마나 중요한지 표현해 보는 것도 좋다. 여러분의 사랑은 그들의 삶에서 긍정적인 힘이고, 그들은 여러분과 더 자주 함께 하려 할 것이다.만약 여러분이 이미 연애를 하고 있다면, 태양은 그것에 대해 밝은 미래를 나타낸다. 여러분의 신뢰는 여러분의 파트너에게까지 확장되며, 파트너에게는 계속해서 감사하고 신뢰하게 될 것이다. 만약 여러분이 관계에 문제가 있다면, 태양은 개방성과 명확한 의사소통을 장려합니다.태양은 또한미래에 중요하고 긍정적인 사건이 일어날 것이라는 것을 나타낸다. 당신의 소울메이트를 찾거나, 결혼하거나, 심지어 아기를 갖는 것은 무엇이든 가능하다.', '전문적인 업무에서 특히 태양은 훌륭한 징조이다. 여러분의 참여는 특정 프로젝트의 성공에 결정적일 것이며, 여러분의 노력과 낙관적인 전망은 주변 사람들에게 알려질 것이다.힘든 일과 당신의 밝은 기질이 성과를 거두어, 당신이 직업적으로새로운 높이에 도달할 수 있게 할 것이다. 이는 동료 및 상사와의 관계 개선을 의미할 수도 있으며, 또한 여러분이 승진을 할 것이라는 것을 의미할 수도 있다.이 성공은 전적으로 여러분의 몫이므로, 여러분의 노력의 성과를 즐기고, 여러분의 성취를 축하하는 시간을 갖는 것을 잊지 마라.', '당신의 업무가 잘 되어가고 있기 때문에, 당신의 재정 상태는 긍정적으로 보인다. 부업으로 약간의 돈을 벌 수 있는 기회를 볼 수도 있고, 당신이 가지고 있는 어떤 빚도 갚을 수 있다.그것은 당신이 그동안 열심히 일한 것에 대한 보상이니, 즐겁게 쇼핑을 하는 것도 좋다.', '여러분의 인생관과 심리적 행복은 여러분의 신체 건강에 긍정적인 영향을 준다. 지금은 새로운 취미나 스포츠를 시도하기에 완벽한 시기이다.     ', 'https://blog.kakaocdn.net/dn/edzDEj/btq0jN2cnte/Q8XO3LK4SFC1KfCv6GHfB1/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('연애 중이라면, 판단력이 중요하다는 것을 나타내며, 현재 파트너가 반드시 솔메이트라는 뜻은 아니다. 이 카드는 당신과 연인의 관계가 길고 유익할 것임을 의미할 수도 있지만, 반대로 여러분의 관계가 짧지만 가치 있는 학습 경험이 될 것이라는 것을 의미할 수도 있다. 만약 현재의 연인 관계가 끝난다면, 앞으로 당신에게 도움이 될교훈들을 얻게 될 것이다.싱글인 경우, 이 카드는 일반적인 환경 이외의 다른 사람과 데이트를 시도해야 한다는 것을 나타낸다. 아마도 당신은 잠재적인 파트너의 외모나 지위에 너무 신경을 많이 써왔을 것이다. 그러나 시선을 약간 돌려서 다른 배경을 가진 사람들에게 관심을 가져 본다면, 여러분이 놓칠 수도 있었던 행복하고 안정적인 연애를 시작하게 될 수도 있다.', '아직 본인은 잘 모를 수도 있지만, 매니저나 헤드 스카우트는 여러분의 일을 계속 주시하고 있다. 당신은 승진이나 새로운 일에 스카우트될 수 있으니, 언제나처럼 열심히 일하면서 업무에 좋은 성과를 내도록 노력해야 한다.만약 여러분이 구직 중이라면, 정치, 법률, 또는 직업소개소에 관련된 업종을 생각해 보라.', '재정분야에서 심판 카드는 나쁜 신호이다. 지출을 최소로 줄이고 보장된(안전한) 투자 외에는 모두 피하도록 하라. 당신의 인생에서 중요한 것들에 대한 보험을 추가로 가입하는 것을 고려해보는 것도 좋다.', '정신적으로나 육체적으로나 심판 카드는 건강에 관한 한 좋은 징조이다. 여러분은 실수를 통해 배웠고 이제 더 건강한 내일을 향해 나아갈 준비가 되었다.', 'https://blog.kakaocdn.net/dn/mGmuc/btq0nO8h79s/4jFZicWhXvBTz7cZpu7jfK/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('만약 여러분이 연애를 하고 있다면, 여러분은 파트너와 꽤 행복할 것이다. 여러분은 결혼, 동거, 자녀, 또는 연인과의 공동 목표에 도달하기 위한 길을 잘 가고 있다. 그러나 이 카드가 반드시 현재 파트너의 요구가 충족되고 있다는 것을 나타내는 것은 아니다. 의사소통을 많이 해서 두 사람 모두 정서적으로 만족감을 느낄 수 있도록 노력하는 것이 좋다.만약 여러분이 미혼이라면, 지금은 밖에 나가서 새로운 사람들을 만나기에 좋은 시간이다.', '당신은 이루기 위해 열심히 노력한 꿈과 목표에 가까워지고 있다. 여러분의 직업적 목표에 더 가까이 다가갈 수 있는 기회를 계속 추구하라.', '재정적으로는 보수적으로 관리하는 것이 좋으며,신중한 투자와 저축을 위해 도박과 고 위험성 투자는 피하는 것이 좋다.', '세상 카드는 건강에 있어서 매우 긍정적인 카드이다. 당신의 건강 상태는호전될 것이고 곧 좋아질 것이다. 비록 여러분이 가까운 미래에 새로운 건강 문제에 직면할지라도, 이것들은 별 문제가 되지 않을 것이다.', 'https://blog.kakaocdn.net/dn/D0HFX/btq0tYhhW1v/kULDTfOZzj4SZ55eGpp3E1/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('사랑에서, 에이스 완즈 카드(Ace of Wands)는 여러분이 연인과의 관계에서 새로운 단계로 접어들고 있다는 신호이다. 이 새로운 시대는 열정, 재미, 그리고 흥분으로 특징지어질 것이니, 모험을 준비하라.싱글인 사람들은 용기를 내서 관심을 갖고 있는 사람에게 손을 내밀어 보라는 카드이다. 새로운 시작의 카드로서, 이 카드는 종종 다산과 임신을 상징하기 때문에, 만약 여러분이 아기를 가지려고 한다면 좋은 소식이 있을 것이다.', '여러분의 열정과 강한 직업윤리로 새로운 아이디어를 생각해내성과를 거둘 것이다. 지금 당장 프로젝트를 시작하면 직업적인 평판이 좋아지고 승진할 수도 있다.특히 한동안 직업을 찾지 못하고 있었던 사람의 경우에는 곧 새로운 직장이 눈앞에 펼쳐질 수도 있다.', '소액의 일시금으로 선물, 상속 또는 복권 당첨금으로 받게 될 수도 있다. 이 돈을 창의적인 프로젝트에 투자하거나 미래에 투자하는데 쓰는 것도 좋을 것이다.', '완즈 에이스는 곧 임신이 될 것이며, 임신은 건강하고 성공적일 것이라는 것을 나타낸다. 아기를 가지려고 하는 것이 아니라면, 이 카드는 체중 감량을 시작하는 것을 의미할 수도 있다.', 'https://blog.kakaocdn.net/dn/b7iiky/btq0tfcHCVi/ktIVAXgMgLbB9gGITkUMC1/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('싱글이라면 여행을 떠나거나 새로운 동네를 찾아다니며 새로운 사람들을 만나는 것은 새로운 로맨스로 이어질 것이다. 사랑을 찾을 때, 여러분이 원하는 것을 성찰하고 다른 사람이 여러분의 인생길을 바꾸지 않도록 하라.연애 중이라면 여러분이 좋은 파트너이고 서로에게 잘 어울린다는 표시이다. 당신은 탄탄한 기반을 가지고 있고 미래를 위한 계획을 세울 준비가 되어 있다. 가까운 미래에 함께 이사하거나 결혼하는 것과 같은 서로에게 장기적인 약속을 하게 될 것이다.', '이 카드는 당신의 커리어에길조를 의미하며, 당신의 업무적인 성과는 더욱 높아질 것임을 나타낸다. 또한 여러분이새로운 도시나 나라에서 일하도록 노력해야 한다는 신호이기도 하다. 신중하게 계획하는 한, 당신의 미래는 밝아 보인다.', '이 카드는 재정적인 안정과 균형을 상징한다. 당신은 목표에 도달하기 위해 잘 가고 있다. 또한 곧 새로운 곳에서 휴가를 보낼 여유가 생길 것이다.', '여러분은 건강을 증진시키기 위해 노력해왔으며, 노력의 결과를 보기 시작했다. 건강한 생활 습관을 유지함으로써 웰빙의 길을 계속 가는 것이 좋다.    ', 'https://blog.kakaocdn.net/dn/C9OEK/btq0qzP3Yf1/2tbkRK8s4iQdMc04uluKsK/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('여러분의 관계는 잘 진행되고 있고, 여러분과 여러분의 파트너 모두 편안한 속도로 발전하고 있다. 여러분은 곧 함께 여행할 시간을 가질 것이고, 이것은 여러분이 서로에 대해 새로운 것을 배울 수 있게 해 줄 것이다. 또한 이 카드는 여러분과 여러분의 파트너가 장거리 연애를 할 수도 있다는 것을 의미할 수 있다.  만약 여러분이 미혼이라면, 여러분은 휴가 중에서나 혹은 외국 여행을 할 때 연인을 만나게 될 수 있고,이전엔 관심이 없었던 사람과 이어질 수도 있다.', '다른 사람들이 간과해 온 분야에서 일하고 있는 당신의 직종이새로운 가능성을 보게 될 수 있다. 혹은 일반적으로 실용적이지 않다고 생각되는 면에서 돈을 벌 수 있는 획기적인 아이디어가 생길 것임을 나타낼 수도 있다. 완즈 3번 카드는 당신의 본능을 신뢰하라는 표시이다. 인내하면 성공할 것이다.', '이 카드는 당신의 재정 상태가곧 개선될 것임을 나타낸다. 지금은 사업을 확장하거나 주식 시장에 투자하기에 좋은 시기이며, 금전적으로 이익을 얻은 후에는이색적인 지방을 여행하는 것도 좋을 것이다.', '부상이 심했거나, 건강 상태가 좋지 않았다면 이제 회복될 것이다. 완쾌된 후, 여러분은 너무 힘든 일을 하기 전에 새롭게 치유된 몸과 다시 친해지는 시간을 가져야 한다. 건강함을 다시 한 번 감사하는 마음을 누리게 될 것이다.', 'https://blog.kakaocdn.net/dn/5nw66/btq0zv0SAnO/94aK57Bbs2gvmYbT5pXslk/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('사랑에서 이 카드는 안전, 조화, 그리고 가족의 상징이다. 당신과 당신의 파트너는 함께 행복한 삶을 살 것이고 많은 아이들을 가질 것이다.가까운 미래에, 당신과 당신의 파트너는 결혼식에 참석하거나 세례를 받을 것이며, 이런 것들로 인해 서로 더 가까워질 수 있다.싱글인 사람들은 가능한 한 많은 파티와 행사에 참석하셔야 한다는 표시이다. 당신은 곧 새로운 연인을 만나게 될 것인데, 그 사람은 흥미롭고, 자상하며, 외향적일 것이다. 또한 두 사람은 오래 지속될 수 있는 강한 잠재력이 있을 것이다.', '당신의 커리어는 잘 진행되고 있다. 여러분은 자신의 능력에 자신감을 느끼고 일에서 의미를 찾았을 수 있으며, 곧 승진이나 업계 수상이라는 중요한 이정표를 세우게 될 것이다. 여러분의 친구들과 가족들이 여러분의 성공을 축하하고 여러분이 성취한 것에 자부심을 느끼도록 하라.', '당신의 치밀한 계획과 목표 설정 때문에 당신의 재정 상태는 양호하다. 여러분은 이 돈을 가족에게 쓰거나 여러분의 공동체를 풍요롭게 하는 데 사용해야 한다. 다른 사람들에게 돌려주는 것은 여러분이 더 많은 돈을 벌도록 격려하면서, 감사함을 느끼게 하는 두 가지 효과를 가져올 것이다.', '정방향 완즈 4번 카드는 여러분의 건강을 위한 좋은 징조이다. 만약 여러분이 병을 앓고 있었다면, 곧 완전히 회복될 것이라고 믿어도 좋다. 또한 이 카드는 임신을 예언하는 것일 수도 있다.', 'https://blog.kakaocdn.net/dn/zSP4B/btq0zZOcQp9/icjXB3akuCrMqYkRhuyU80/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('완즈 5번 카드는 당신이 불꽃 튀는 관계에 있다는 신호이다. 당신과 당신의 파트너는자주 싸우고성질이 거칠다. 이것이 여러분의 관계에 대해 여러분이 좋아하는 것인지 아니면 항상 서로 앙숙인 것에 지쳤는지 알아내는 것은 여러분에게 달려 있다. 만약 여러분이 끊임없는 말다툼에 불만족스럽다면, 여러분의 감정과 필요에 대해 침착하게 생각해보고 대화해 보는 시간을 갖는 것도 좋을 것이다. 미혼이라면 이 카드는 연애가 다소 혼란스럽다는 것을 나타낸다. 적극적으로 사귀고 있지만, 파트너에게 원하는 것이 무엇인지에 대해서는 확신하지 못하고 있다. 다른 사람에게 구애 받지 말고, 자유롭게 옵션을 살펴보라.', '당신은 당신의 동료들과충돌해 왔을 수 있다. 성격과 업무 스타일이 맞지 않으면 충돌이 발생하여 생산성이 저하된다. 또한 당신은 당신이 원하는 것을 위해 더 열심히경쟁하고, 그것은 직장에서 당신이 더 돋보이게 될 것임을 나타내기도 한다. 모든 주변 상황에 너무 몰두하지 말고, 여러분의 책임에 집중하라. 그러면 결국 성공할 것이다.', '당신은 당신의 파트너나 가족과 돈 문제로 다투고 있다. 불행하게도, 이러한 논쟁은 당신이 당신의 재정 상태를 파악할 수 있을 때까지 계속될 것이다. 과소비를 피하고, 전적으로 신뢰하지 않는 사람들에게 돈을 빌려주지 마라.', '오래된 부상을 극복하거나, 만성질환과의 싸움에서 승리할 것이다. 완즈 5번 카드는 여러분이 스트레스와 불안감에 압도당하고 있다는 신호일 수도 있다. 명상과 운동은 여러분을 진정시키고 전반적인 건강을 증진시키는데 도움을 줄 것이다.', 'https://blog.kakaocdn.net/dn/cvLbYm/btq0AqdE59M/q5Up6AW42SH2vM5b6lSFDK/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('이 카드는 힘과 안정에 좋은 징조이다. 여러분과 여러분의 파트너는 오랜 시간 동안 함께 해왔으며, 서로에게 도움을 기대할 수 있다. 여러분은 또한 여러분의 관계를 과시하는 경향이 있고, 여러분이 꿈의 커플로서 살고 있다는 것을 모든 사람들이 알기를 바라고 있을 수 있다.싱글인 경우, ''이 카드는 새로운 파트너가 곧 여러분의 삶에 들어오게 될 것임을 나타낸다. 이 사람은 매우 자신감 있고 사교적일 것이며,당신의 구애는 다른 사람들로부터 많은 관심을 끌 것이다. 여러분이 이 새로운 관심을 좋아하는지, 아니면 조용한 삶으로 돌아가는지를 결정하는 것은 여러분에게 달려 있다.', '완즈 6번 카드는승진이 진행 중임을 강하게 시사하는 것으로, 주요 지도부 직책일 가능성이 크다. 또한 최근에 완료한 프로젝트에 대한 인지도와 환호를 받을 수 있다.', '당신의 재정은 충분히 탄탄해서 당신은 당신의 돈을 영원히 사용할 수 있을 것이고, 자선단체에 기부할 수 있을 것이다. 이렇게 하면 다른 사람들의 존경을 받을 수 있고, 여러분은 공동체의 훌륭한 구성원으로 여겨질 것이다.', '건강 측면에서 가장 긍정적인 징후 중 하나인 완즈 6번 카드는 질병이나 부상에서완전히 회복된 것을 의미한다. 이는 특히 고치기 어려웠던 만성질환에 해당된다.    ', 'https://blog.kakaocdn.net/dn/bHSXvF/btq0A33pMgc/QZsylg3IRusLxdfFYLy6ek/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('여러분은 새로운 사람이나 외부 환경에 의해 여러분의 애정 관계가 위협받고 있다고 느껴왔다. 이러한 인식된 위협에 대해 걱정하고 집착하는 대신, 파트너와의 유대감을 강화하는 데 초점을 맞추어야 한다. 여러분의 두려움에 대해 이야기하고 일이 잘못되었을 때 서로를 비난하지 마라.일을 해결하려는 마음만 있다면 어떤 어려움도 견뎌낼 수 있다.', '현재 직책을 두고경쟁이 치열하고, 밀려나지 않도록 스스로 보호해야 할 필요성을 느끼고 있다. 완즈 7번 카드는 회사에 자신의 가치를 증명하기 위해 열심히 일해야 할 것이지만, 당신이 직업을 유지하는 데는 성공할 것이며, 곧승진할 것이라는 신호이다. 당신이 집중만 한다면, 어떤 것도 당신에게서 그것을 빼앗을 수 없을 것이다.', '당신은 당신이 재산을 보호하기 위해 현명하게 행동하고 있다. 열심히 저축하고, 건전한 투자를 하면서 그 길을 계속 나아가라. 여러분은 자녀들에게 상당한 유산을 남기고 자선단체에 후한 기부를 할 수 있을 것이다.', '당신은 얼마 전부터 어려운 병과 싸우고 있다. 건강 관리에 많은 노력이 필요하지만, 결국에는 완전히회복된다.    ', 'https://blog.kakaocdn.net/dn/n27RV/btq0A3h1tJF/1QfBkn8tmnY5xLTOTk0yE1/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('소용돌이치는 로맨스나 첫눈에 반하는 운명적 사랑이 당신에게로 향하고 있다. 누군가가 당신의 삶에 들어 올 것이며, 그 열정은 매우 격렬하여 인생을 변화시킬 것이다. 그러나 그런 로맨스의 지속 가능 여부는 여러분에게 달려 있다. 만약 여러분이 연애 중이라면, 정방향 완즈 8번 카드는 여러분과 여러분의 파트너가 함께 발전하거나, 곧 중요한 소식을 듣게 될 것이라는 것을 의미한다. 이것은 약혼이나 새로운 아기를 의미할 수 있다.', '여러분의 직장 생활은 매우 빠르게 움직이기 시작할 것이다. 새로운 모험이 당신이 예상했던 것보다 빨리 시작될 것이다. 당신에게는 당신이 준비가 되었다고 느끼는 것보다 더 큰 책임이 주어질 것이다. 지금은 행동을 취해서 여러분의 능력을 보여줄 수 있는 적절한 시기이다.', '최근에 당신 재정에 많은 우여곡절이 있었을 것이다. 당신은 저축을 하려고 노력했지만, 돈이 들어오는 만큼 빨리 나가 버렸을 수도 있다.새로운 투자를 피하고 재정적 목표에 대해 생각해 보라. 물질적 소유는 당신에게 진정으로 얼마나 중요한가? 여러분의 개인적인 성장은 자숙을 통해 이루어질 것이다.', '당신이나 당신 인생의 누군가가 질병이나 부상으로부터 놀랄 만큼빨리 회복될 것이다. 또한 이 카드는 새로운 식이요법이나 운동요법 같은 생활습관 변화가 눈에 띄는 결과를 매우 빨리 만들어낼 것이라는 것을 나타낼 수 있다. 그러니 희망을 버리거나 기다리지 말고 신속하게 행동으로 옮겨야 한다.     ', 'https://blog.kakaocdn.net/dn/nXGsh/btq0y1eP05C/KeNcdNSB7KT9DyhNBon1nk/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('당신의 연인 관계는 지금 난국을 겪고 있다. 이 카드는 싸울 가치가 있는지에 대해 의문을 품어왔다는 징조이다. 파트너의 장점에 대해 다시금 생각해 보고, 상대방과 대화를 통해함께 문제를 해결해 나가야 한다.만약 여러분이 싱글이라면, 이 카드는 여러분이 여전히 과거의 고통스러운 이별로 힘들어하고 있다는 것을 나타낸다. 새로운 연인을 만나기 전에 자기성찰을 위한 시간을 가져야 한다.', '당신은 당신의 일에 나쁜 일이 생길까 봐 걱정하고 있다. 조심하는 것은 좋지만,지나친 근심은 오히려 안좋을 수 있다.여러분이 어떤 중대한 좌절도 극복할 수 있는 강한 사람이라는 것을 기억하라. 끈기있게만 하면 모든 것이 잘 될 것이다.', '당신은 과거에 재정적인 선택을 잘하지 못했으며 당신이 믿었던 누군가에게 재정적으로 배신당했을 수 있다. 그러나 희망을 버리지 않는 것이 중요하다. 다시금 현명하게 재정 관리를 하면서 노력한다면, 재산은 다시 늘어날 것이다.확실하지 않은 정보만을 믿고 성급하게 투자를 결정하는 것은 좋지 않으니 유의해야 한다.', '불행하게도, 당신은 질병과 부상으로 인해 많은 고통을 겪었을 수도 있다. 그럼에도 불구하고, 여러분은 결코 포기하지 않고 건강 문제를 잘 이겨내기 위해 노력해 왔을 것이다. 지병이 치료됨으로써 당신의 끈기와 인내심에 대한 보상을 받게 될 것이다.     ', 'https://blog.kakaocdn.net/dn/ems6yM/btq0vP0kt0Q/IxpH7lWQVYKBQV9QyKxfu0/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('당신은 일과 가족의 의무로너무 바빠서당신의 소울메이트와 연락이 뜸할 수 있고, 당신의 연인은 당신의 그런 모습을 원망할 수 있다.너무 늦기 전에 연인과의 관계를 회복할 수 있는 시간을 찾는 것이 좋다.싱글이라면, 일이 너무 바빠서 누군가를 만날 수 없다는 변명을 계속하고 있을 수 있다. 일을 잠시 쉬고 밖으로 나가보라. 사랑은 여러분이 생각하는 것보다 더 가까이 있을 수 있다.', '진정한 열정으로 시작한 일이었지만, 그동안 너무 일에 치어 현재 너무 지쳤을 지도 모른다. 여러분은 할 수 있는 것보다 더 많은 일을 떠맏고 있을 수 있는데,이제 여러분은 최선을 다해도 그것들을 완성할 수 없다고 느낄 수 있다.업무량을 줄이도록노력하라. 실제로 중요한 일에만 집중하는 것이 더 나은 성과를 거둘 수 있다.', '여러분이 재정적인 책임을 다하지 못하고 있더라도, 그로 인한우려로 여러분 스스로를 지치게 하지는 말아야 한다. 여러분의 삶을 소중히 여기고 여러분이 진정으로 무엇을 소중하게 여기는지 생각해보라. 인생에서 가장 중요한 것은 무형적이라는 것을 기억해야 한다. 물질적 소유와 금전적 이득을 추구하는 것이 여러분의 주된 관심사가 되어서는 안 된다.', '당신은 최근에 엄청나게높은 스트레스를 받고 있을 수 있다. 그것은 건강에 해로운 영향을 끼칠 수 있으니, 여러분의 수면 주기와 식습관에 주의하면서 건강관리에 힘써야 한다. 명상과 가벼운 운동은 여러분에게 많은 도움이 될 것이다.    ', 'https://blog.kakaocdn.net/dn/bjcmHV/btq0AGHnmKn/qktgzT7IEjOWpjbKGY1Snk/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('만약 당신이 연인과의 관계에서 약간 불안감을 느꼈다면, 아마도 변화가 필요한 것일 수도 있다. 지금은 파트너와 함께 편안한 곳에서 벗어나기에 좋은 시간이다. 주말 휴가나 짧은 소풍을 함께 가보는 등,일상에서 벗어나는 것은 열정을 되돌려 줄 것이며, 여러분의 애정이 더 깊어지게 만들 수 있다. 만약 여러분이 낭만적인 연인을 찾고 있다면, 이 카드는 위험을 무릅쓰고 자신을 밖으로 내놓으라고 말한다. 늘 하던 일상대로 움직인다면 새 파트너를 만날 수 없다. 평소에 다니지 않던 곳을 방문하거나 평소 기피하던 사람들과 대화를 시작해 보라. 여러분이 새롭게 발견한 것을 보면 깜짝 놀라게 될 것이다.', '새로운 일을 시작하거나 자신의 사업을 시작할까 생각하셨다면 지금이 시작하기에 좋은 시기이다. 여러분은 오랫동안 이 새로운 사업에 대해 신나게 생각해왔지만, 큰 도약을 망설여왔다. 본능에 따라 행동하라. 빠를수록 좋다.', '좋은 소식이 기다리고 있다. 곧 돈이나 새로운 투자 기회가 생길 것이다. 너무 한꺼번에 투자하지 말고 천천히 옵션을 검토하면 결과가 훨씬 더 행복해질 것이다.', '이제는 여러분의 건강에 대해 더 적극적으로 나서야 할 때이다. 비록 여러분이 지금까지 꽤 운이 좋았지만, 여러분의 운동법과 식이요법을 꾸준히 개선시키는 것은 더 건강한 미래를 위해 필요하다.또한 이 카드는 여러분이 건강 검진을 미루지 말라는 것을 의미하기도 한다.', 'https://blog.kakaocdn.net/dn/bGwCgH/btq0AIll0DV/UZRJ31fLW2q4jNT9FMtCz0/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('여러분의 연인과의 관계는 이제까지 보지 못했던 파트너 모습을 발견하게 되면서 더욱 흥미롭고 열정적이 될 것이다. 두 사람은 함께 인생의 새로운 장을 시작하시거나, 낯선 곳으로 가게 될 수도 있다. 비록 여러분이 현재 지역에서 새로운 활동을 시도한다고 해도, 여러분은 서로를 새로운 시각으로 보게 될 것이다.사랑을 찾고 있는 사람들에게 이 카드는 훌륭한 상징이다. 그것은 여러분이 자신감과 매력을 발산하게 될 것이라는 의미이기도 하며, 혹은 그러한 자질을 가진 누군가를 만나게 될 것임을 나타낸다. 또한 이 카드는 누군가가 여러분의 매력에 빠져 마음에 두고 있으면서도, 적절한 순간을 찾지 못했다는 신호이기도 하다.', '당신은 에너지가 넘치고 일에 몰두할 준비가 되어 있다고 느낀다. 새로운 일을 맡거나, 승진을 신청하거나, 혹은 당신 자신을 위해 사업을 시작하기도 하지만, 지금 당장 당신이 할 수 있는 것은 아무것도 없다. 그러나 이러한 추진력과 야망은 같은 마음을 가진 사람들을 끌어 모을 것이며, 지금이 네트워킹 노력을 강화할 절호의 기회이다.', '지금은 돈을 이리저리 옮기기에 좋은 때이다. 퇴직금이나 저축예금 계좌에 자금이 충분하다면, 자금을 재할당하여 투자를 해보는 것도 좋다. 정방향 완즈 12번 카드는 곧 돈을 받게 될 것이라는 신호이기도 하다. 그러니 이미 재정적인 문제를 해결해 놓은 상태라면 더욱 좋을 것이다.', '건강을 증진시키기 위해서는 조치를 취해야 한다. 만약 여러분이 우울함을 느꼈다면, 유일한 탈출구는 여러분이 새로운 일을 시작하는 것이다. 만약 여러분이 중대한 건강 결정을 미루고 있다면, 여러분은 그것에 직면해서 결정을 내려야 하며, 그 결정은빠를수록 더 좋다.    ', 'https://blog.kakaocdn.net/dn/yBqFV/btq0DsvkTXM/8GDLHSkTkVkTB3E0wt3jWK/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('여왕의 강한 여성적인 에너지는 여러분의 연인 관계가 강렬한 열정과 로맨스의 시기를 갖게 될 것임을 의미한다. 당신과 당신의 파트너는 이전 어느 때보다도 서로에게 더 많은 사랑을 느낄 것이다.만약 당신이 미혼이라면,누군가가 당신에게 관심이 있다는 미묘한 암시를 보내고 있을지도 모른다. 당신 주변 사람들의 행동을 유심히 보라.그들은 너무 수줍어서 적극적으로 행동을 할 수 없을지도 모른다. 여러분의 대담함과 사교성이 필요할 수 있다.', '당신은 당신의 경력에 있어서 중요한 시기를 준비하고 있다. 힘들겠지만 걱정하지 마라. 힘든 일을 완성한 후에 더 큰 만족감을 즐길 수 있을 것이다. 어떤 사람들은 여러분이 너무 많은 일을 한꺼번에 처리한다고 말할지도 모르지만, 그들은 여러분이 일을 완수하는 능력을 과소평가하고 있는 것뿐이다.', '자신이 가치 있는 것을 얻고 있는지 확인하고, 그렇지 않다면 용기를 내어 더 많은 것을 요구하라. 당신은 열심히 일해왔기 때문에 당신의 노력이 보상을 받지 못해서는 안 된다. 당신의 재정은 곧 이것을 반영하고 그에 따라 성장할 것이다.그 동안은 당신이 가장 아끼는 사람에게라도 무모한 지출은 피하라.', '여러분의 강한 에너지는 여러분이 목표를 달성할 수 있도록 해줄 것이다. 또한 정방향 완즈 13번 카드는임신과 다산을 상징한다. 만약 여러분이나 여러분의 인생에서 누군가가 아기를 위해 노력하고 있다면, 이 카드는 좋은 징조이다.     ', 'https://blog.kakaocdn.net/dn/bf52F8/btq0A3bJMqC/Z8K7Q3T6zAWMCZsEmb82x0/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('여러분의 연인 관계는 곧정서적 성숙에 의해 정의되는 새로운 단계로 접어들 것이다. 여러분과 여러분의 파트너는 서로의 독립성과 개인적 목표를 인식하고 존중한다.연인과의 새로운 전환은 감정적으로 벅찰 수 있으므로, 서로 의사소통을 많이 하는 것이관계를 강하게 유지하는 데 도움이 될 것이다.싱글이라면 이 카드는 싱글 생활의 자유와 흥분을 즐기고 있다는 표시이다. 정착하는 것을 서두를 필요는 없다. 당신은 아직 해야 할 일이 남아 있을 수 있으며,당신이 준비가 되면 적임자가 나타날 것이다..', '직장에서 선배에게 조언이나 멘토링을 구하는 것이 좋다. 당신은 타고난 재능이 많지만, 그들의 경험을 통해 배우는 것은 당신을 더 발전시킬 것이다.리더십 역할이나 책임 증대를 위해 경쟁하는 경우, 가장 좋은 전략은 위의 사람들과 네트워크를 형성하고 그들로부터 배우는 것이다.', '정방향 완즈 14번 카드는 금전적인 면에서 특히 좋은 징조이다. 이 카드는 재정적인 횡재나 곧 결실을 맺을 투자를 나타내기도 한다. 지출만 잘 관리 한다면 재정 상황이 크게 개선될 것이다.', '당신은 상당히 건강하지만운동, 식이요법, 그리고 건강 요법에 관해무리하는 경향이 있을 수 있다. 여러분의 몸 상태에 맞게 꼭 필요한 것들만 하는 것이 더 나을 수 있다.또한 당신의 생활습관을 개선하는 데 도움이 되는 전문적인 조언을 구하는 것이 좋을 수 있다. 이것은 개인 트레이너, 영양사, 또는 여러분이 정보를 공유할 수 있는 친구의 형태로 나타날 수 있다. 그들의 충고는 당신에게 많은 도움이 될 것이다.   ', 'https://blog.kakaocdn.net/dn/bUbYE9/btq0ECYMdNF/yK2fHA5SszfxtePz3Jmq31/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('이것은 애정에 관해서최고의 카드중 하나이다. 이미 연애를 하고 있다면 유대감이 더욱 깊어지고 의미 있는 관계가 될 수 있다는 신호이다. 최근 당신과 당신의 파트너가 갈등을 빚었다면, 정방향 에이스 컵 카드는 당신의 인연이 연민과 사랑 그리고 로맨스를 통해 강화될 것임을 나타낸다.여러분이 발산하는 긍정적인 에너지는 사람들의 관심을 끈다. 만약 여러분이 미혼이거나 새로운 파트너를 찾고 있다면, 이 카드는 그것이 열애든 우정이든깊은 관계를 예고한다.', '여러분이 새로운 직업을 찾고 있다면, 정방향 에이스 컵 카드는 여러분에게새로운 기회가 주어질 것이라는 신호이다. 만약 여러분이 이미 직업을 가지고 있다면, 그것은승진이나 다른 형태의 인지도들이 다가오고 있다는 것을 암시한다. 여러분은 이 사건으로 활력을 느끼게 될 것이다. 직장에서의 동기부여가 정말로 치솟을 것이다.', '지금은 당신의 재정적 목표에 집중하기 시작하는 것이 옳다. 그것이 주택 계약금을 지불하기 위한 것이든 당신의 꿈의 휴가든 간에, 은행에 돈을 가지고 있는 것은 장기적인 안전과 재정적인 자유를 보장할 것이다.현재 돈이 빠듯하다면 에이스 컵 카드는 당신이 상황을 반전시킬 능력이 있다는 것을 나타낸다. 좋은 일이 오고 있다.', '현재 병에 걸렸다면 긍정적인 태도를 유지하는 것이 좋다. 여러분의 건강은 여러분이 걱정하는 것보다 훨씬 덜 심각하다. 가벼운 증상들과 통증이 멈추면 어느 때보다 기운이 날 것이다.    ', 'https://blog.kakaocdn.net/dn/cvrwb7/btq0AHfGtBg/SLVhobxtEKlYf0dPTuV89k/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('컵 2번 카드는 새로운 관계가 위대한 것으로 꽃피고 있다는 것을 의미한다. 여러분이 공유하는 신체적, 감정적 연결고리는 여러분을 즐겁고 행복하게 만든다. 이러한 파트너십을 통해 두 사람은 한 번도 경험해 본 적이 없는 수준으로 향상되어, 더 강한 개인과 더 나은 파트너가 될 수 있다.두 잔은 감정을 균등하게 교환하는 것으로, 두 분 모두 서로를 지지하기 위해 무슨 일이든 기꺼이 할 용의가 있다는 뜻이다. 현재 연애 중이라면, 이 카드는청혼이나 결혼의 표시일 수 있다.', '컵 2번 카드는 두 사람이 미래의 프로젝트나 사업 계획에 대해 서로 관심을 가지고 함께 하는 것을 의미한다. 여러분이 같은 기술을 공유하지 않을 수도 있지만, 여러분은 당면한 일을 함께 처리해 나갈 것이다. 이와 같은협력 관계는 상호 존중과 신뢰에 의해 구축될 수밖에 없기 때문에성공할 가능성이 높다. 아이디어의 일치를 유지하기 위해서는 매 순간 협력자와 소통하는 것이 중요하다.', '재정에 관한 한, 정방향 컵 2번 카드는균형을 나타내는 표시이다. 지금 당장은 돈에 쪼들릴 수도 있지만, 곧 청구서를 지불하고 편안하게 살 수 있을 것이다.연인이 있는 사람들에게 이것은 여러분의 자원을 결합해야 한다는 것을 암시할 수 있다. 생활비를 분담하든, 아니면 별도의 소득을 하나로 합치든, 상호 신뢰는 두 사람의 재정 상황을 더욱 좋아지게 하는 데 도움이 될 수 있다.', '건강에서도 이 카드는 전체적인 균형을 나타낸다. 장기간의 부상이나 질병으로 어려움을 겪고 있다면, 이 카드는 가까운 미래에 완쾌될 수도 있다는 신호이다.때때로 생활 스트레스 요인이 병을 일으키거나 기존의 병을 악화시킨다. 당신과 마음이 맞는 사람들을 만나 스트레스를 푸는 것이 치유로 이어질 수 있다.    ', 'https://blog.kakaocdn.net/dn/ba2QyX/btq0A3ivatZ/6nD7Zcy6hrw5syIBEqG9ck/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('당신을 둘러싼사랑의 성공이 느껴진다. 싱글이라면, 같은 인생관을 공유하는 파트너를 찾아야 할 때임을 나타낸다. 이것은 공동의 이익과 상호 존중에 기반을 둔 진정으로 열정적인 관계의 시작을 의미할 수도 있다. 여러분의 소통하는 성격 때문에, 개방적이고 신뢰할 수 있는 관계를 유지하는 것은 어렵지 않을 것이다.또한 지금은 비즈니스 파트너들과 보내는 성취의 시간이 될 수 있다. 여러분의 관계 내에서 더 많은 의사소통을 할수록, 여러분은 더 많은 성공을 거두게 될 것이다.', '아직 직업이나 비즈니스 협력자를 찾지 못했다면, 빠른 시일 내에 찾을 수 있을 것이다. 그 사람들은 직업 목표와 그것들을 성취하는 방법에 관해서 당신과 비슷한 비전을 가지고 있을 것이다. 이미 직업이나 비즈니스 협력자가 있다면, 여러분은 지금까지 달성한 목표와 그 성공을 축하해야 할 때일지도 모른다.', '당신이 성취한 모든 것을즐길 시간이다. 여러분은 여러분이 가진 것을 위해 열심히 일했으니 이제 여러분의 성공을 축하하기 위해 약간의 돈을 쓰는 것도 좋다. 지금 당신의 수입이 예기치 않게 증가할 수도 있다. 여러분이 그것을 즐겨야 할 때 즐길 줄 알아야 하며, 또한 미래를 위해 일부를 저축해야 한다.', '축하하는 이 즐거운 시간 동안, 여러분은 지나친 욕심을 느낄지도 모른다. 예를 들면, 어떤 사람들은 과도하게 술을 마실 수도 있다. 한두 잔의 술로 몸을 푸는 것은 나쁠 것이 없지만, 장기적으로, 여러분은 긴장을 풀고 축하하는 더 건강한 방법을 찾아야 한다. 힘든 일이 닥칠 때가 올 것이다. 푹 쉬고 도전할 준비를 해야 할 것이다.    ', 'https://blog.kakaocdn.net/dn/5RG9s/btq0AHUiLIO/rWOzUk8olAgBI9dnhYmg61/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('사랑면에서 컵 4번 카드는 여러분의 관계가시들해지고 있다는 경고로 나타난다. 이것이 받아들이기 힘든 현실일 수도 있지만, 이 관계를 지속하는 것은 두 사람 모두에게 긍정적인 것은 없을 것이다.여러분에게 도움이 되지 않는 오래된 행동 패턴은 깨는 것이 옳다. 만약 여러분이 현재 연애 관계에 불만족스럽거나 지루함을 느낀다면, 그런 틀에서 벗어나기 위해 노력하라.만약 여러분이 관심을 두고 있는 사람과 새로운 연애를 시작하고 싶다면, 이 카드는 그들은 여러분에게 맞지 않을 수도 있다는 신호로 받아들여질 수 있다. 뭔가 숨기고있거나, 아니면 자기들이 가장 매력적이라고 생각하는 부분만 드러내고 있는 것일 수도 있다.애인이자 로맨틱한 파트너로서 당신은 쉽게 지루해하고 당신에게서 관심이 멀어지면 잘하지 못하는 경향이 있다. 만약 여러분이 미혼이라면, 여러분은 내면을 들여다보고 여러분이 진지한 관계에서 어떤 종류의 사람과 유대감을 가질지에 대해 진지하게 생각하기 시작하는 것이 좋을 것입니다.', '컵 4번 카드는 여러분이 현재직업에 싫증이 났다는 것을 나타낸다. 여러분의 불행은 결국 여러분의 직업 생활에서 진보하는 것을 막을 수 있는 나쁜 성과로 이어질 것이다.이직이나 휴직을 할 여유가 없는 처지라면 적어도 열심히 일할 동기를 찾으려는 노력이 중요하다.', '자신을 다른 사람들과 비교하는 것은 여러분의 모든 상황을 바꿀 수 있는 중요한 재정적 기회를 놓치게 할 것이다.성공하지 못하는 것처럼 느끼지 않도록 더 현실적인 개인적 목표를 세우라. 지금까지 성취한 일의 긍정적인 부분에 초점을 맞추고 미래를 위해 희망을 가져라.', '당신은 당신이 스스로를 재평가할 필요가 있다. 만약 여러분이 아팠다면, 미래에 이 병이 재발하지 않도록 하기 위해 올바른 생활을 하고 있는지 자문해 보라.만약 여러분이 더 많이 운동하거나, 더 잘 먹거나, 더 많이 휴식을 취하는 것과 같은 건강과 관련된 결심들을 미뤄왔다면, 이제 여러분은 이런 것들을 실행에 옮길 때이다.     ', 'https://blog.kakaocdn.net/dn/d5ctOu/btq0EDi5Iak/W43MOSlRHtj25rrvZNzCk0/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('이 카드는 이별과 비통한 일을 나타낼 수도 있지만, 반드시 최악의 경우를 위한 것은 아니다. 당신의 성향은 피해자인 척하는 것이지만, 당신과 파트너 사이에 존재하는 화해할 수 없는 차이점들을 인식하는 것이 더 현명할 것이다.싱글이신 분들은 조금 더 활동하실 시간임을 나타낸다. 여러분은 실패한 과거 관계를 생각하며, 새로운 연인을 만날 기회를 놓치고 있을 수 있다.', '정방향 컵 5번 카드는 일자리, 사업체 또는 사업 파트너의 손실을 나타낸다. 업무적으로 팀의 핵심 구성원 중 한 명이 곧 당신과의 협력을 포기할 수 있다. 아직 성취해야 할 것이 많기 때문에 분노와 실망이 당신을 압도하지 않도록 하라.', '가까운 미래에재정적 손실이 예상된다. 당신은 친구에게 돈을 빌려주고 받지 못하고 있을 수도 있고, 투자가 당신이 바라던 대로 되지 않았을 수도 있다. 이 시간을 보내는 가장 좋은 방법은 남은 재정 상태를 유지하는 것이다.너무 부정적인 생각을 하지 말고 대신, 여러분이 항상 해왔던 것처럼 너그러운 사람이 된다면, 그것은 결국 결실을 맺을 것이다.', '정방향 컵 5번 카드는 주로 정신적인 건강 문제를 나타낸다. 자기애 부족, 끊임없는 부정적 사고, 어깨에 짊어진 후회의 무거운 무게를 의미한다.이 카드는 정신 건강을 다루고 있지만, 정신 건강과 신체 건강 사이에는 강한 상관관계가 있다는 것을 나타내기 때문에, 여러분의 신체적 건강을 위해서라도, 정신 건강을 잘 관리해야 할 필요가 있다.    ', 'https://blog.kakaocdn.net/dn/bS63uw/btq0zuBqFiE/ofPkzUMcL1juaEDzY7wjxk/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('컵 6번 카드(Six of Cups)는진정한 사랑을 의미하며, 소울메이트 카드 중 하나로 여겨진다.만약 당신이 연애 중이라면, 그 사람을 평생 알고 지낸 것 같은 느낌이 드는데, 그 이유는 아주 완벽한 조화를 이루고 있기 때문이다. 만약 당신이 미혼이라면, 이 카드는 당신의과거 누군가가 다시 나타나서 사랑의 관심사가 될 수도 있다는 것을 나타낸다.당신의 우정은 강하고 오래 지속된다. 여러분은 가장 가까운 친구들이나 가족들과 함께 있을 때 큰 위안을 주고받는다. 이 카드는 또한오랜 친구가 몇 년 만에 당신을 방문할 것이고, 마치 떨어져 있지 않았던 것처럼 다시 연결될 것이라는 것을 나타낼 수도 있다.', '이 카드는 여러분이 과거에 가졌던 위치로 되돌아갈 수도 있다는 신호이다. 사업이든, 직책이든, 프로젝트든, 그것은 다시 한 번 당신의 직업 생활의 초점이 될 것이다.이 카드는 또한 사업에서 어려움을 겪고 있는 사람들에게 경고가 될 수 있다. 그것은과거의 실수를 되풀이하지 말 것을 일깨워 주는 것이다. 앞으로 나아가기 위해서는 새로운 것을 시도해야 한다.', '당신은 재정적으로 안전하지만, 당신 소유의 돈을 놓치고 있다. 당신은 이미 오래 전에 친구에게 돈을 빌려주고 나서 완전히 잊어버렸을지도 모른다. 그 돈을 받기 위해 과도하게 애쓰는 것도 좋지 않지만, 당신의 재정을 좀 더 잘 관리해야 한다.', '당신의 과거 어떤 일로 인한 것들이 현재 건강에 영향을 미치고 있을 수 있다. 이것은 다시 불붙기 시작한 젊음의 상처일 수도 있고, 여러분을 괴롭히고 있는 과거의 일이나, 후회되는 과거의 사건일 수도 있다. 여러분의 내면적인 아이의 에너지를 받아들이고 긍정적인 라이프스타일 선택에 집중하라.      ', 'https://blog.kakaocdn.net/dn/WKGK1/btq0A2xanwf/MZzS3yVLssxjlT1Zsrw9YK/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('연애 중인 경우, 컵 7번 카드는중요한 선택을 해야 한다는 것을 나타낸다. 모든 면에서 볼 때, 여러분의 관계는 이상적이지만, 실제로는 서로의 피상적인 욕구를 충족시키는 것 외에는 거의 하지 못한다.당신은 지금 환상 속에서 살고 있고, 연인 관계의 균열을 보는 것을 거부한다. 당신과 연인 사이의 관계를 잘 살펴보고, 그것이 모두 가치가 있는지 살펴봐야 할 때이다.이 카드는 미래에 크게 의존하고 있다. 즉, 현재 여러분의 결정이 앞으로 다가올 미래에 중대한 결과를 가져올 것이라는 뜻이다. 파트너를 갖는 것은 멋진 일일 수도 있지만, 결코 그들을 맹목적으로 떠받드는 실수를 하지 마라. 여러분은 그들이 누구인지에 눈이 멀게 될 수도 있다.', '이 카드는 업무적인 면에서 매우 좋은 카드가 될 수 있다. 정방향 컵 7번 카드는많은 기회가 주어진다는 것을 의미한다. 여러분의 능력보다 과도하게 일을 떠맡거나, 자신에 대한 비현실적인 기대를 세우지 않도록 조심하면 된다. 여러분의 미래에 대한 꿈은 결실을 맺을 수 있을 것이다. 그러나 여러분의 목표를 향한 진정한 발걸음을 내딛는 것이 더 중요하다.', '여러분이 재정 상태가 좋든, 나쁘든 간에, 부를 축적할 수 있는 기회는 여러분에게 다가올 것이다. 중요한 재정적인 결정을 하기 전에 외부 변호사와 함께 실사를 수행하고 상황을 분석하는 것도 좋다.', '여러분은 하루에 여러분이 관리할 수 있는 것에 대한 비현실적인 이상 때문에, 과도하게 체력을 소모하고 있다. 이것은 여러분을 지치게 하고, 스트레스를 받고, 만성적인 질병으로 이끌 것이다. 작업량을 줄이거나 일정을 재조정하는 것이 좋다. 건강한 식습관을 유지하고, 시간을 내서 가벼운 운동을 하는 것이 좋다.    ', 'https://blog.kakaocdn.net/dn/2PwY2/btq0LOLRpFm/1xjKZgSgOJdC8ryXBYmBGk/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('당신의 연애에는 부정적인 부분이 있다. 불가능한 것은 없지만, 이 관계를 회복할 가능성은 낮으며, 관계를 회복시켜야 할 가치가 없을 것 같다. 여러분이 겪은 싸움과 두 사람 사이의 분명한 차이는 극복하기가 점점 어려워지고 있다.싱글인 사람들한테 정방향 컵 8번 카드가 나온다면, 뭔가새로운 것이 펼쳐지려는 신호일 수 있다. 여러분은 최근에 연애 관계를 끝냈거나, 혹은 너무 오랫동안 독신으로 지내면서 심각한 공허의 시기를 맞이했을지도 모른다. 하지만 이제 그것은 끝이 났다. 만약 당신이 새로운 연인을 만날 마음의 준비를 했다면, 새롭고 흥미로운 누군가가 곧 당신의 길을 찾아올 것이다.', '이 카드는 경력에 관한 한 긍정적인 카드가 아니다. 만약 여러분이 현재 실직 상태이거나 불확실한 위치에 있다면, 그것은 잘못된 직업윤리 혹은 자격 부족 때문이 아니다. 그 책임은 다른 곳에 있다. 경제 악화든, 관리 부실이든, 아니면 통제할 수 없는 다른 요인이든, 앞으로 닥칠 과제에 대비해야 한다.이제는 새로운 것을 시작할 때이거나, 안정을 되찾을 때까지 수입을 보충할 수 있는 방법을 찾기 시작할 때일지도 모른다.', '재정적으로, 당신은 지금 힘든 시기를 겪고 있다. 돈을 잘못 관리했거나 지나치게 야심 찬 투자 전략이 당신에게 심각한 재정적 부담을 주고 있다.투자와 저축에 더 많은 주의를 기울여야 한다. 신중한 예산 편성은 당신을 빨리 좋은 재정 상태로 되돌려 놓을 것이다.', '여러분의 삶의 모든 부정성은 정신적으로나 육체적으로 여러분의 건강을 해치고 있다. 여러분은 최근에 일어난 모든 나쁜 일이 통제할 수 없는 일이라고 느낄지도 모르지만, 그러한 생각들은 역효과를 낳는다.여러분의 책임이든 아니든, 지금 여러분이 성공하기 위한 유일한 방법은 앞으로 나아가는 것이다. 건강검진, 규칙적인 운동, 균형잡힌 식이요법을 통해 나쁜 생활습관을 개선하고 건강 관리에 꾸준히 힘써야 한다.     ', 'https://blog.kakaocdn.net/dn/bjkFCx/btq0BEqq0HF/bgrPKcoh06PUisk2XKBnMk/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('이 카드는 여러분의 현재 연인 관계가 모든 면에서 여러분을만족시키고 있다는 것을 의미한다. 신뢰, 이해, 로맨스 모두 지금 당신에게 유리하게 작용하고 있다.미혼이거나 막 사귀기 시작하는 사람이라면, 올바른 길을 가고 있으니 안심해도 된다. 여러분은 과거의 실수로부터 많은 것을 배웠고, 마침내 새로운 사랑을 시작했다.', '여러분은 직장에서큰 성공을 거두었고, 파트너와 동료들로부터 칭찬을 받고 있다. 자신을 과소평가하지 말고, 지금처럼 미래를 위해 꾸준히 노력해야 한다.', '당신의 모든 노력과 약간의 행운 덕분에, 당신은 지금까지 있었던 것 중 최고의 재정 상태에 있다. 당신은 여러 가지 면에서 보상을 받고 인정을 받고 있으니, 승진이나 보너스가 주어져도 놀라지 마라.', '당신이 과거에 겪었던 건강 문제가마침내 회복된다. 여러분은 그 어느 때보다 운동을 하고 재미있는 활동을 즐길 수 있을 것이다.새로 찾은 이 에너지를 활용하여 현재의 긍정적인 상태를 유지하는 데 도움이 되는 건강한 습관을 만드는 것이 좋다.     ', 'https://blog.kakaocdn.net/dn/bzeUlG/btq0LQb11yZ/JkPpspNxdUYDf64WtLd1a0/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('정방향 컵 10번 카드는 특히 현재 장기적으로 연애를 하고 있는 사람들에게 좋은 징조이며, 그 로맨스는 무한히 계속될 것임을 나타낸다.여러분이 아직 완전히 깨닫지 못했을지도 모르지만, 여러분은 곧 여러분의 현재 파트너가 여러분의 인생의진정한 사랑, 여러분의 소울메이트라는 것을 인식하게 될 것이다. 만약 여러분이 전반적으로 여러분의 관계에 대해 어떤 염려가 있다면, 걱정할 필요 없다. 여러분의 파트너는 여러분이 그들을 사랑하는 것만큼, 여러분을 아주 많이 사랑하기 때문에, 여러분은 쉽게 헤어지지 않을 것이다.싱글인 경우에도 정방향 컵 10번 카드는 매우 긍정적인 카드이다. 이는 당신이 하게될 다음 연애 관계가 매우 진지하며, 장기적으로는 결혼으로 이어질 가능성이 있다는 것을 나타낸다.', '정방향 컵 10번 카드(Ten of Cups)는 직업적인 면에서도 환상적인 카드이며, 당신은 회사의 매우 중요한 자산일 수 있다. 이것은 여러분의 노력과 충성, 그리고 헌신 때문이다.여러분이 결코 게으르지는 않지만, 여러분의 영예에 안주하지 말고 계속해서 열심히 일하는 것이 중요하다. 만약 그렇지 않는다면, 여러분이 고용주에게 호감을 잃게 될 가능성이 크다. 그들은 그들 나름대로 지난 시간 동안 여러분의 노고를 평가해 왔을 것이다.', '이 카드는 당신이 경제적으로 힘들었다면, 그 기간이 끝나가고 있다는 것을 말해주는 것이다. 더 이상 빈털터리가 되지 않을 뿐만 아니라,번창하는 시기가 도래할 것이다.그러나 이것은 도박이나 복권 같은 일확천금을 말하는 것이 아니니 위험한 투자에 욕심을 내지 않는 것이 좋다.', '만약 여러분이 건강상의 문제를 겪고 있다면, 여러분의 상태는 나아지고 있고, 앞으로도계속해서 좋아질 것이다. 만약 여러분이 현재 건강하다면, 이것은 여러분이 가까운 미래에도 계속 건강할 것임을 나타낸다.  지금은 체육관에 들어가거나 몇몇 가까운 친구나 가족들과 함께 새로운 다이어트를 시작하기에 좋은 시기이다. 같은 목표를 갖고 있는 사람들과 함께 한다면, 여러분의 목표를 달성하기까지 더 쉽고 즐거운 시간을 갖게 될 것이다.     ', 'https://blog.kakaocdn.net/dn/GpHMF/btq0LO6lyYQ/u7eitgFmRs8iqVWZYkTXY0/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('이 카드는 결혼프로포즈, 임신, 출산 또는 약혼발표와 같은 중요한 일이 생길 수 있다는 것을 암시하기 때문에 사랑면에 서 매우 좋은 징조이다.싱글인 경우, 이 카드는 몰래 당신을 좋아하던 사람이 이제 당신에게 자신을 알리려고 한다는 것을 나타낸다.만약 여러분이 연애를 하고 있다면, 여러분과 파트너의 연인 관계가 위험에 처해 있거나, 혹은 지치지고 늙어가는 것 같을 수 있으니,좀 더 낭만적이고 자발적인 데이트를 시도할 필요가 있다는 것을 나타낸다.', '직업적으로 이 카드는 여러분의 행동을 요구한다.황소의 뿔을 잡고능동적으로 행동하라. 당신은 현재의 직업으로 당신이 원하는 것을 하고 있는지, 아니면 그저 떠다니는 것인지, 직업적인 부분에서 스스로 잘 생각해보고 행동으로 옮겨야 한다.한 발 물러서서 여러분의 진로를 잘 살펴본 후, 무엇이 도움이 될 수 있을지 결정하라.', '경제적으로, 이 카드는 보통재정적좋은 소식을 의미하기 때문에 매우 긍정적인 카드이다. 그러나 현재 고려하고 있는 모든 투자에 매우 신중해야 하며, 어렵게 번 현금을 기업에 투자하기 전에 여러 옵션들을 살펴봐야 한다.지금이 새로운 투자를 하기에 좋지 않은 시기라고 말하는 것은 아니다. 사실 지금이 환상적인 시기일 수도 있다. 그러한 기회에서는 어떤 것이든 조심해서 접근해야 함을 기억하라.', '그 어느 때보다도, 여러분은 여러분의 몸 상태에 귀를 기울일 필요가 있다. 여러분은 갑자기 건강이 악화되는 것을 발견할 수 있기 때문에, 가까운 미래에 여러분은 자신을 최대한 돌봐야 할 것이다.만약 여러분이 조금이라도 아프거나 통증을 느낀다면, 의사를 찾아 진찰을 받는 것을 미루지 마라. 또한 지금은 격렬하거나 위험한 신체 활동을 할 최적의 시기가 아닐 수도 있다.     ', 'https://blog.kakaocdn.net/dn/lXAMk/btq0EEXJLrY/foe83wNLWHIowced2Kyhp1/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('만약 여러분이 싱글이고 한동안 어떤 미래의 짝을 눈여겨 보았다면, 이제는 용기를 내어 그들에게다가가야 할 때이다. 당신이 할 수 있는 최악의 선택은, 나중에 후회할까 두려워 현상을 유지하는 것이다. 비록 일이 잘 풀리지 않더라도, 그 경험은 노력한 보람이 있을 것이다.만약 여러분이 연애 중이라면, 여러분을 괴롭히는 근본적인 문제들을 다룰 필요가 있다. 지금은 타협을 해야 할 때이다. 여러분은 여러분의 연인 관계를 강화시키고, 긍정적인 변화를 만들 수 있는 능력과 마음의 평화를 가지고 있다.', '여러분은 직장에서 여러분에게 긍정적이고 중요한 영향을 주게 될 대대적인승진을 앞두고 있을 수도 있다. 만약 당신이 콘텐츠 제작자 또는 전문 아티스트라면 창의성이 높아지는 시기로 접어드는 것을 나타낸다. 지금이 당신의 창의성과 예술성을 마음껏 발휘해 걸작을 만들어 낼 최적의 시간이다.', '정방향 컵 12번 카드(Knight of Cups)는 특히앞으로 다가올 부를 상징한다. 복권에 당첨되지는 않을 수도 있지만, 현재 직장에서 상당한 급여 인상을 기대할 수 있으며, 이는 여러분이 기대하는 승진과 일치할 것이다.지금 당장 돈과 관련된 당신의 모든 직감을 믿으라.', '건강면에서도 이 카드는 여전히좋은 징조이다. 건강 검진 결과를 애타게 기다리고 있었다면 걱정할 필요가 없다. 당신의 건강은 괜찮으며 가까운 미래에도 계속 유지될 것이다.    ', 'https://blog.kakaocdn.net/dn/bCYJlS/btq0MUL5n2G/bkLaU3gnEMAGR18uKLlYr1/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('이 카드보다 더 환영할 만한 카드를 러브 리딩에서 찾기는 어렵다. 만약 여러분이 연애 중이라면, 여러분의 파트너가 매일매일 더깊은 사랑에 빠지고 있다는 분명한 신호로 해석될 수 있다. 당신 역시 깊은 사랑에 빠졌고, 당신의 연인 관계는 앞으로도 잘 유지될 것이다.당신과 당신의 파트너 둘 다 서로 완벽하게 편안함을 느끼며, 절대로 거짓말을 하거나 다른 사람을 조종하려 하지 않는다. 여러분의 관계는 신뢰, 타협, 상호 존중에 기초하고 있다.싱글인 경우 여러분은 지금 자석이고, 여러분이 아직 완전히 깨닫지 못하더라도, 사람들은 여러분에게 조금 더 다가가기 위해 노력하고 있을 수 있다. 당신과 친해지기를 바라는 많은 사람들이 있기 때문에, 당신은 곧 좋은 연인을 만날 수 있을 것이다.', '정방향 컵 13번 카드(Queen of Cups)는사무실에서 현재 여러분의 위치가 여러분의 잠재적인 꿈의 직업으로조용히 진화하고 있다는 것을 의미한다.여러분은 지금 여러분의 삶의 주요한 변화를 겪고 있다. 만약 여러분이 이런 변화에 조화롭게 잘 어우러질 수 있다면, 여러분은 모든 면에서 더 행복하고, 훨씬 더 부유한 사람이 될 수 있을 것이다.', '이 카드는 당신의 재정적으로 풍족해 질 수 있는좋은 소식이 뒤따를 것임을 나타낸다. 만약 여러분이 돈 문제를 겪고 있다면, 이것을 여러분이 반등할 준비가 되어 있고, 곧 재정적으로 편안해질 것이라는 신호이다.대출로 혜택을 받을 수 있다고 생각되면, 지금이 대출을 신청하기에 좋은 시기이다. 당신의 대출이 받아들여질 뿐만 아니라, 당신이 이 자금을 사용하는 방식이 향후 몇 년 동안 당신의 복지에 가장 중요할 것이다. 모든 연구와 투자를 현명하게 고려해 보는 것이 좋다.', '건강면에서 이 카드는 사랑하는 사람이 병이 났으며, 곧 보살핌, 편안함, 그리고 재정적인 지원을 위해 당신에게 의지할 것임을 의미할 수도 있다.혹은 반대로 여러분이 지원을 요청하게 될 수도 있는데, 이 경우 여러분은 보살핌을 잘 받게 될 것이다. 만약 여러분이 지금 아프다면, 인내심을 가지고 자신을 잘 돌보고 적절하게 치유될 수 있는 충분한 시간을 갖는 것이 좋다.     ', 'https://blog.kakaocdn.net/dn/bGwhr9/btq0NwYwl1V/bRkclNrKkxGsOSM50WV10K/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('정방향컵 14번 카드(King of Cups)는 사랑면에서도 가장 좋은 의미를 나타내는 카드 중에 하나이다. 특히진정한 연인을 찾는 사람들에게는 더욱 좋다. 만약 여러분이 이미 연애 중이라면, 여러분은 여러분의 파트너의 영원한 사랑 표현이 진실하며, 그들의 동기 또한 진실하다는 것을 알고 편안히 쉴 수 있을 것이다.하지만, 만약 여러분이 사이가 나빠지기 시작하는 것이라면, 왕의 출현은 관계가 개선될 것 같지 않고, 곧 끝날 것이라는 것을 나타내기도 한다. 그러나 진정한 연인이 여러분의 바로 코앞에 기다리고 있으니, 현재 연인과 헤어지는 것을 두려워할 필요가 없다. 당신의 로맨틱한 삶은 이제 시작될 것이다.', '당신은 직장에서의 당신의 공헌이 제대로 인정 받지 못하고 있는 것처럼 느낄지도 모른다. 그리고 그것이 사실일 수도 있지만, 그것은 더 이상 문제가 되지 않는다. 여러분은곧 큰 축하를 받을 것이다. 이것은 주요 일자리 기회, 또는 권위 있는 상 후보에 오르고 수상하는 것으로 나타날 수 있다.정방향컵 14번 카드(King of Cups)는높은 수준에서 큰 것을 의미하며, 이는 여러분이 직장에서 선도적인 위치에 있는 자신을 발견할 수 있다는 것을 의미하기도 한다.', '재정과 관련하여 당신은 가계부를 적는다던지, 자신의 예산 편성을 다시 해야 할 것이다.이것은 여러분의 자금 상황이 악화될 것이라는 것을 의미하지는 않는다. 단지 여러분이 감당할 수 없는 몇 가지사치적인 습관을 가지고 있고, 여러분은 재정적으로 더 책임감을 갖기 시작할 필요가 있다는 것을 나타낸다.', '최근 건강에 대한 고민이 많다면 걱정을 떨쳐버릴 수 있다. 정방향 컵 14번 카드(King of Cups)는가장 긍정적인 결과만을 나타낸다.의료인들의 역량과 능력을 신뢰하고, 긴장을 풀고, 인생을 즐겨라. 어떤 질병도 당신에게 몰래 다가와 당신을 망치려 하지 않는다.    ', 'https://blog.kakaocdn.net/dn/INZoo/btq0Dq6CeXV/2tdEv7ePDlR5MttkOTuhvk/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('이제 여러분의 생각을말하고 행동을 취해야할 때이다. 당신의 연인 관계에 대해 뭔가 신경이 쓰이는데, 당신은 너무 두려워서 말을 못 하고 있었을 수 있다. 이제 고통받는 것을 침묵하는 것은 때로는 가장 쉬운 탈출구로 보일 수 있으나, 행동을 취하지 않는다면, 그 고통은 변하지 않을 것이다. 이제 말을 하는 것이 좋다.  때때로 어떤 것이 잘못되었다는 것을 인정하는 것이 그것을 지나쳐 가는 가장 좋은 방법일 수 있다. 그것이 결별을 초래하거나 아니면 연인 관계를 강화시킬 수 있을 테지만, 어떤 결과든 여러분에게 좋을 것이다.싱글이라면 이 카드는 자신에게 솔직하기만 하면곧 사랑을 찾을 수 있다는 신호이다.', '정방향검 1번 카드(Ace of Swords)는새로운 기회가 찾아올 것임을 나타낸다. 현재 직장이 만족스럽다면 큰 프로젝트가 시작되거나승진이 곧 될 것으로 예상할 수 있다.만약 당신이 현재 직업을 찾고 있다면, 이 카드는 당신이 올바른 직업을 찾기 위해 당신의 전술을 바꾸기를 권장한다. 만약 여러분이 이전 직업이 실망스러웠다고 느낀다면, 완전히 다른 직업을 추구하라.', '이제는 자신의 재정 상태에 대해 스스로에게 솔직하고, 소비보다는 저축을 시작해야 할 때이다. 받아들이기 어려울 수도 있지만, 만일의 사태에 대비하기 위해 예산을 재조정해야 한다.', '건강에 관한 한, 여러분은 지나친 행동들을 줄일 필요가 있다. 그것이 알코올 섭취를 줄이는 것을 의미하든, 식단을 조절하는 것을 의미하든, 이러한 작은 변화들은 여러분의 심신에 활력을 가져다줄 것이다.      ', 'https://blog.kakaocdn.net/dn/bPGgDW/btq0MU1bxre/z89QD32cNkqCVInrYgLRR1/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('여러분이 싱글이라면, 여러분은 상처 받거나, 거절당할까 봐 새로운 관계의 추구를 피하고 있을지도 모른다. 만약 당신이 여러 이성들과 데이트를 하고 있다면, 지금은 여러분이 시간과 관심을 쏟고 싶은 사람을 선택할 때이다. 여러분이 연애 중이라면, 여러분은 돌파구를 경험하게 될 수 있다. 여러분은 자신의 진심을 드러내는 것을 두려워하고, 종종 마음을 닫거나 의도하지 않게 갈등을 일으킬까 봐 두려워한다.  당신은 현재 연인 관계를 지속하고 싶은지 아닌지를선택해야한다. 만약 관계를 지속하고 싶다면, 이제 마음을 열고 사랑을 받아들여야 할 때이다.', '여러분은 현재 중요한 직업 선택에 직면해 있을 수 있다. 두 가지 직업의 기회 사이에서고민할 수도 있고, 아니면 업무 갈등으로 고민할 수도 있다.가장 현명한 결정을 내리는 데 필요한 모든 정보를 수집해야 한다. 이것은 쉽지 않겠지만, 만약 여러분이 자신의 직감을 잘 따른다면 여러분은 좋은 선택을 하게 될 것이다.', '당신은 지금 재정적으로 어려움을 겪고 있는 것 같다. 눈가리개를 제거하고 자신에게 문제가 있다는 것을 인정할 필요가 있다. 즉, 예산, 수입, 저축에 관한 한결정을 내려야본 궤도에 오를 수 있다.', '여러분의 정신적, 육체적 건강은 여러분의 내적 갈등으로 인해 고통 받고 있을 수 있다. 심적 고통을 참는 것은 내적 갈등을 야기시키고 있다. 사랑하는 사람들 곁에서 계속 경계하는 것에 지쳐있는 것 같다.이런 상황에서는 불안과 우울증이 생길 수 있다. 이러한 문제는 의료 전문가의 도움을 받아 해결할 수 있을 것이다.     ', 'https://blog.kakaocdn.net/dn/FHNuC/btq0Q8Yotep/CKyr3qdiZJmKjFdWpBL090/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('싱글인 경우, 이 카드는 여러분이과거에 갇혀 있고, 이전 관계에서 겪었던 고통에서 벗어날 수 없다는 것을 나타낸다. 심각한 불안, 질투는 갈등을 포함하여 여러분의 연애에 많은 부정적인 영향을 미칠 수 있다.만약 당신이 현재 연애 중이라면, 당신과 당신의 파트너 사이에 그 어느 때보다도 긴장감이 있을 것이다. 이것은 이별이나 이혼을 초래할 수도 있으며, 점점 커져가는분노를 암시한다.과거에 머물지 말고, 여러분이 느끼는 고통을 받아들이고, 다음 장으로 넘어갈 때이다.', '직업적 면에서 당신은큰 슬픔에 빠져 있을 수 있다. 그것이 실직이든, 단순한 불쾌감이든, 혹은 동료들과의 소통의 부족이든, 그 결과 여러분은 실망감을 느끼고 있다.이때 긍정적으로 생각하고 상황을 반전시킬 수 있는 여러 가지 방법을 평가하는 것이 중요하다. 애초에 어디가 잘못됐는지 이해하기 위해 자신과 주변 사람들에게 정직하라.', '정방향검 3번 카드(Three of Swords)는당신의 재정이 좋지 않다는 것을 나타낸다. 지금은 여러분의 삶에 부정적인 면이, 어떻게 하면 다시 정상 궤도에 오를 수 있을지에 대한계획을 세울 때이다.최근의 이혼이나 실직은 당신의 재정 불안의 원인일 가능성이 높기 때문에, 현재로서는 당신 자신을 최우선 순위로 삼는 것을 기억하는 것이 중요하다.', '당신은 지금 당신의 건강에 대해 약간상실감을 느끼실 것 같다. 여러분의 몸과 마음을 완전히 통제할 수 없는 것은 여러분을 슬픔과 환멸을 느끼게 할 수 있다.여러분은 오랫동안 설명할 수 없는 병을 앓은 후에 미루던 건강 진단을 받았을지도 모른다. 이제 여러분은 무엇이 여러분을 아프게 했는지에 대한 해답을 가지고 있지만, 그것은 여러분이 결코 보지 못했던 것이고, 따라서 여러분을 불쾌하게 만들고 있을 수 있다.이 시간을 명상, 치료, 자기 성찰을 통해 여러분의 마음과 연결해 보는 것이 좋다. 그렇게 하는 것은 생각하는 능력을 향상하고, 여러분이 이 어려운 시기를 이겨내도록 도와줄 것이다.     ', 'https://blog.kakaocdn.net/dn/crxn3u/btq0LOmxfbQ/Ru3qZUYRGJVNsz3WLMLvUk/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('여러분이 연애 중이라면, 여러분과 여러분의 파트너는휴식이 두 사람 모두에게 지금 당장 필수적이라는 상호 합의에 도달했을 수 있다. 여러분은 함께 편안하게 데이트를 하는 것이 아니라, 여러분의 관계에 불안과 부담을 주는 바쁜 삶을 살아 왔을 수 있다. 하지만, 이번 휴식 시간은 여러분이 서로를 다시 찾을 수 있게 해줄 것이다.만약 당신이 싱글이라면, 이 카드는 당신의 현재 상황을 더 잘 이해하기 위해, 약간의 자기 성찰과 고독의 필요성을 나타낸다.만약 당신이 당신의 욕망에 대해 정직하다면, 적절한 연인은 때가 되면 당신의 길을 찾아올 것이다.', '이 카드는 당신이 현재 직장 생활에서극도로 스트레스를 받고 있다는 것을 의미한다. 한 때 처리할 수 있었던 업무량이 엄청난 작업처럼 느껴지기 시작했을 수 있다. 이것은 당신이 다시 업무에 집중할 수 있도록, 일에서 멀어져 약간의 휴식 시간이 필요하다는 표시이다.새롭게 리셋하기 위해 집에서 며칠을 보내거나, 새로운 풍경을 보기 위해 일주일간의 휴가를 필요로 할지도 모른다. 여러분의 상쾌한 정신은 여러분이 다시 일을 시작할 때 이러한 스트레스 받는 일을 잘 해결할 수 있게 해 줄 것이다.', '이 카드는 당신의 재정적 상황에 대한 압박이 크다는 것을 나타내며,당신은 더 이상 당신의 재정 상태에 대해 명확하게 볼 수 없을 수도 있다. 그러나 상황이 여러분이 인식하는 것만큼 나쁘지는 않다는 것을 알아두어야 한다. 심호흡을 하고, 생각을 가다듬고, 계획을 세워라. 계획에 따른 질서를 회복하는 것은 여러분이 겪고 있는 불안감을 해소하는 데 도움이 될 것이다.', '여러분은 잠시 뒤로 물러나 휴식을 취해야 한다. 만약 여러분이 계속해서 몸의 내부 경고를 무시한다면, 여러분은 곧 지치거나 불안과 관련된 질병으로 병원에 입원할 수 있다. 여러분의 신체적, 정신적 건강을 위해 적당한 휴식과 함께 명상과 요가를 해보는 것이 좋다.     ', 'https://blog.kakaocdn.net/dn/dGYulE/btq0SGf2jUK/2jGSRW4Q5WiSRXggA0IAqK/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('이 카드는 당신의 행동으로 인해 연인 관계에갈등이 생겼다는 것을 나타낸다. 여러분은 연인 관계를 망치고 있으면서도, 스스로는 무엇을 잘못하고 있는지 모르고 있을 수도 있다. 당신이 일만을 너무 우선시 하고 있거나, 의사소통을 할 때 자신의 주장만을 고집하고 있을 수 있다. 스스로 돌아보고 조금 더 마음을 터놓고 연인을 존중하고, 편안하게 대화할 수 있도록 해야 한다.만약 여러분이 싱글이라면, 여러분은 과거의 실수로부터 배우기를 거부하기 때문에, 여러분이 시작한 어떤 연인 관계에서도 계속 실패해왔을 것이다. 여러분은 잘난 체하지 말고, 자신의 잘못을 인정해야 하며, 어느 누구도 완벽하지 않다는 것을 깨달아야 한다.', '여러분의부정적인 태도와험담하는 경향은 여러분이 알고 있는 것보다 업무 환경에 더 많은 해를 끼치고 있다. 당신은 당신의 패거리들에 의해 이용되어 왔고, 사내 정치에 끼어들어 험담하는 데 과도한 시간을 소비하고 있다. 계속 그런 식으로 행동한다면, 당신의 회사의 생산성은 더욱 악화될 것이며, 머지않아 상사 또한 당신의 그런 행동을 알아차리게 될 것이다.', '당신의 재정과 관련하여 약간의속임수가 있을지도 모른다. 당신이 당신의 돈을 처리하도록 맡기고 있는 사람이 당신을 잘못된 방향으로 인도하고 있거나, 혹은 당신은 당신의 재정 상태에 대해 스스로에게 거짓말을 하고 있을 수 있다.당신은 과거의 실수로부터 배워야 하고, 당신의 재정 안정을 스스로 방해하지 말아야 한다. 이제는 예산을 짜고, 그것을 고수할 때이다.', '여러분이 겪어온 어려움들이 여러분의 에너지를 고갈시키고 있다. 당신의태만적 행동이 정서적, 육체적 건강에 직접적으로 영향을 주었을 수 있다. 이 카드는 경고 표시이다. 너무 늦기 전에 자신 스스로에 대해 심각하게 재평가해보고, 상황을 개선시키도록 노력해야 한다.     ', 'https://blog.kakaocdn.net/dn/4WXzA/btq0PbOEVk2/MBFE1wFOsFAYtU8zA7A9z1/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('싱글인 경우, 이 카드는 연애에 뛰어들기 전에기다려야 한다는 것을 보여주는 좋은 표시이다. 새로운 목표를 달성하거나, 항상 궁금했던 새로운 것을 시도함으로써 여러분 자신을 더 잘 알기 위해 기다리는 이 시간을 사용하는 것이 좋다. 이러한 모험은 여러분을 더 나은 사람, 더 나은 미래의 파트너로 만들 것이다. 당신의 여정을 따라 사랑이 찾아올 것이다.연애 중인 사람들은갑자기 어떤 일이 생겨, 여러분 연인과의 관계를 더 좋게 변화시킬 것이다. 예상치 못한 기습은 두 분 모두를 당황하게 만들지만, 궁극적으로 두 분을 더 가깝게 만들 것이다.', '정방향검 6번 카드(Six of Swords)는 당신의 업무에변경 사항이 생길 것임을 나타내지만, 이 카드만으로는 이러한 변화가 긍정적인지부정적인지 여부를 알 수 없다. (필요하다면 스프레드에서 이 카드를 지원하는 카드를 살펴봐야 한다.) 이 카드는 승진에서 실직까지 모든 것을 나타낼 수 있다. 그러나 실직이 또 다른 더 좋은 기회를 줄 수도 있고, 반대로 승진이 위험으로 이어질 수 있는 것이기에, 긍정적 혹은 부정적으로 볼 수 없는 것이다.변화를 환영하며, 결과에 상관없이 더 나은 날이 기다리고 있다고 믿는 것이 좋다.', '정방향검 6번 카드(Six of Swords)는재정적인 균형을 이루는 카드이다. 먹고살기 위해 애쓰셨다면, 상황이 곧 나아질 것이다. 당신은 큰 부를 얻지는 못할 수 있지만, 더 많은 안정을 누릴 것이다.하지만, 부유한 사람들에게 이 카드는 다소 끔찍한 카드가 될 수 있다. 당신은 가까운 미래에 재정적으로 힘들어져, 생활마저 위협받을지도 모른다. 사치스럽거나 경솔한 지출은 그만두고, 여러분이 꼭 필요한 것만 사면서 재정을 관리하는 것이 좋다.', '여러분의 신체 건강은 현재 좋은 상태에 있거나, 곧좋아질 것이다. 당신은 건강 상태를 개선하기 위해 노력해왔기 때문에, 신체적 고통도 사라지고, 건강이 회복될 것이다.하지만, 당신은 정신적 행복은 소홀히 해왔을 수 있다. 집중력을 되찾기 위해 더 많은 시간을 들이는 것이 좋다. 조용한 숙고와 성찰은 균형을 이루는데 도움을 줄 수 있으며, 전문가의 지도를 구하는 것도 매우 좋다.     ', 'https://blog.kakaocdn.net/dn/cat8nJ/btq0Q8KS3eX/jEGexObhsW0v0G7z3jifmK/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('만약 여러분이 현재 새로운 연애를 시작하고자 한다면, 이 카드는 당분간보류하는 것을 암시한다. 여러분은 동료들 사이에서 여러분의 사회적 지위를 높이는 방법으로서, 누군가를 찾으려 하고 있을지도 모른다.  만약 여러분이 현재 연애 중이라면, 이 카드는 여러분이 그것을끝내고 싶어 할 수도 있다는 것을 암시한다. 이것은 여러분이 더 이상 파트너를 돌보지 않는다는 것을 의미하지 않는다. 사실, 여러분이 여전히 그들에게 느끼는 사랑은 여러분이 그것을 그만두는 것을 막고 있는 것일 수도 있다. 여러분은 그들을 다치게 하고 싶지 않지만, 여러분은 또한 여러분이 처한 상황에서 자신을 구할 필요를 느낍니다.', '이 카드는 누군가가 당신에게 반감을 품고 있거나 당신을배신할 계획을 하고 있음을 암시한다. 주변 사람들을 비난하는 것은 삼가되, 누군가 여러분을 지치게 하고 그만두게 만들려고 할지도 모른다는 것에 대해 경계를 늦추지 말아야 한다.', '여러분은 자신이 대담하다고 느낄지도 모르지만, 이러한 덧없는 감정들이 여러분의 재정 상황의 현실을 감추지는 못한다.검 7번 카드(Seven of Swords)는여러분이 원하던 것들을 어떤 대가도 치르지 않고 갖고 있다는 것을 암시한다. 아마도 배우자에게 말하지 않고 배우자의 신용카드를 빌렸거나, 그들의 허락 없이 그들의 돈을 사용하면서 중요한 일방적인 결정을 내렸는지도 모른다. 이것은 당신의 재정 상태를 해칠 뿐만 아니라, 당신의 관계를 불안정하게 만들 수도 있다.이 카드는 또한 당신의 재정을 관리하는 누군가가, 당신의 무지를 이용하고 있다는 것을 나타낼 수 있다. 그들은 그들이 받아야 할 것보다 더 많은 돈을 청구하거나, 여러분이 알고 있는 것보다 자기 몫을 더 많이 챙겨가고 있을지도 모른다.만약 여러분이 뭔가 잘못되었다고 생각한다면, 주저하지 말고 다른 전문가에게 확인해 보는 것이 좋다.', '육체적으로, 당신은 아주 건강하다. 하지만, 이 카드는 때때로갈등이나, 여러분의 사회적 환경에 의해 야기되는고뇌의 감정을 나타낼 수 있다. 높은 불안감은 불면증이나 우울증으로 이어질 수 있고, 이것은 여러분의 정신 건강에 영향을 줄 수 있다.여러분은 여러분이 주어진 상황에서 어떻게 행동하든 불행하게 끝날 수 있다는 것을 알게 될 수도 있고, 갇혀 있는 느낌을 받을 수 있다.그런 감정이 건강에 영향을 줄 수 있으니, 주저하지 말고 심리학자를 만나보라. 그들은 여러분의 생각을 정리할 수 있도록 도와줄 수 있고, 여러분이 지금 처해 있다고 믿는 끝없는 패배적 상황에서 벗어나는 방법을 알려줄 수도 있다.     ', 'https://blog.kakaocdn.net/dn/bvnTwr/btq0TuT1N1R/uibXvA4tpwjCdgOkJMUf8K/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('싱글일 경우 당신은자신만만한 태도를 취했음에도 불구하고, 스스로 바람직하지 않다고 느낀다. 새로운 사람들과 잠재적인 연인들을 만날 때, 이런 당신은 그들에 대한 회의와 의심으로 보여질 수 있다.그러나 당신은 계속해서벽을 쌓고, 자신을 거부 당하는 것으로부터 보호하는 방법만을 찾고 있을 수 있다.현재 연애 중이라면, 이 카드는 앞으로 나아가는 험난한 길을 상징한다. 여러분은 여러분이 마음속에 만들어 놓은 시나리오에 맞추기 위해, 파트너의 행동을 부정적으로왜곡할 가능성이 있다. 이로 인해 연인과의 관계가 나빠질 수 있으니 유의해야 한다.의사소통이 가장 좋은 치료법이다. 파트너에게 여러분이 어떻게 느끼는지에 대해 이야기하고, 여러분이 두려움 때문에 행동하고 있을 가능성에 대해 열린 마음을 가져야 한다.', '직업과 관련해서도, 여러분의 공포심이 오히려 문제가 될 수 있다는 것을 나타낸다. 직장에서, 여러분은 자신이 너무 빨리 진보하는 것처럼 느낄 수도 있는데, 여러분은 상황이 너무 좋은 것이 진짜일 리 없다거나 숨겨진 뭔가가 있을 것이라고 생각할 수 있다.여러분은 스스로 느끼는 그런 어려움에 대처하기 위해 행동할 것이고, 이것은 여러분 자신을 방해하고 있을지 모른다. 비록 그렇게 하는 것이 여러분에게 안전감을 줄 수 있을지라도, 여러분의 행동, 혹은 행동하지 않는 것은 궁극적으로 여러분을 다치게 한다.이 카드는 또한 여러분이 자신감을 찾아야 한다는 것을 나타낼 수도 있다. 특히 컨설팅, 은행 또는 법률 관련 일을 하는 경우, 업무적으로 어려움을 겪을 수 있다. 의사소통과 설득력이 필요한 모든 영역은 당신의망설임에 의해 방해받을 것이다.', '정방향검 8번 카드는당신이 경제적스트레스를 느끼고 있다는 것을 의미한다. 재정적으로 안정적임에도 불구하고, 당신은 당신의 돈을 세고 있을지도 모른다.사실, 이러한 문제들은 대부분 실제 상황과 거의 관련이 없다. 은행에 아무리 많은 돈이 있다고 해도, 두려움을 달래기에는 결코 충분하지 않을 것이다. 이런 불안한 상태에서 벗어날 수 있도록 전문가와 상담하는 것이 좋을지도 모른다.', '비록 여러분이 신체 건강에 이상이 있지는 않지만, 여러분은 숨겨진 무언가가 여러분을 안에서부터 잡아먹는 것처럼 느끼고 있을 수 있다. 당신은 모든 신체적 고통이나 증상을 면밀히 조사하면서 최악의 상황을 가정한다.그러나 당신은 의사를 만나는 것을 망설인다. 당신은 의식적으로 "무지는 행복"이라는 사고방식을 경멸하지만, 정작 병원 예약은 하지 못하고 흐지부지한 상태에 있을 수 있다. 여러분은 두려움에 직면하거나, 어려운 진실을 받아들일 필요 없이, 모든 것이 괜찮다는 것을 알고 싶어 한다.     ', 'https://blog.kakaocdn.net/dn/cWkn0s/btq0PdlrIUE/6NRg7RbcgKqMVZjmXLxKq1/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('이 카드는 현재 여러분의연인 관계에 먹구름이 드리워져 있음을 나타낸다. 상대방에 대한 신뢰는 크게 흔들렸는데, 이는 여러분이 그들의 어긋난 행동, 거짓말, 그들의 이상한 습관들을 알아차렸기 때문일 수 있다.그 결과, 여러분은 이제 그들의 모든 행동을 면밀히 조사해서 그들을 현장에서 잡으려고 할 수도 있다. 그러나 이러한 불안정은 근거가 충분치 않을 수 있으며, 파트너를 부당하게 밀어낼 수 있다는 점을 명심하라.현재 미혼인 경우, 이 카드는 당신에게 데이트를 그만둘 것을 제안한다. 과거의 연인 관계는 여러분을 회의적이고 불안하게 만들었고, 여러분은 아직 그것을 극복하지 못하고 있을 수도 있다. 만약 여러분이 어떻게 혹은 왜 모든 것이 잘못되었는지에 대해, 예전 파트너를 떠올리게 된다면, 여러분은 아직 새로운 누군가를 찾을 때가 아니다.지난과거에 집착하지 말고, 여러분의 미래 연인 관계를 상상해 보는 것이 더 나을 것이다.', '이 카드는 직장에서일이 잘 진행되지 않는다는 강력한 표시이다. 당신은 틀에 박혀 출세하는 것이 어렵다는 것을 알게 될 수 있다. 과거의 실수가 당신의 자신감을 흔들었고, 당신의 일에 영향을 주었을 수 있다.또는 동료나 상사가 당신의 과거 실수에 집중하고 있을 수 있으며, 이를 자주 반복하고 있을 수 있다. 이것이 악의적이든 단순한 농담이든 간에, 가능한 한 빨리 그것을 웃어넘기고 다른 것으로 넘어가도록 하라. 그 실수를 설명하거나 방어하는 것은, 그 문제를 단순히 과거 일로 사라지게 하는 대신, 그것을 끌어내고 관련성을 부여할 것이다.', '이 카드는돈이 궁하다는 것을 암시한다. 과거의 실수로 인해 재정적으로 불안하고, 상황을 어떻게 수습해야 할지 확신이 서지 않게 되었을 수 있다.또한 그 실수로 인해, 여러분은 돈을 지키기 위해 안간힘을 쓰고 있을지도 모른다.  그 실수는 당신 책임으로 받아들여야 하겠지만, 거기에만 매달려 있지 말고, 가능한 앞으로 나아가면서 이 어려운 시간을 인내해야 한다. 다행히 이 카드는 영구적인 상태를 나타내는 것이 아니니 안심해도 된다.', '여러분은 건강에 대해불안감을느끼고 있고, 이것은 여러분에게 엄청난 심리적 고통을 주고 있다. 여러분의 몸에 대한 지나친 걱정은 모든 크고 작은 신체적 고통이나 상처에 대해 너무 예민하게 생각하게 될 수 있으며, 심하면불면증, 수면 부족, 강박증까지 가져올 수 있다. 과도한 걱정이나 두려움으로 인해 심리적으로 쇠약해질 수 있으니, 여러분을 안심시켜줄 의료 전문가를 찾는 것이 좋다.     ', 'https://blog.kakaocdn.net/dn/erEExy/btq0SGf3Cxe/lTq6DjKds7Xh4bkjAGVsvK/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('정방향검 10번 카드(Ten of Swords)는사랑에 빠진 사람들에게는 좋은 징조가 아니다. 만약 당신이 현재 미혼이라면, 당신에게 접근하는 사람들은 믿지 않는 것이 좋을 수 있다. 당신은 당신을 배신할 누군가에게속았을지도모른다.현재 연애 중이라면, 그 연인 관계가곧 끝날 것임을 나타낼 수 있다. 아마 여러분은 행복하고 모든 것이 잘 되고 있다고 믿겠지만, 여러분의 파트너는 그렇게 만족감을 느끼지 못할 수도 있다. 당신 대신 다른 곳에서 의미를 찾을 수 있고, 일단 그들이 그것을 발견하면 즉시 당신을 떠날 것이다.싱글이든 커플이든, 여러분은 인생에서 더 많은 극적 상황을 경험하게 될 수 있다. 이것은 사랑하는 사람들을 당신에게서 멀어지게 할 수도 있다. 또한 이 카드는 배신이 일어나기도 전에, 이를 막기 위해 주변 사람들을 지나치게 면밀히 조사하고 있다는 것을 나타낼 수 있다.그러나 그런 행동이 오히려 그들이 여러분과 거리를 두거나, 다른 곳에서 사랑과 애정을 찾도록 만들 수 있다.', '이 카드는 당신이 곧 직장을 잃게 되어,어렵고 위태로운 처지에놓이게 될 징조일 수 있다. 당신의 현재 경력은 갑작스럽게 끝날 수도 있고, 새로운 시작을 요구할 수도 있다.하지만, 이것은 여러분이 유리 천장에 부딪혔고, 위로 올라가는 것이 점점 더 어려워지고 있다는 것을 나타낼지도 모른다. 여러분의 현재 어려움은 직장 동료의배신에 의해 야기될지도 모른다. 여러분이 등을 돌렸다고 생각하는 동료들은 사실 그들 자신의 개인적인 이익을 위해 여러분에게 등을 돌렸을지도 모른다. 예의 바르게 행동하되, 그들에게 중요한 프로젝트에 대해 너무 많은 통제권을 주지 않도록 해야 한다.', '정방향검 10번 카드(Ten of Swords)는는 당신의 재정 상황이곧 바닥을 칠 것이라는 것을 나타내며, 곧 신용 카드 빚에 빠질 것이라는 것을 나타낼 수도 있다. 이 예상치 못한 재정적 위기로 인해 궁지에 몰릴 수 있다.향후 재정 문제를 완화하기 위해 필요한 비용을 최대한 절약하는 것이 좋다. 신용카드 빚이 당신의 미래 재정에 어떤 영향을 미칠지 유념해야 한다.', '정방향 검 10번 카드는물리적으로 고정된 느낌을 나타낸다. 최근의 부상은 여러분이 원하는 것을 하는 것을 방해할 수도 있고, 이것은 다시 여러분의 정신 건강에 영향을 줄 수도 있으며,여러분이 부족하거나 혼자 있다고 느끼게 할 수도 있다.여러분은 또한 주변 사람들에게 공격받고 상처 받기 쉽다고 느낄지도 모른다. 다른 사람들의 의도에 대한 회의감을 느끼고, 그로인해 당신의 건강을 악화시킬 수 있는 심각한 스트레스를 받거나 우울증을 초래할 수 있다. 여러분은 그들의 행동이 공격적이거나 잔인하지 않을 때에도 그렇게 느낄 수 있다.이러한 편집증적인 경향을 완화하기 위해 긍정적인 생각을 가지도록 노력해야 한다. 만약 여러분이 사회적 관계에서 어려움을 겪고 있다면, 여러분의 감정을 정리하는 것을 도와줄 수 있는 건강 전문가를 만나보는 것도 좋을 것이다.    ', 'https://blog.kakaocdn.net/dn/dswv28/btq0MVZ2I74/KJK26M3BJZ1y8HCJDZ0Le0/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('최근 들어 연인 관계에서마찰이 심할 수있다. 여러분과 여러분의 파트너는 의지할 수 있는 탄탄한 기반을 가지고 있지만, 여러분의 작은 결점은 자신의 건강에도 좋지 않으며, 연인 관계를 서서히 망칠 수 있다.따라서 작은 논쟁이라도, 원인을 찾아 파트너와 함께 해결하는 것이 좋다. 사랑에 관한 한 정직은 항상 최선의 정책이고, 현재 문제를 토론하는 것은 여러분이 미래의 문제를 피하는 데 도움이 될 것이다.싱글인 경우 파트너를 찾기 위해 좀더 외향적이어야한다. 새로운 그룹에 가입하고 사람들과 친분을 맺는 것은 여러분이 새로운 연인을 만나는 데 도움이 될 것이다. 그러나 그 과정에는 인내가 중요할 것이다. 아무도 하룻밤 사이에 깊은 감정적 유대감을 형성하지 않는다.', '당신의 마음속에는 신중하게계획된 진로가 있다. 당신의 목표를 향해 한 걸음 한 걸음을 내딛는 것만으로 충분해 보일 수도 있지만, 그것은 시작에 불과하다. 계획을 실행하는 것은 여러분의인내력과 힘을 필요로 할 것이다.진도가 느릴 수 있지만, 여행 자체가 목적지에 도달하는 것보다 더 보람 있는 경우가 많다는 것을 기억하라.', '당신은 당신의 재정 상황에 대해과신하고 있을 수있다. 현재 재정 상황을 안전하게 생각하고, 저축과 예산에 대해 자신감을 갖는 것은 좋지만, 때로는 당신의 실질적인 재정 상태를 확실히 하고, 잘 관리하기 위해 전문가의 의견을 구하는 것이 더 좋을 수 있다.', '이 카드는 신체 건강보다는 여러분의특별한 정신력을 나타낸다. 이제 당신의 옛 정신적 상처를 치료하고, 당신의 악마들을 잠재울 때가 된 것 같다.     ', 'https://blog.kakaocdn.net/dn/bisUQZ/btq0VotkYpz/v7NCNKfzQYQ407W1UjKzck/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('정방향검 12번 카드(Knight of Swords)는움직임을 강조한다. 사람들이 여러분의 삶에 드나들 것이다. 이것은 싱글인 사람들에게는 좋은 징조가 되고, 연애하는 사람들에게는 다소 부정적인 징조가 된다.당신이 싱글이라면,연인을 곧 만나게 될 것이다. 새로운 로맨틱한 사랑을 찾기 위해서, 때로는 여러분의 편안한 영역 밖으로 나가야 하지만, (예를 들면, 새로운 취미를 취함으로써) 그 사람은 여러분이 평상시의 습관을 바꿀 필요 없이 여러분을 찾아올 것이다. 그 사람이 당신에게 데이트 신청을 할 때, 당신은 놀랄 수도 있다.만약 당신이 연애 중이라면, 당신의 파트너는불만족스러울 것이다. 여러분이 일에 집중하고, 여러분의 목표를 달성하기 위해 노력하는 동안 그들은 무시당했다고 느낄 수 있다. 좋은 연인 관계를 지속하고 싶다면, 그들에게 그들이 중요하다는 것을 보여주기 위해 시간을 내도록 하라.', '이 카드는 당신의 커리어에 매우 좋은 징조이다. 계속해서 대담하게 도전해 보라. 주변 사람들이 여러분의 노력을 알아채고 보상해 줄 것이다.여러분의 동료들은 여러분이 더 위험한 행동을 추구하지 않도록 설득하려고 할 것이다. 하지만 여러분의 목표를 달성하는 것은 오직 위험을 통해서만 가능하다.', '재정적으로 기회가 찾아오고 있다. 당신은 재정적 성공을 이룰 수 있을 투자 기회를 갖게 될 수 있다. 당신의 대담한 태도는 당신을 소심한 사람들이라면 그냥 지나칠 수 있는 승리로 이끌 것이다.', '여러분의 식단을 개선하고, 운동 루틴을 향상하기 위해 시간을 내야 한다. 지금 여러분의 신체 건강을 유지하는 것은, 미래에 올 수 있는 합병증을 피할 수 있게 해 줄 것이다.     ', 'https://blog.kakaocdn.net/dn/vDCKk/btq0UdyRiNy/rWy02LQKg8MTfUy0Y39nHK/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('만약 여러분이 연애 중이라면, 당신의 연인 관계는 의심할 여지없이매우 성숙한 관계일 것이며, 연인과 명확한 의사소통이 이루어지고 있고, 둘은 매우 잘 어울릴 것이다. 여러분의 연인과의 협력은 서로에게 도움이 되는 긍정적인 관계이며, 둘은 서로에 대한무언의 이해로 이어져 있는데, 이는 지적이고 객관적인 결정을 내릴 수 있게 한다. 이 카드는 맑은 정신의 결실을 의미하는데, 이것은 두 사람 모두에게좋은 결과를 가져올 것이며, 유대관계가 강화될 것임을 나타낸다. 만약 여러분이 싱글이라면, 여러분은 매우 독립적이기 때문에, 여러분의 현재 상태에 대해 자신감을 가질 수 있다. 자신이나 잠재적인 새 연인에게 분명한 이익이 없는 경우, 서둘러 관계를 맺을 필요는 없다고 생각한다.하지만, 만약 여러분이 연애를 하고 싶다면, 여러분은 여러분이 무엇을 찾고 있는지도 정확히 알고 있다. 이것은 자기 성찰, 즉 자신의 진정한 모습을 아는 능력이다. 현명하고 철저한 검의 여왕은 그들이 누구와 어울릴지 정확히 알고 있으며, 잠재적인 새로운 파트너를 만날 때 다른 사람과 자신 모두에게정직하다.', '전체 그림을 볼 줄 알며, 본능적으로 성공의 길을 이해하는 능력은 당신이 앞장설 수 있는 원동력이 될 것이다. 당신이 고위직에 있든 아니든, 당신의지혜는 주위 사람들의 성공을 돕는다.가장 간단하고 논리적인 성공 경로를 찾기 위해 노력하는 가운데, 타인과 협력할 수 있는 능력은 직장에서 높이 평가될 것이다. 이 카드는당신이 당신의 업무에 매우 헌신적이고, 그것의 성공에 전적으로 투자한다는 것을 나타낸다.당신의 헌신, 틀림없는 논리, 그리고 심각한 스트레스 상황이나 시기에서 나타나는 당신의 명료함은, 직장 내에서 당신의임금 인상이나 승진으로 이어질 수 있다.', '당신의 재정적인 결정은 무엇보다도 계산된 논리에 기초한다. 이는 잠재력을 충분히 평가하기 전에는 함부로 투자를 하지 않는다는 것을 의미한다. 이러한 기회가 위험성이나 투자 수익이 높든, 낮든 간에, 여러분은 개인적인 상황에 대한 확률과 잠재적인 영향을 정확하게 이해하고 있을 것이다.', '이 카드는 매우 긍정적인 카드 이지만, 한편으로는 당신이감정을 억누르고 있다는 것을 의미할지도 모른다. 이것은 여러분의 건강한 삶에 부정적인 영향을 미칠 수 있다. 왜냐하면 여러분은 여러분의 감정을 위한 배출구가 없다고 느낄 수 있기 때문이다.또한 이 카드는 당신이 당신의 감정을 부끄러워하고 있다는 것을 의미할 수도 있다.  여러분은 충분히 능력이 있으니, 주저하지 말고 자신의 두려움을 분석하고, 건강을 위한 방법들을 탐구해 보는 것이 좋다.     ', 'https://blog.kakaocdn.net/dn/bm0uuP/btq08uVdhBL/3ODi4PC1XbJn8Q01WDIiik/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('당신은 마음보다 머리를 더 좋아하기 때문에, 당신은 사랑 관계에 대해서 막막하다는 것을 알게 될 수도 있다. 여러분이 마음속에 만들어둔 틀에 정확히 맞는 사람을 찾는 것은 어렵거나, 아예 불가능할 수도 있다.싱글인 경우, 잠재 파트너와의 만남에 대해 열린 마음을 갖도록 노력해야 한다. 방금 만난 사람에게 너무 많은 것을 요구하거나, 그들의 외모를 무시하는 말을 할 수 있으니 유의해야 한다. 또한 연인을 만나기 위해서는 너무 계획적인 틀을 잠시 내려놓고, 어느 정도는 방심하면서 즉흥적으로 행동하는 것이 필요할 수 있다.  현재 연인이 있는 경우, 파트너가 무시당했다고 느끼지 않도록 주의해야 한다. 그들은 여러분의 엄격한 스케줄을 따르거나, 여러분과 같은 결정을 하고 싶지 않을 수 있다. 여러분의 목표를 달성하기 위해 만든 구조들은 편안한 가정생활로 이어질 수도 있지만, 여러분의 파트너는 여러분의 엄격한 일상으로부터 벗어나는 작지만, 배려심 많은 행동들을 더 좋아할 수 있다. 그들이 좋아하는 것에 빠져보는 것도 좋다.', '당신은 치밀하고 계산적으로, 빡빡하게 일을 진행시키기 때문에, 당신의 상사들은 당신이 맡은 일은 잘 진행될 것임을 알고 있을 수 있다.세 걸음 앞으로 나아가는 당신의 능력으로 인해 당신은 직장에서 빠르게 승진할 수도 있다. 비록 여러분의 무의미한 태도가 동료들을 위협할 수도 있지만, 여러분이 성취한 결과는 성공에 대한 각광을 받으며 사람들이 여러분 주위에 모이게 할 것이다.외모와 생활방식에 있어서 명확하고 구조화된 여러분은 대변인 또는 직장 행사 주최자가 될 수도 있다. 이러한 기회를 통해 여러분은 카리스마와 의사소통 능력을 동시에 연마할 수 있을 것이다.', '큰 그림을 볼 수 있는 능력과 직장에서 높은 자리에 오를 수 있는 가능성이 있음에도 불구하고, 이 카드는 돈에 관해서는 조심해야 한다는 지표이다.지금은 한 발 물러서서 재정 상태를 점검하기에 좋은 때이다. 신뢰할 수 있는 조언자를 찾고 경솔한 결정을 내리지 않도록 유의해야 한다.', '이 카드는 정신적 건강과 신체적 건강의 잠재적인 불균형을 나타낸다.당신의 새로운 식이요법은 특히 신체 건강에 관한 한 긍정적인 결과를 낳을지도 모른다. 그러나 운동을 추가한다면, 점점 더 자신감이 생길 수 있고, 신체적으로 더 강해지는 것을 느낄지도 모른다.또한 이 카드는 여러분이 걱정과실패에 대한 두려움으로, 고통받고있다는 것을 암시할 수 있다. 여러분은 어떤 실책을 보완하기 위해 초과 근무를 해야 하거나, 여러분 자신을 위해 만든 일상을 따르지 못하고 있을 수 있는데, 이것은 정신적인 피로를 초래할 수 있다.여러분은 너무 높은 기준을 설정함으로써, 스스로에게 불가능한 기대를 만들어냈을지도 모른다. 여러분을 가장 행복하게 하는 것들에 빠져들기 위해 가끔은 며칠씩 쉬는 것도 좋을 것이다.   ', 'https://blog.kakaocdn.net/dn/mxEdv/btq0Yg2pwBI/ac6z3AyhBZBOljIbhbukkK/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('이 카드는 모든 면에서 훌륭한 카드이다. 싱글이라면, 새로운 사랑이 여러분을 기다리고 있다는 것을 암시한다. 여러분의 잠재적 연인은 이미 직장이나 사업을 통해 여러분이알고 있는 사람 중에있을 수 있다. 그들은 여러분의 삶에 긍정적인 영향을 끼치고 있다는 것을 곧 알게 될 것이다. 만약 여러분이 연애 중이라면, 이 카드는 여러분이 여러분의 파트너와 함께 창조적이거나 재정적인 프로젝트를 시작할 것이라고 말한다. 이러한 노력은 결국 안정과 풍요로 이어지며, 이미 행복한 결합을 더욱 공고히 할 것이다.', '급여가 대폭 인상되는 것과 함께,새로운 직업이나 진로가 당신을 기다리고 있다. 그것은 여러분이 과거에 경험했던 그 어떤 것과도 근본적으로 다르겠지만, 그 새로운 직업이나 진로는 여러분이 매우 깊이 관심을 갖게 될 일이다.', '이 카드는풍요와 가장 밀접한 관련이 있는 카드이다. 지금은 평상시보다 조금 더 위험한 곳에 돈을 투자하기에 아주 좋은 시기이다. 미래에큰 성과를 거둘 수 있기 때문이다.', '이 카드는 여러분의 건강이 전반적으로 개선되고 있고, 계속해서개선될 것이라는 것을 의미한다. 따라서 여러분의 삶에 새롭고 건강한 활동들을 시도해보기 좋은 시기이다. 더 규칙적으로 운동하고, 더 건강에 좋은 음식을 먹는 것이 많은 도움이 될 것이다.    ', 'https://blog.kakaocdn.net/dn/cUPMBe/btq0XqqWgy2/6iICrbIi2LyGzLMe3rvWK1/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('이 카드는 여러분이 여러분의 파트너를 행복하게 하기 위해, 충분한 에너지나 시간을 쏟지 않고 있다는 것을 암시한다. 여러분이 파트너를 끔찍이 사랑할지 모르지만, 지금여러분의 삶은 너무 산만하여, 연인 관계를 고통스럽게 만들고 있다. 따라서 당신은 삶에 균형을 잡고, 인생에서 여러분의 우선순위를 잘 생각해 봐야 한다.싱글인 경우, 이 카드는 새로운 연애를 시작할 시간이 있는지, 준비는 되어 있는지에 대해 여러분이 해야 할 선택을 나타낸다. 당신은 스스로에게 솔직해질 필요가 있다. 만약 여러분 자신이 스스로 좋은 연인을 만나고, 그 관계를 유지하기 위해 노력하지 않을 것을 알고 있다면, 현재 관심을 두고 있는 사람이나, 혹은 연애 자체를 포기하는 것이 더 현명할지도 모른다.', '여러분은 여러분의 재능을 더 널리 알리기 시작할지도 모르고, 어쩌면 새로운 기술을 개발하기 위해 현재의 직장을 떠나고 싶을지도 모른다.어느 쪽이든, 당신은 직업 생활에 관해 몇 가지 중요한 결정을 내려야 할 것 같다.  일반적으로 편안한 것을 선택하기 보다는, 더 위험한 것을 선택해야 할 수도 있다. 이 카드는 당신이잠재적 위험과잠재적 이익의 균형을 잘 맞춘다면, 모든 것이 잘 될 것이라고 여러분에게 말하고 있다.', '이제 여러분은 저축이나투자를 다양화해야할 때이다. 모든 계란을 한 바구니에 담아두지 않겠다는 생각으로 말이다.  이 카드는 일반적으로 긍정적인 카드이기 때문에, 만약 여러분이 다가오는 재정적인 결정에 신중하다면, 여러분은 올바른 선택을 할 수 있을 것이다.', '건강에서, 이 카드는 여러분이일과 개인 생활의 균형을 맞춰, 자신의 건강을 더욱 살펴야 한다는 것을 의미한다. 일에 너무 치우쳐 있지 말고, 시간을 내어 운동을 하거나, 건강한 식습관을 위해 노력할 필요가 있다는 것이다.  만약 여러분이 건강에 더 많은 주의를 기울이지 않는다면, 머지않아 꽤 심각한 문제들이 발생할 수 있다.    ', 'https://blog.kakaocdn.net/dn/bfcEeF/btq0SGhf0Gv/lnvQkeNQV3FCiCwrzvX4zK/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('여러분은 여러분이 학교나 직장에서 알고 있는 누군가와 연애 하기를 원하고 있거나, 적어도 한 명 이상의 동료들을 성적으로 원하고 있을지도 모른다.만약 여러분이 현재 연애 중이라면, 이 카드는 당신과 당신의 파트너 모두 현재 연애 관계를 더욱 발전할 수 있도록타협을 받아들일 준비가 되어 있다는 것을 나타낸다. 또한 이 카드는 여러분이 너무 자신의 직업과일에만 몰두하고 있다는 것을 의미할 수도 있는데, 만약 여러분이 행동을 바꾸지 않는다면, 현재의 연애 관계에 분명히 고통을 겪게 될 것이라는 경고 일 수 있다.', '이 카드는 숙련된 노동력과 팀워크를 의미하며, 특히 건축, 디자인분야의 직업과 관련되어 있으며,우수한 직업 윤리, 일에 대한 헌신, 그리고 각각의 분야에서 성공하겠다는 결의를 암시한다.  이 카드는 여러분의 모든 노력과 헌신이승진이나 연봉 인상의 결과를 가져올 가능성이 높다는 것을 의미한다. 또한 전문적인 목표를 달성하기 위해팀과 협력해야한다는 것을 의미할 수 있는데,여러분은 다른 사람들과 협력하는데 특히 뛰어나기 때문에, 아무런 문제가 없다.', '재정적인 면에서 이 카드는 좋은 징조이다. 정방향펜타클 3번 카드(Three of Pentacles)는 여러분이곧 여러분의 노력에 대한 보상을 받을 것이라는 것을 의미한다. 당신이 최근에 겪고 있는 재정적인 어려움은 곧 끝날 것이다.현재의 재정 상황에 관계없이, 여러분의 재정 상황은 앞으로 개선될 것인데, 장기 투자보다는 단기 투자가 더 좋은 시기일 수도 있다.', '만약 여러분이 꾸준히 운동을 해왔고, 자신의 건강을 위해 열심히 노력해왔다면, 이 카드는 여러분의 모든 노력이 성과를 거두기 시작했다는 것을 암시하기 때문에 좋은 징조이다.당신은 기분이 좋고, 활기차 보이게 될 것이다. 계속해서 열심히 하라.     ', 'https://blog.kakaocdn.net/dn/Gg46A/btq0YiFYWwA/XT6WULYtAhJMqfkTlm9CUK/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('사랑면에서 좋은 카드가 아니다. 이 카드는 돈이 여러분 연애 관계의 원동력이 될 수 있고, 여러분이 재정적인 안정을 위해, 혹은 더 나쁜 욕심 때문에 그것에 매달릴 수도 있다는 것을 암시한다.마찬가지로, 당신의 파트너는 당신보다 당신의 지갑에 훨씬 더 관심이 있을 수 있다. 이것은 여러분이 현재 파트너가 있든 없든 똑같을 수 있다. 당신, 혹은 당신의 주변 사람들은 상대방의 돈에 가장 큰 관심을 갖고 있을 뿐, 애정은 없다는 것을 나타낸다.', '이 카드는 잘 확립된 회사들의 큰 돈을 의미한다. 이것은 분명히 긍정적인 것일 수 있지만, 동시에 이 카드는 여러분이 전반적으로 여러분의 직업에 만족하지 않을 수 있다는 것을 나타내기도 한다.만약 여러분의 월급이 매일 일을 해야 하는 유일한 동기라면, 돈이 여러분의 행복에 얼마나 중요한지 생각해 봐야 하는데, 아마도 직업을 바꾸는 게 더 나을 수 있다.', '이 카드는 당신의 재정 상황이 안정적이고, 그렇게유지될 것이라는 것을 나타내지만, 재정이 더 좋아지지는 않을 것임을 나타낸다.분명 신중하고 책임감이 있어야 하지만, 많은 수익을 올리고 싶다면 지금보다는 덜 보수적인 태도를 취해야 할 것이다.', '당신의 신체적 건강은 괜찮지만, 당신의 정서적 건강은 좋지 않다. 이 카드는 여러분이 과거의 문제를 극복하고, 여러분의 삶을 향해 나아가야 한다는 신호이다.  당신은 오래된 원한에 매달리고 있거나, 이미 지난 일들을 끊임없이 후회하고 있을지도 모른다. 이러한 감정들은 흡연, 음주와 같은 잘못된 습관으로 이어질 수 있기 때문에, 정서적 편안함을 위해 더욱 노력해야 한다.     ', 'https://blog.kakaocdn.net/dn/cMBeZE/btq0Y3huX1z/tItZ72QtAgTr47AjoJ4Up1/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('만약 여러분이 연애 중이라면, 여러분은 버림받거나 사랑받지 못하거나 무시당했다고 느낄 것이다.이러한 감정은 최근 당신이 겪고 있는 불안감을 가중시킬 뿐입니다.  당신의 경제적 어려움과 건강 문제로 인해, 당신의 파트너가 당신을 떠날 수도 있다. 이것을 여러분이 스스로를 개선해야 할 때라는 신호로 생각해 보라. 먼저 문제의 근본 원인을 파악하여 문제를 해결해야 한다.', '최근 직장을 잃었거나 사업이 망했을 가능성이 높으며, 그 손실로 인해 당신의 자존감을 심각하게 손상됐을 수 있다.이것은 영구적인 상황이 아니니, 당신은 빨리 남은 조각들을 집어 들고 재건할수록 노력해야 한다.', '당신은 앞으로 당분간재정적인 어려움을 겪을 것이며, 돈이 빠듯할 것이다. 또한 이 카드는 노숙자, 빈곤 또는 파산을 나타낼 수 있다. 돈이 부족할 테니, 최대한 절약해야 한다.  이 카드는 정서적, 재정적 문제와 전반적인 불운의 도래를 나타낸다. 큰 좌절들은 모두 이 카드와 연관되어 있으며, 이것은 궁극적으로 여러분 자신과 삶에 대한 믿음을 잃게 할 수도 있다.', '조만간 건강이 나빠질 수 있다. 이러한 건강상의 문제는 여러분 삶의스트레스와 직결된다.  스트레스로 인해 건강을 해치지 않도록 노력해야 한다.긍정적이고 건강한 정신 상태를 유지하는 것은 여러분이 곧 직면하게 될 타격을 완화시키는데 도움이 될 것이다.    ', 'https://blog.kakaocdn.net/dn/b7hVe7/btq04LhONyt/Gc9zkEBwM7HfFukqIluyiK/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('연애 중이라면, 이 카드는 현재 연인 관계가상호 이익이 된다는 것을 나타낸다. 유대감은 계속 강화될 것이며, 계속해서 좋은 유대감이 쌓이다 보면, 나중에는 어떤 문제에도 연인 관계는 흔들리지 않을 것이다. 만약 여러분이 싱글이라면, 여러분은 여러분에게매우 친절하고 관대한 누군가를 만날 가능성이 있다. 여러분의 긍정적인 에너지가 그 사람을 끌어들이고 있다. 그 사랑에 마음을 열고 보답하라. 위대한 것의 시작일 가능성이 높다.', '이 카드는 당신이 직업적으로 좋은 위치에 도달했다는 것을 나타낸다. 여러분은 마침내 여러분의 경력에 쏟은모든 노력과 헌신에 보답받게되었다.당신의 뛰어난 기술은 당신을 빠르게 승진시켜 줄 수 있는 매우 영향력 있는 상급자들의 주목을 받게 될 것이다. 만약 여러분이 독자적으로 사업을 운영한다면, 높은 수준의 투자자가 여러분의 창의성을 알아차리고, 여러분의 사업에 투자하려고 할 것이고, 이것은 여러분의 성공을 촉진시킬 것이다.', '재정적으로, 여러분은 잘하고 있다. 여러분은 계속해서 다른 사람들에게 금전적으로 관대해야 할 필요성을 느끼고 있을 수 있으며, 혹은여러분은 여러분의 삶을 완전히 바꿀 수 있는, 다른 누군가의 관대함을 받고 있을지도 모른다.어느 시나리오에서든 부와 성취감을 가져다 준 것은 여러분의긍정적인 에너지일 것이다.', '이 카드는 당신의 건강에 관한 두 가지 중 하나를 의미할 수 있다. 건강이 급격히 좋아졌거나, 아니면 다른 사람들에게 도움을 청하기 시작할 때라는 것이다. 당신 주변 사람들은 그들이 할 수 있는 어떤 방법으로든 도울 것이다.     ', 'https://blog.kakaocdn.net/dn/dbzgR6/btq081cXMJ8/rCYPfeLxvAAIPXQnbgjuY0/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('만약 여러분이 연애 중이라면, 이 카드는 두 사람이 그동안 연인 관계를 위해 노력해온 것들이 두 사람의 관계에 긍정적인 영향으로 나타날 것임을 의미한다. 여러분은 서로 더 명확하고 정직하게 소통하는 법을 배우고 있을 수 있다. 만약 여러분이 중요한 것을 함께 계획하고 있다면, 가까운 미래에결실을 맺을 것이다.만약 여러분이 미혼이라면, 여러분은 미래의 관계에서 정확히 무엇을 기대하는지를 결정하기 위해 과거의 경험을 반성하게 될 것이다. 두 사람이 완전히 마음을 열고, 서로를 신뢰하는 데 시간이 걸릴 수 있으므로, 새로운 파트너에 대해 인내심을 갖도록 노력하라.', '당신의 노력이결실을 맺기 시작한다. 당신은 과거에 목표를 세우고 그것을 이루기 위해 꾸준히 노력해왔을 것이다. 당신이 앞으로 나아갈 길은 곧 명백해질 것이다.', '여러분이 어떤 일을 해냈거나, 곧 성취할 것이기 때문에, 분명히 여러분의 재정은좋아질 것이다. 당신은 미래에 대한 큰 계획을 세울 수 있게 해주는 수입 증가를 즐길 수 있을 것이다. 또한 곧 상속이나 투자 현금의 혜택을 받을 수도 있다.', '지금이 자신을 위한 새로운 건강 목표를 세울 이상적인 시기이다. 적절한 계획을 세우면, 그 어떤 것도 여러분이 그 목표들을 달성하는 것을 막을 수 없을 것이다. 이 카드는 현재 또는 미래의 임신에 대한 긍정적인 신호일 수도 있다.    ', 'https://blog.kakaocdn.net/dn/bIJ68R/btq082CV5uQ/825SkVIZKuD1fIIlAM6e2k/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('싱글인 경우, 새로운 기술을 배우거나 워크숍에 참여하는 대신, 잠재적인 연인을 위해 관심을 더 기울여야 한다.만약 여러분이 연애를 하고 있다면, 여러분의일과 돈에 대한 집착이 여러분의 연인 관계를 해치고, 여러분을 멀어지게 할지도 모른다. 여러분의 일이 매우 중요할 수 있으나, 여러분 주변 사람들을 위해 시간을 낼 필요가 있다. 연인 관계의 회복을 위해 공통의 취미를 찾아보는 것도 좋을 것이다.', '이 카드는 현재 자신의 재능을 발견하거나, 자신을 재정의하고 있음을 나타낸다. 겸손한 마음으로 열심히 배우면서 자신감을 갖고 미래에 성공한 자신을 그려보는 것도 좋다.여러분의 업무 밖에 있는 책임을 맡을 기회를 주시하라.', '재정적으로 장기적인 계획을 세워야 한다. 비록 이 카드는 여러분의 재정에좋은 징조이기는 하지만, 어떤 종류의 횡재도 기대하지 마라. 여러분은 여전히 노력해서 돈을 벌어야 한다.모든 재정 계획에서 가장 중요한 부분은 예산이다. 충동구매를 줄이고 목표를 세워야 한다.', '그동안 의사의 지시를 따르지 않았거나, 건강에 충분히 주의를 기울이지 않았다면, 이 카드는 빨리 조치를 취하라고 요구하고 있다.    ', 'https://blog.kakaocdn.net/dn/lek4D/btq08vrSgfW/jgw4vweJt0pErX0TmR2LwK/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('지금은 잠시 동안 혼자 남아서독립을 즐길 수 있는 시간이 될 수도 있다.만약 여러분이 연애를 하고 있다면, 이것은 여러분의 파트너에게 약간의 공간을 주고, 창의적인 일에 집중하라는 신호이다.싱글이라면 당신은 생각보다 긍정적인관심을 끌고 있을 수있다. 주변 사람들은 당신의 강인함과 관능미를 알아차리고 있다. 사랑이 바로 코앞에 있을 수도 있다.', '이 카드는 경력에 관해서도 좋은 카드이다. 당신은 당신의 일에 있어서 새로운 단계에 도달하고, 그동안의 노력에 대한 보상을 받을 것이다. 만약 당신이 사업을 하고 있다면, 이것은 곧 다가올 이익을 가리킨다.  또한 이 카드는 휴식의 기간과 은퇴와 같은 새로운 방향을 말할 수 있다.', '머지않아 당신이 해온 고된 노력에 대한 보상이 따를 것이다. 이 카드는 재정적인 독립이 진행되고 있음을 의미하는데, 투자를 계획해봐도 좋을 것이다.', '만약 여러분이 건강에 문제가 있었다면, 이 카드는 그동안 치료를 잘 받아온 효과가 나타날 것임을 의미한다. 당신의 건강은 곧 회복될 것이다.     ', 'https://blog.kakaocdn.net/dn/lUyrx/btq06gBKyIc/ccYoGPeF3kJcVgqo7Rsnhk/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('여러분의 연인 관계가 강력한 기반을 가지고 있음을 나타낸다. 이 카드는 전통적인 가족 가치의 표시일 수 있다. 반지, 약혼, 심지어결혼과 같은 이정표가 세워질 수도 있다.만약 당신이 싱글이라면, 이 카드는 곧 누군가가 당신의 삶에 들어올 것이라는 것을 의미한다. 그 사람은 당신에게 장기적인 약속을 할 준비가 되어 있을 것이다.', '이 카드는 일이나 교육에 관한 좋은 징조이다. 지금 힘든 일을 겪고 있다면 포기하지 마라. 여러분은 중대한 돌파구를 눈앞에 두고 있다. 안정과 재정적인 독립이 코앞으로 다가왔다.', '정방향펜타클 10번 카드는특권과 부를 상징한다. 당신이 떠난 후 미래 세대를 위해 제공하기 위해 신탁, 보험 증권 및 기타 금융 상품을 설정하는 것을 고려해보라.이 카드는 또한 뜻밖의 횡재가 여러분 앞에 닥칠 수도 있다는 것을 암시한다.', '일반적으로 이 카드는 여러분의 건강에 좋은 카드이다. 여러분은 부상이나 장기적인 질병에 대처하는 데 필요한 보살핌과 치료를 받게 될 것이다.만약 여러분이 건강에 문제가 있었다면, 유전적인 요인들을 살펴보라.  ', 'https://blog.kakaocdn.net/dn/blsN2O/btq08vyDqEs/d7AFQzG66KYqBxW8t35Vxk/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('싱글인 경우, 이 카드는 당신의 관심을 끌구혼자가 곧 나타날 것임을 나타낸다. 당신은 그 신비스러운 사람에 대한 모든 것을 알고 싶을 것이다. 여러분은 사랑을 즐기는 동안 현실적인 것을 잊지 말아야 한다.이미 결혼을 했거나, 연인이 있는 사람들에게는 어려운 시기가 끝나고,새로운 시기가 시작됨을 의미한다. 서로에 대해 다시 알아가고, 자신과 연인의 관계를 재검토하면서, 여러분은 서로에 대해 더 잘 알게 될 것이다. 짧은 휴가나 두 번째 신혼 여행이 여러분에게 필요한 것일 수도 있다.', '이것은 진로, 직업면에 있어서 매우 좋은 카드이다. 이 카드는승진, 새로운 사업, 혹은직업의 변화를 의미하는데, 여러분에게 새로운 길이 형성되기 시작하고 있음을 의미한다. 여러분의 영감에 주시하고, 우주에 귀를 기울이는 것이 좋다.변화가 필요한 기회가 주어진다면, 망설이지 말고 기회를 잡으세요.', '만약 여러분이 현재 재정적인 문제를 겪고 있다면, 여러분의 노력이마침내 성과를 거두기 시작할 것이다. 하지만 지금은 큰 위험을 감수하는 것보다는, 느리지만 꾸준한 것에 최선을 다하는 것이 좋다.', '건강에서 이 카드는 젊은 에너지와 복지를 상징한다. 임신이나 출산이 다가올 수도 있다. 식단에 신경을 쓰고, 건강한 생활 습관을 기르는 것은 여러분이 가장 좋은 기분을 느끼도록 도와줄 것이다. 작은 선택이 합해져 큰 차이를 만들 것이다.     ', 'https://blog.kakaocdn.net/dn/eSjFe8/btq05otj47A/6ZuRBdKHYBdcbK59e7FoR0/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('고장나지 않았으면 고치지 마라. 이 카드는 성공하기 위한 필요한 일상생활의반복적인 부분을 나타낸다. 하던 일을 계속하면 원하는 것을 얻을 수 있다.반면에 이 카드는 완고함과, 자신의 방식을 고수하는 면을 나타내기도 한다. 만약 일이 뜻대로 되지 않는다면, 마음을 바꾸고 새로운 접근법을 취해야 할 때일지도 모른다. 똑같은 실수를 반복하면서, 다른 결과를 기대하는 것은 피해야 한다.', '당신은 믿을 수 있는 사람이라, 당신 자신과, 당신 주위의 사람들을 지지할 수 있다. 때로는 사업을 시작해 당신이 당신만의 상사가 되는 것이 더 좋을 수도 있다. 여러분은추진력이 있다.하지만 당신의 완벽주의를 조심해야 하며, 좀 더 실용적이어야 한다. 만약 여러분이 근거지를 지키고 노력한다면, 그에 대한 보상을 받을 것이다.', '금전적인 면에서 이 카드는 좋은 징조이다. 지금까지 제대로 해왔던 일을 계속하는 한, 재정적인 목표에 도달할 수 있을 것이다.', '이 카드는 건강에도 좋은 징조이다. 만약 당신이 아팠다면, 곧 회복될 것이고, 다시 활력이 생겨 새로운 목적의식을 갖게 될 수 있다. 적절한 운동과 충분한 휴식을 취하는 것은 건강한 생활을 유지하는 데 핵심이다.  ', 'https://blog.kakaocdn.net/dn/TzEtP/btq1g9higG4/e0S7ZEzCnxSKphwK8MXKdK/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('현재 연애중이라면, 이 카드는 당신의 연인 관계에 당신이 원하고 노력하던안정이 찾아 오고 있음을 나타낸다. 지금은 연인과 호화로운 휴가를 즐기기에 좋은 시간이다.만약 당신이 현재 싱글이라면, 곧 좋은 인연을 만나게 될 것임을 의미한다.', '이 카드는 당신의 경력에 아주 좋은 징조이며, 당신의 업무가실용적이고, 체계적으로 준비가 잘 되어 있음을 나타낸다. 여러분에게 큰 부와 성공을 가져다 줄 사업 파트너가 생길 수도 있을 것이다.', '이 카드는 곧 있을 재정 번영을 상징하기 때문에 아주 좋은 카드이다. 여러분이 노력해 온 시간들은 재정적인 보상과 성과로 나타날 것이다.', '이 카드는 여러분이 좋은 신체 상태를 유지하고 있다는 것을 의미하는데, 이는 여러분이 다이어트와 운동에 적극적으로 관심을 기울인 결과일 것이다. 만약 현재 건강상의 문제를 겪고 있다면, 그것 또한 여러분의 잘못된 습관 때문일 것이다. 그동안 건강상의 문제가 있었다 하더라도, 이 카드는 이제 막 활력을 되찾기 직전의 상황임을 나타낸다. 따라서 너무 걱정하지 말고, 미래에 대한 희망을 갖고 긍정적으로 지내는 것이 좋다.   ', 'https://blog.kakaocdn.net/dn/bvmgYA/btq09yJ78SS/OU4Z6oNToAdwOSH1yMIKk1/img.jpg');
INSERT INTO tarot (contextLove, contextCareer, contextMoney, contextHealth, imgLink) VALUES ('펜타클의 왕은 삶을 최대한 즐기기 위해 돈을 쓰는 것을 두려워하지 않는다. 이 카드는 우리에게 삶이 가져다줄 수 있는 관능적이고 육체적인 즐거움을 일깨워준다.연애에서 이 카드가 나온 것은 당신이 갈망해왔던 연인을 마침내만나게 될 것이라는 의미일 수도 있다.이미 사귀는 사람이 있다면, 이 카드는 당신이 새로운안정기에 접어들고 있다는 것을 나타낸다. 하지만, 만약 여러분이 현재의 연인 관계에 불만이 있다면, 아마 솔직한 대화가 필요할 것이다. 서로 솔직하게 소통하면 문제들은 저절로 해결될 것이다.', '승진이 다가오고 있다. 당신은 다른 사람의 도움을 받아 직장에서 위치가 더욱 확고해질 것이다. 하지만 원치 않는 가혹한 비판이 따라올 수 있다.', '재정적으로 이 카드는 훌륭한 징조이며, 최고의 카드 중 하나이다. 이 카드는 당신의 고된 노력으로 인해, 당신의 재정 상황이안정되고 있다는 것을 의미하며, 곧 다가올횡재를 의미할 수도 있다', '현재의 안정은 여러분의 심신이 건강하게 유지될 수 있도록 도움을 줄 것이다.자기 성찰을 통해 부정적인 기운을 없애고, 건강한 습관이나 음식으로 꾸준히 건강 관리를 하는 것이 좋다.   ', 'https://blog.kakaocdn.net/dn/c8bIlC/btq1e7cXQTa/Buc6of2Whm8BTFb2qb61Yk/img.jpg');


INSERT INTO zodiac (context, imgLink, zodiacType) VALUES
('적을 알고 나를 알아야 위태롭지 않은 한 달 입니다. 당신의 능력을 과대평가한 나머지, 힘에 부친 일에 섣불리 뛰어들 수 있으니 난항을 거듭하게 될 것입니다. 당신의 능력을 정확히 파악하는 것이야말로 실패하지 않는 길 입니다. 공명심과 최근 연이은 작은 성공이 당신을 부추기게 되니 유혹에 꿋꿋하지 못하면 가시밭길을 걸어가게 됩니다. 물론 패기와 진취적인 자세는 중요한 것이지만, 계란으로 바위를 치는 것은 만용과 상대에 대한 무지일 뿐 청년의 패기라 할 수 없습니다. 몇몇 성공을 거두었다고 해서 스스로를 대견히 여기는 것을 삼가 해야 하니, 자칫 자만심에 빠진 당신이 주제 넘는 행동으로 실례를 범할 수 있기 때문입니다. 언제나 자신을 낮추고 최소한 자신의 능력과 분수에 벗어나는 일은 건드리면 안되니, 스스로를 잘 절제하지 못한다면 힘든 한 달이 될 것입니다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000579_500.jpg', 'mouse'),

('무엇보다도 학업운이 빛을 발하는 시기이므로 준비하고 있던 시험이 있다면 좋은 결과를 얻게 될 수 있을 것으로 보입니다. 집중력이 생기는 달이므로 공부하기에 좋은 시기이며 준비한 것보다 배는 좋은 결과를 가져올 수 있으니 공부 하는 시간을 아끼지 않도록 하는 것이 좋겠습니다. 약간의 긴장이 더 좋은 결과를 가져오게 되니 너무 마음을 풀어놓고 해이해져 있는 것보다는 가벼운 긴장을 놓지 않는 것이 더 나은 방법입니다. 그러나 재물운은 좋은 시기라 보기 힘든 때이므로 투자를 하거나 진행하고 있던 일이 있었다면 이번달은 잠시 거두어 두는 것이 더 좋은 방법이라 하겠습니다. 돈을 불릴 생각을 하는 것보다는 가지고 있는 돈을 아끼는 것이 좋을 것입니다. 하지만 소비를 한다면 현명한 소비가 되니 쇼핑을 하기에도 좋은 시기입니다. 다만 너무 과소비할 수 있으니 적당한 선에서 그만두는 것이 좋겠습니다. 또한 횡재수는 없는 시기이므로 쓸데없이 복권에 투자하는 것은 그만두는 것이 좋으며 충동적으로 주식 등에 투자해서는 오히려 안 좋은 결과를 가져올 수 있으니 주의하도록 하십시오. 학업에 관련한 것은 전체적으로 길조를 띠고 있으니 학자금을 고민하던 자에게도 학업에 투자할 돈이 들어오게 될 수 있다 하겠습니다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000579_500.jpg', 'mouse'),

('공상적, 이상적인 목적에 몰두해 일이 잘 풀릴 듯 보이지만 매사에 신중해야 함을 명심하자. 내 것인 줄 알고 덤벼들었다가 화를 입기도 하니 깊이 생각해 결정해야 할 것이며 특히 문서나 부동산과 관련해서는 무리하는 일은 없도록 해야 한다. 금전운의 길흉은 반반이라 내가 노력한 만큼의 재물을 얻을 것이니 게으름 피우지 말고 매사에 성심을 다해야 한다. 음력 1, 2, 3월은 시비나 구설수와 관련된 일로 피로한 가운데 좋은 일도 다소 생기니 길흉이 반반인 운세. 4, 5, 6월은 계획했던 일이 뜻밖에 틀어지고 시비수가 있으나 너그러운 마음을 가지고 행동을 하면 전화위복이 돼 결국 자신에게 이익이 돌아온다. 7, 8, 9월은 주위에 나를 도와주는 이가 많아지고 기쁨을 함께할 사람이 생기니 계획한 일에 자신감을 갖고 추진하도록 하라. 10, 11, 12월은 가벼운 마음으로 여행을 떠날 수 있겠으나 술로 인한 사고 및 망신수를 경계해야 한 해의 마무리를 무사히 끝마칠 수 있다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000579_500.jpg', 'mouse'),

('언제나 남의 떡이 더 커 보이는 법이니, 스스로에게 만족하는 법을 배우셔야 하는 한 달입니다. 남의 것만 쫓다가는 스스로에게 주어진 기회와 행운을 지나칠 수 있으니 뒤를 돌아보면 후회만 남게 됩니다. 대부분이 그렇듯, 실상 타인의 행운도 그리 대단한 것이 아니니 뒤에 손익을 계산해보면 당신과 그리 다를 것이 없습니다. 자신의 상황과 지니고 있는 행운과 능력에 만족할 수 있다면 평안을 찾을 수 있을 것입니다. 자녀를 기르고 있는 학부모라면, 다른 아이와 상대적인 비교로 섣불리 자녀를 다그치거나 실망하는 일이 없도록 하세요. 자식자랑이 늘 그렇듯 어느 정도는 부풀려지기 마련이고 그에게 없는 장점이 당신의 자녀에게는 또한 있는 것입니다. 자기가 가진 것에 대한 애정과 확신이 없다면 그 누구와 비교한다고 해도 상대적으로 초라해 보이게 되니 스스로에 대해 애정을 가져야 합니다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000580_500.jpg', 'cow'),

('특히 연애운이 좋은 시기이므로 연인에게 평소 하기 힘들었던 사랑의 말을 속삭인다면 둘의 사이가 돈독하고 애정이 깊어지게 될 수 있으니 가까운 곳으로 여행을 떠나는 것도 연정을 깊게 하기에 좋은 방법이라 하겠습니다. 관심이 있던 이성에게 대시하여도 괜찮은 결과를 얻게 될 수 있을 것입니다. 다만 판단력이 흐려지는 시기가 될 수 있으니 회사에서 진행하는 사업이나 학업과 관련한 일에 신중한 태도를 취하는 것이 좋습니다. 전체적으로 대인관계가 좋아지는 시기이니 동료 간의 의견 화합이 잘 되겠으므로 팀으로 진행하는 일을 하기에 유리한 시기라 하겠습니다. 다만 숨겨왔던 잘못이 드러나게 될 수 있는 시기이니 끝까지 숨기려 드는 것보다는 어느 정도 선에서 인정하고 물러나는 자세가 필요하다 하겠습니다. 이 때 원한 등을 품는 것은 앞으로 악운을 불러들일 수 있으니 주의하도록 하십시오. 금전운은 좋은 시기이니 손에 만지는 돈이 많겠고 돈 쓸 일이 줄어들게 되니 돈을 모으기에 적합한 시기입니다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000580_500.jpg', 'cow'),

('사유축(巳酉丑) 삼합에 해당하는 해이니, 함께 기쁨을 나눌 수 있는 사람이 곁에 생기겠다. 작년까지는 현명한 처세로 쉼 없이 달려왔다면 올해는 결과가 좋아 그 성과가 겉으로 드러나니 그동안의 고생을 보상받는 심정이겠다. 다만 장기적인 안목으로 일을 진행해야지 자칫 눈앞의 이익만 바라보다가 좋은 기운을 스스로 차버릴 수도 있으니 선택의 기로에서 신중함을 요한다. 거침없던 불길이 뜨끈히 온돌방을 데우니 직장, 사업, 가정이 화목하다. 음력 1, 2, 3월은 온순한 사람이 화를 내는 격이고 애정관계로 주위가 복잡하니 조심. 4, 5, 6월은 그동안 자신의 처세가 빛을 보게 되니 금전적으로 안정이 될 것이며 가을의 곡식이 쌓이듯 마음이 풍요롭겠다. 7, 8, 9월은 뜻밖의 시비가 발생해 해결이 어렵게 되니 말과 행동이 일치되도록 노력해야 길할 운세. 10, 11, 12월은 나를 필요로 하는 사람들이 많아져서 분주다사하나, 결과도 확실하니 결국에는 원하는 것을 얻을 수 있다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000580_500.jpg', 'cow'),

('일은 산더미같이 쌓여있는데, 어찌된 노릇인지 일에 손에 붙지를 않습니다. 자꾸 시간은 흘러가고 해야 할 일은 줄지 않으나, 초조하면서도 서두를 마음은 들지 않아 좋지 않은 상황이 정체될 것입니다. 당신에게 주어진 시간은 얼마 남지 않았는데 그에 비해 일은 끝이 보이지 않을 정도로 쌓였으니 애초에 힘든 일이기도 했거니와 당신이 게으름을 피운 까닭입니다. 이제는 마음을 다잡고 일에 정진하려 해도, 앞날을 생각하면 막막하여 절로 손에서 힘이 빠져나가니 곧 다시 어깨를 늘어트리고 체념하고 맙니다. 회피할 수 없는 일임은 당신이 더 잘 알 테이니, 오늘에라도 마음을 일신하여 힘을 내는 것이 필요합니다. 마음을 추스르지 못한다면 계속하여 부진의 늪에 빠지게 되니 스스로도 나태해지고 주변으로부터 신뢰를 잃게 될 것입니다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000581_500.jpg', 'tiger'),

('전체적으로 예술적인 감성이 커지는 달입니다. 창의적인 생각으로 번뜩이는 아이디어를 낼 수 있으나 이러한 생각을 실행으로 옮길 수 있는 행동력이 부족한 시기이므로 이에 유의하도록 하십시오. 다만 이러한 아이디어로 인하여 상사의 눈에 잘 들 수 있겠으니 앞으로 더 많은 일을 맡게 될 수 있겠습니다. 그러나 계획 없이 많은 일을 벌여서는 이로울 것이 없으니 이 점을 명심하고, 자신이 할 수 있는 범위에서 최선을 다하십시오. 또한 자신의 의견만 강하게 내세운다면 주변 사람과 충돌이 생길 수 있습니다. 언제나 상대방의 입장을 생각해보는 자세가 필요합니다. 이번달은 행운이 따르는 아주 좋은 달입니다. 가뭄에 힘찬 빗줄기가 쏟아지듯이 그 동안 당신에게 근심거리가 되었던 고민이 시원스럽게 풀리는 기분 좋은 달입니다. 모든 일은 잘 풀리고, 앞으로 당신이 살아가는 데 좋은 경험이 될 것입니다. 그러니 지금까지 힘들었더라도 마지막까지 최선을 다하는 모습을 보이십시오. 이 행운을 바탕으로 돈도 들어오고 여유도 생기게 됩니다. 재물운이 따라줄 수 있으니 일을 진행하는 데에 여유가 많겠고 고민할 것이 없겠습니다. 근심걱정이 공연한 것이나 마음을 괴롭게 한다면 주변인에게 충고를 부탁하는 것도 좋은 방법입니다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000581_500.jpg', 'tiger'),

('인사(寅巳)형살이 드는 해이니, 시비수나 사고수를 항시 조심해야 하는 해다. 가정 내에서도 변동이 있으며 타지에서의 생활을 할 수도 있어 미리 대비해야 하나, 이러한 계기가 본인에게는 발전의 밑거름이 되는 해이므로 건강문제만 신경 쓴다면 무리 없는 한 해를 보내게 될 것이다. 이상적인 목적에 몰두해 일이 잘 풀릴 듯 보이지만 현실을 되돌아보는 지혜 또한 발휘해야 함을 명심하자. 음력 1, 2, 3월은 숲속에 바람이 부니 소리만 높고 실속이 없을 수로 천리 길도 한 걸음부터이니 순서를 철저히 지켜 노력하면 성공할 수. 4, 5, 6월은 무더위에 부채를 잊고 나왔으니 방심은 절대 금물이라 승승장구하던 일을 한 박자 쉬어 감이 좋겠다. 7, 8, 9월은 앉아서 만리를 보는 격이니 하면 된다는 자신감으로 매사 전진하고 운기도 길하다. 10, 11, 12월은 건강에 유의하고 관재수와 음주운전에 유의하라.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000581_500.jpg', 'tiger'),

('너무 큰 욕심을 부리다가, 당신뿐 아니라 당신의 주변사람에게도 화를 끼치게 되는 한 달입니다. 확실치 않은 일에 현혹되어, 자꾸만 모험을 감수하게 되니 동료들과 가족들의 불안이 가시지를 않습니다. 당신은, 말하자면 사춘기의 열병과 비슷한 것을 앓고 있는 것이니 이성과 뚜렷한 계산보다는 마음과 근거 없는 낙관이 앞서 앞뒤 돌아보지 않고 돌진하고 있습니다. 요행히도 성공하면 다행이나 대개의 무모한 돌진이 그렇듯 실패가 다반사라 큰 손해를 보아 당신뿐 아니라 당신의 동료들에게도 손실을 끼치고 말 것 입니다. 안으로는 당신을 믿고 있는 가족들에게 곤경을 안겨주게 되니 잠깐 절제의 끈을 놓친 대가로 많은 것을 잃게 됩니다. 부디 스스로를 다스리는 법을 익혀야 훗날을 기약할 수 있는 한 달입니다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000582_500.jpg', 'rabbit'),

('직장 관계의 사람 사이가 벌어질 수 있는 시기이니 팀 프로젝트를 진행하는 것이 있었다면 조금 애로사항이 꽃피게 되는 시기라 할 수 있겠습니다. 그러니 팀원들에게 회식자리 등을 제공하여 줄 수 있는데 이 때 들어가는 돈을 아끼지 않는 것이 좋다 하겠습니다. 그러나 지나친 음주는 피하는 것이 좋으며 다음달을 생각하여 무리하게 몸을 혹사시키는 일은 피하도록 하시고, 운전을 하게 될 일이 있으면 대리 운전을 통하거나 가능하면 대중교통을 사용하는 것이 좋다 하겠습니다. 또한 평소에 비하여 사업 관련의 일이나 학업이 잘 진행되는 것을 체감할 수 있을 것으로 무리하게 일을 진행시킬 수 있으나 지나치게 무리하여 일을 하게 될 경우 나쁜 영향을 미치게 될 수 있으니 피하는 것이 좋겠습니다. 일을 가득 쌓아놓고 고민하기 보다는 우선 순위를 결정하여 하나하나 해결하는 것이 더 좋은 방법입니다. 재물운이 전체적으로 좋은 시기로서 횡재수 역시 있으니 기분전환 삼아 복권을 시도하거나 재테크 등을 시도하는 것도 좋겠습니다. 다만 들어오는 만큼 돈이 나갈 수 있는데 이 돈은 아끼지 않는 것이 미래를 생각할 때 더 좋은 방법이라 할 수 있습니다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000582_500.jpg', 'rabbit'),

('삼재에 해당하는 해이므로, 도모하려던 일이 중간에 파할 수도 있으나 인간관계를 원만히 유지하기만 한다면 크게 무리는 없겠다. 혹 타인이 감언이설로 자신을 속일 수도 있으니 주의를 요하며, 사업운은 대체적으로 좋으니 뚝심을 가지고 사리분별을 잘해야 할 것이다. 가정 문제에 다소 마찰이 예상되니 성급한 마음을 갖고 있으면 후회스러운 일이 생기겠고, 늘 주위를 살피는 버릇을 기르도록 하라. 음력 1, 2, 3월은 무엇을 하든지 자신감이 넘치고 승승장구할 것이나 자칫 교만한 생각을 갖고 있다면 반드시 실수해 자칫 쓴맛을 보게 될 운세. 4, 5, 6월은 하늘을 보고 소원을 기도하니 말 못할 고민이 서서히 해결돼가나 가정에 우환이 예상되니 잘 대처해야 한다. 7, 8, 9월은 길흉이 상반되니 피곤해도 상대의 비위를 맞추고 현상유지할 것. 10, 11, 12월은 돌다리도 두드려 보라고 계획했던 일을 성급히 결정하면 크게 후회하니, 꼼꼼하고 계획성 있게 일을 추진하고 시비, 구설수에 엮이지 않도록 근신해야 길하다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000582_500.jpg', 'rabbit'),

('솔깃한 감언이설에 쉽게 노출될 수 있으니 무엇이든 의심해봐야 하는 한 달입니다. 당신에게 친절하고 유리한 조건이라 해서 무조건 납득하고 좋아해서는 안될 것입니다. 무조건적인 호의라는 것은 없는 법이니 솔깃해 보인다고 해도 당신에게 원하는 바가 있어서 내건 조건일 뿐입니다. 투자라는 것이 그렇듯, 당신에게 쥐어준 것 이상을 당신에게서 가져가니, 시간이 지나면 분명 손해가 남게 됩니다. 귀가 얇아 고생을 하게 되니, 당신을 부추겨 목적한 바를 이루려는 수많은 의도에 농락당하게 됩니다. 대체로 유혹에 쉽게 빠질 수 있으니 마음을 굳건히 다지지 못한다면 섶을 지고 불에 뛰어드는 것과 마찬가지인 한 달입니다. 세상은 당신이 생각하는 것보다는 더 순수하지 못하니 조금 더 깊은 주의가 필요합니다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000583_500.jpg', 'dragon'),

('사업운에서 사공이 많아 배가 산으로 가는 격입니다. 여러 사람들로부터 다양한 의견을 듣는 것도 좋지만 주체적인 의식을 가지고 자신 마음의 목소리에 더 귀 기울여 보세요. 다른 사람들의 충고가 오히려 해가 되어 일을 그르칠 수 있으니 주의를 기울이시는 게 좋겠습니다. 특히 집단의 일에 있어 마찰이 인다면 올바른 방향으로 팀이 진척할 수 있도록 지도자 적인 면모를 과시해야 할 때로 보입니다. 한편 가족들에게 소원했다면 이번달은 가족들과 많은 이야기를 나누어 보세요. 냉담한 관계가 지속된 채 이 시기를 놓친다면 가정의 화목은 더욱 멀어질 지도 모릅니다. 가족들에게 먼저 다가가시는 것이 좋겠습니다. 재물운에 있어서는 남쪽에 가면 손해를 볼 것이니 피하시는 것이 좋겠습니다. 운이 따라주지 않으니 무리한 투자나 소비는 금물입니다. 학업운 역시 따라주지 않으니 중요한 시험이 있다면 이 시기는 미루고 성공을 위해 다음을 기약하시는 것이 좋겠습니다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000583_500.jpg', 'dragon'),

('권력과 재물이 좋아지니 일신상에 귀인의 조력이 생겨 행운이 따르나, 변화무쌍한 운세이기에 사업이나 직업적으로 복잡한 일이 생기겠고 가정 내에도 변화가 예상된다. 인간관계가 복잡하고 분주해 갈피를 못 잡는 이도 종종 있겠으나 공과 사의 구분을 잘하고 페이스를 유지한다면 결과적으로는 대체로 좋은 일이 많이 생길 운세. 음력 1, 2, 3월은 뜻이 있는 곳에 길이 있으니 한번은 좋을 것이며 한번은 나쁠 것이다. 마음을 잘 추스르고 중도를 잃지 않는 지혜가 필요한 때. 4, 5, 6월은 망망대해에 산들바람이 불어오니 순풍에 돛을 단 격으로 부동산운이 길하고 사업상에도 큰 이익이 생기겠다. 7, 8, 9월은 눈뜨고 코 베어 가듯이 오랫동안 공들인 것을 잃을 수 있으니 실속을 차려야 하고 방심하면 큰코다칠 수. 10, 11, 12월은 자기 자신만을 고집하고 자신만 믿다가 앞뒤가 막히는 격이니 항상 겸손한 자세가 필요하고 저축해야 길할 운세.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000583_500.jpg', 'dragon'),

('분명 당신을 가로막고 있는 문제가 있으나, 그 자세한 정황을 살필 수가 없어 그 불확실함이 가장 큰 장애가 될 것입니다. 마치 안개를 헤쳐나가는 것처럼 시야가 흐리고 불안하니, 언제 어떤 위험이 당신을 가로막을 지 몰라 마음이 편치 않습니다. 또한 주변사람들 중 누가 당신의 편이고 누가 당신의 적인지 판단하기 힘드니 마음을 털어놓을 수가 없어 불안함이 더욱 쌓일 것입니다. 무작정 행동하는 것은 당신의 곤란함을 알려주는 결과밖에 초라하지 않으니 천천히 당신이 처한 상황에 대해 파악하는 시간을 갖고 무엇이 당신을 둘러싼 위협인지를 깨달아야 합니다. 외연적인 요인과 내재적인 불안을 파악하고 나서야 안개를 걷어낼 수가 있으니 성찰의 시간을 가지는 것이 반드시 도움이 될 것입니다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000584_500.jpg', 'snake'),

('운이 떨어지는 달이니 체력과 지력, 재력이 잘 조화되지 못하는 시기라 하겠습니다. 이번달은 어떤 일을 해도 무난하게 이루어지기는 힘들 것이니 집 안에서 몸을 휴식하며 미래에 대한 계획이나 목표를 생각해보는 것도 좋을 것입니다. 마음이 예민하여 신경이 달카로우니 어떤 것을 해도 흥이 나기 힘들 것이며 자신이 한 말 한 마디에 다른 이가 상처 받을 수 있으니 언사를 냄에 있어 특히 주의할 수 있어야 할 것입니다. 이 때 꼬이게 된 사람의 관계는 당분간은 풀기 어려울 것이라 할 수 있습니다. 또한 무엇인가를 새로이 시작하는 것보다는 하던 일에 매진하는 것이 좋겠습니다. 다이어트나 금연과 같은 자신과의 약속을 하는 것도 좋겠으나 쉽게 지키기는 어려울 것입니다. 이러한 기운은 의욕만으로 이어나가기 어려우니, 확실하게 규율을 정하거나 계획을 짜서 거기에 따라 실천하는 것이 좋습니다. 건강운은 나쁘지 않고 재운 또한 좋습니다. 모든 것이 순조롭게 흘러가니 가정에도 또한 웃음소리가 가득하리라 여겨집니다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000584_500.jpg', 'snake'),

('집안의 우환이 호전되어 근심 없는 한 해를 보내겠다. 사업운도 길하니 자신의 능력이 인정을 받아 결실이 좋고, 애정운도 길해 배필을 만나게 되니 인연을 소중히 여기도록 하자. 다만 후반부로 갈수록 근심거리가 생겨서 마음이 붕 뜨게 될 수 있으니 초심을 잃지 말고 자만해 후회하는 일을 만들지 말라. 음력 1, 2, 3월은 겸손하지 못하고 설치면 화를 면치 못하게 되니 각별히 신경 써야 할 운세. 4, 5, 6월은 무해무득한 때이니 현상유지를 위해 힘써야 하며 열심히 일한 만큼은 소득을 거둘 것이니 무리하고 성급한 결정은 유보함이 좋다. 7, 8, 9월은 관재 구설수를 조심해야 하며 바람기로 인한 망신살이 예상되니 ‘사필귀정’이라는 말을 명심해 애써 쌓은 명예를 잃지 않아야 한다. 10, 11, 12월은 과욕을 부리니 투자로 손해 볼 운세도 있으니 주의해야 될 운세.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000584_500.jpg', 'snake'),

('특히 부부 간의 운에 역운이 끼이는 시기이므로 부부간의 다툼이 발생할 수 있는 시기이며 또한 자녀가 좋지 않은 소식을 들고 올 수 있으니 마음의 준비를 해 두는 것이 좋은 달이라 하겠습니다. 연인간의 운 역시 마찬가지로 사소한 일로 다툼이 발생할 수 있으며 서로 간에 오해가 발생할 수 있으니 가능하면 서로 마음을 열고 문제를 해결할 수 있도록 하는 것이 가장 좋은 방법이라 하겠습니다. 가정 내의 운도 좋지 않은 편이니 가정 내에 반목이 생길 수 있으므로 가정 내에서 특히 언사를 조심하도록 하는 것이 좋겠습니다. 다만 직장 내의 인간관계는 좋은 편으로 팀 프로젝트 등을 진행하는 것에 있어 무리가 없고 오히려 좋은 결과를 가져오게 되겠습니다. 또한 건강운 역시 따라주는 시기이므로 미루어 두었던 일을 하기에 적합한 시기라 하겠습니다. 그러나 젊은이의 경우 몸이 갑자기 추워지고 감기기운이 있을 수 있으니 밖으로 나돌기보다는 집안에 머물며 따스한 음식을 취하여 양기를 보충하는 것이 더 좋을 것입니다. 재물운에는 길조가 보이고 있으니 투자하고 있는 것에서 좋은 소식을 들을 수 있고 사업의 계약이 잘 이루어지게 되겠습니다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000585_500.jpg', 'horse'),

('때로는 당신을 드러내고 자랑하는 것보다, 감추고 물러나는 것이 더 도움이 될 때가 있습니다. 스스로의 장점을 PR하고 사람들에게 인지시키는 것은 물론 중요한일이지만, 그것이 과하면 아니하는 것만 못하니 지나친 광고는 짜증을 불러일으키는 까닭입니다. 가끔은 행동함에 있어 자신을 감추고 뒤로 물러서는 양보의 자세를 견지하는 것도 좋습니다. 양보는 당신을 여유로운 사람으로 비치게 하며, 사람들은 당신에게 감사와 경외의 시선을 갖게 될 것 입니다. 비록 다소 손해 보는 입장에 서게 될지라도 더 여유를 가진 당신이 사양하고 양보하는 것이 멀리 보아서는 이득을 가져올 것 입니다. 기회는 또 다시 찾아오게 마련이고, 한번 쌓아 다져진 인망은 두고두고 당신에게 좋은 인상을 불러일으키며 신뢰를 헌사 합니다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000585_500.jpg', 'horse'),

('책임이 막중해지고 본인이 남을 더욱 챙겨야 하는 한 해겠다. 몸이 힘드니 마음도 힘들고 과로로 인해 건강이 악화돼 연초에는 건강검진을 반드시 받아 봐야 할 것이며, 충분한 휴식 및 여가 활동을 틈틈이 해 스트레스가 쌓이는 것을 방지하라. 술 조심은 물론이고 흡연자는 담배도 끊어야 하며 유기농식품과 비타민 섭취 등을 게을리하지 말아야 할 것이다. 자신을 먼저 챙겨야 남을 도와줄 수 있고, 좋은 기운을 다 감당할 수 있다. 음력 1, 2, 3월은 벚꽃에 꾀꼬리가 날아드니 막히던 일이 잠시나마 숨통이 트이니 매사를 성실히 임하면 주변 사람들이 나를 도와줄 것이다. 4, 5, 6월은 믿는 도끼에 발등 찍힌다고 내 편인줄 알았던 사람에게 배신당할 수 있으니 자기 것에 손해를 보지 않도록 미리 대처해야 한다. 7, 8, 9월은 매매와 이사는 길하며 자신에게 이익이 생겨서 마음이 가볍지만 새로운 환경에 적응하기 여간 쉽지 않겠다. 10, 11, 12월은 문서운이 길해 부동산 및 매매가 활발해 지고 시험이나 승진의 결과를 기다리는 사람들은 기쁜 일이 있겠다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000585_500.jpg', 'horse'),

('전체적으로 건강운이 낙조를 띠고 있으니 몸이 찌뿌둥하고 이에 따라 기분도 우울해지는 시기가 된다 하겠습니다. 어떤 일을 해도 기운이 나지 않고 일의 진행이 뭐가 막힌 듯 잘 진행되기 어려우니 기분 전환차 가벼운 운동을 하기에도 적합하고 가까운 곳으로 산책을 나가는 것도 괜찮은 방법이라 하겠습니다. 그 동안 흐지부지했던 사업에 투자하게 된다면 마무리를 쉽게 지을 수 없을 것이니 하던 일에 충실하도록 하는 것이 좋겠습니다. 재물운이 약간 하락세를 타고 있으나 크게 심각하지 않으니 투자를 하는 분들도 너무 몸을 사릴 이유는 없을 것으로 보입니다. 다만 들어오는 재물을 다른 이가 부러워하고 시기할 수 있으니 가진 물건에 대해 자랑하는 것은 하지 않는 것이 좋겠습니다. 좋은 일이 있다 하더라도 이번달은 무심한 표정으로 지내는 것이 운을 불러들이는 길입니다. 들어오는 돈은 평소와 다름이 없겠으나 돈을 써야 하는 일이 늘어나게 되니 소비하는 모양새가 평소보다 폭이 커지게 될 수 있으므로 유의하는 것이 좋습니다. 공치사를 남발하게 될 경우 잘 풀리던 일도 꼬이게 될 수 있으니 확실하게 거절의 의사를 밝힐 수 있도록 하는 것이 좋겠습니다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000586_500.jpg', 'sheep'),

('일이 당신의 생각만큼은 잘 풀리지 않는 한 달 입니다. 꼬인 일만큼이나 마음도 꼬이게 되니 자꾸만 다름 사람에게 짜증을 내게 됩니다. 비록 이렇게 저렇게 마음에 차지 않는 사람이 있겠지만, 그를 탓하는 것보단 조금 더 엄격한 스스로에 대한 반성이 필요할 것입니다. 실패의 후회는 언제나 늦고, 그 뒤의 싸움은 언제나 무익한 법이니 타인에게 실패의 핑계를 돌리고 자신을 위로하는 것은 비겁한 일일 뿐입니다. 먼저 스스로의 잘못과 미숙을 반성하고 독려하는 것이 필요할 것입니다. 자신의 실수를 겸허히 받아들이고 그것을 고칠 수 있다면, 다음 번의 일은 좀 더 순조로울 것임에 틀림없습니다. 사후처리를 두고 쉽게 타인과 다툴 수 있으니 여유로운 마음과 양보하는 마음이 무엇보다도 필요한 한 주 입니다. 들어갈 때와 나올 때의 마음이 다른 것은 사람의 슬픈 생리이니 당신에게 섭섭함이 많이 주어지는 한 달 일지라도 크게 낙담하는 일이 없도록 하십시오.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000586_500.jpg', 'sheep'),

('삼재에 해당하는 해이므로 이상만 추구하다 스스로 함정에 빠지는 격이니 올해는 신중을 기해야 한다. 시비는 큰 해가 되니 절대 금물이고 문서에 유의하고 애정은 허무감 느낀다. 삼재가 발동하니 건강, 교통사고, 여행 불리, 사업도 불길하니 매사에 조심해야 할 운세. 음력 1, 2, 3월은 친구나 사회로부터 오해가 생길 운세이니 신중한 처세가 필요할 때. 4, 5, 6월은 조력자가 도와주니 대체로 길하나 문서에 유의할 수. 7, 8, 9월은 갈 길은 바쁘고 마음은 조급한데 준비가 잘 안되고 여건이 여의치 않으니 공연히 불안할 수. 10, 11, 12월은 행운의 열쇠를 얻고 여행을 떠나는 운세이나 불조심할 운세. 건강만 조심하면 유종의 미를 거둘 수있다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000586_500.jpg', 'sheep'),

('역운이 끼어 실패가 많이 따르니 일을 구해도 이루어지지 못할 형세입니다. 이 시기에는 조바심을 가지고 성급하게 서두를 시에는 주위의 다른 사람에게 해가 끼칠지도 모르니 주의해야 하겠습니다. 당장의 가시적인 성과를 얻으려 하기보다는 미래를 위한 점진적 발전의 토대를 마련하는 것이 좋겠습니다. 금전운에 있어서도 새로운 시도들이 빛을 발하지 못할 수도 있으니 너무 큰 기대를 거는 것보다는 단조로움을 피하기 위한 변화 정도로 일을 진척시키는 것이 큰 손해를 피할 수 있는 방도가 될 것입니다. 명예운 역시 따라주지 않아 하는 일마다 고배를 마실 수 있고 주위 사람들로부터 악담을 들을 수도 있으나 이에 낙심하기 보다는 차분한 마음가짐으로 때를 기다리는 편이 좋겠습니다. 감성운 역시 낙조를 띠어 주변 사람들에게 신경질적인 반응을 하게 될 수도 있지만 이는 절대로 피해야 할 것입니다. 다른 사람들과의 관계에 있어서도 신중한 언행으로 마찰을 무마하시는 편이 낫겠습니다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000587_500.jpg', 'monkey'),

('당신 혼자서는 아무것도 이룰 수 없다는 것을 깨닫고야 마는 한 달입니다. 당신의 힘찬 패기와 추진력은 산이라도 허물 것 같지만, 자꾸 주위의 방해에 부딪치고 힘에 부치니 초심은 온데간데 없고 지친 숨만 가빠지게 됩니다. 성공을 나누기 싫다는 이기적인 생각을 하기보다는, 힘을 합쳐 더 수월하게 목적지에 다다르려는 타협이 필요한 한 달입니다. 주위의 도움을 적극적으로 구하는 것이 이로우니, 나누어 주어야 하는 당신의 이득의 몫보다 도움을 통해 덜게 되는 수고가 훨씬 큰 까닭입니다. 괜한 욕심에 고집을 피우다가는 아무것도 이루지 못하는 무익한 한 달이 되고 말 테니 적당한 양보와 타협, 나눔의 마음이 무엇보다 필요한 한 달입니다. 세상은 사람 홀로 살아갈 수 있을 만큼 친절한 곳이 아니니 이를 잘 알아야 무언가를 도모할 수 있을 것입니다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000587_500.jpg', 'monkey'),

('사신(巳申)형살과 육합이 함께 드는 해이니, 사람을 잘 만나면 금년은 길한 운세이나 믿었던 도끼에 발등 찍히는 수도 있겠으니 감언이설에 주의해야. 사업은 호운이 도래하고 직업운도 행운이 따르는 운세. 가정은 평온하나 송사나 시비 문제로 고민발생. 여행과 술을 조심하라. 나의 편을 만들되 자신만의 소신을 지키는 것이 중요한 해이니, ‘감언이설’이라는 말을 항상 마음속에. 음력 1, 2, 3월은 어항 속에 갇힌 금붕어와 같아 물에 독이 있는 격이니 일보 양보함이 길하고 미혼자는 성혼할 수. 4, 5, 6월은 마음의 창문이 활짝 열리는 격으로 하고자 하는 일을 밀고 나가면 대체로 좋다. 7, 8, 9월은 불안할 수로 금전거래 분명하게 해야 하고 부모 궁에 근심 있을 수이니 후덕을 베풀어라. 10, 11, 12월은 과욕을 부리니 이성 문제가 있고 투자로 손해 볼 운세도 있으니 주의해야 할 운세.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000587_500.jpg', 'monkey'),

('직장보다는 가정에 몸을 두는 것이 좋은 운을 불러오는 시기로 가정에 몸을 맡긴다면 몸이 편안해지게 되겠습니다. 직장에서 안 좋은 운이 발생할 수 있으니 정신적으로 힘든 한달이 될 수 있으나 집안에 들어오게 된다면 만사가 형통하게 될 수 있으므로 직장에서 중요한 일을 진행하게 될 일이 있다면 이번달은 잠시 미루어 두는 것도 나쁘지 않은 일이라 하겠습니다. 집에서 충분한 휴식을 취하도록 한 이후 업무로 돌아가게 된다면 일의 효율이 훨씬 올라가게 될 것이니 쉬는 것이 가장 좋은 지름길이라 할 수 있을 것입니다. 지력이 떨어지는 시기이니 복잡한 일은 이번달 처리하는 것보다 다음달로 미루어 두는 것이 더 나은 결과를 가져올 수 있을 것입니다. 다만 지나치게 미루어 오는 일은 악운을 불러들일 수 있으니 너무 일을 미루어두지 않도록 하는 것이 좋으며 건강운이 평소보다 좋으므로 이러한 일을 진행함에 있어 도움이 될 수 있을 것으로 보입니다. 다만 정신이 지칠 수 있어 다른 이에게 마음에 없는 소리를 할 수 있으니 언사에 있어 주의하도록 하는 것이 좋습니다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000588_500.jpg', 'chicken'),

('당신에게 적지 않은 행운이 찾아오게 되나, 입 밖으로 그것을 자랑하는 일은 없도록 하셔야 할 것입니다. 고로 입이란 몸을 망치는 문이니, 말을 꺼내면 꺼낼수록 화를 부르고 복을 달아나게 합니다. 당신의 세속적 성공은 쉽게 질투를 불러일으키고 질투라는 감정은 쉽게 적대감으로 변질됩니다. 괜한 적과 비판적인 방관자를 만들고 싶지 않다면 아무쪼록 당신의 마음을 추스르고 말을 아끼는 것이 좋습니다. 윗사람의 총애를 받기 시작했다면, 그 전보다도 몸가짐을 조심해야 할 필요가 있습니다. 당신에게 주어지는 기회와 칭찬만큼 주위의 시기와 질투, 쓴 소리도 늘어날 것입니다. 당신 스스로의 분발과 능력만으로는 온전히 살아갈 수 없는 것이 바로 사회이니 늘 반대편을 만들지 않도록 조심하여야만 합니다. 당신의 자그마한 성공에 취해있다간 눈덩이처럼 적이 불어날 수는 한 달이니, 늘 말을 아끼고 주위를 살펴야 할 것입니다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000588_500.jpg', 'chicken'),

('사유축(巳酉丑) 삼합이 드는 해이니 주위에 협조자가 있으니 기회가 좋으며 위기를 기회로 만드는 행운의 찬스가 오고 직업으로 고민거리가 해소된다. 재물도 많이 들어오나 지출 또한 많을 수이니 쓰임에 신경 쓰고 가정에는 서운함이 있으나 애정은 만족한다. 한 해를 되돌아 봤을 때 대체로 경험이 많이 쌓이고 결과론적으로 긍정적인 방향으로 흘러가고 실속을 챙길것이니 자신감을 가지고 원하는 바를 이뤄라. 음력 1, 2, 3월은 돈이 들어와 기분이 좋으나 보증은 절대 금물. 액운이 물러나고 밝은 운기가 도래하고 행운이 있을 수. 4, 5, 6월은 어두운 방에서 빛을 보게 되는 격으로 예상외로 길할 운세. 7, 8, 9월은 정들었던 고향 이별하는 격으로 어려운 여건 속에서 새로운 길을 찾는 운세. 10, 11, 12월은 봉황새가 서쪽하늘에 날아오르니 행운이 따르고 이득을 볼 운세.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000588_500.jpg', 'chicken'),

('전체적으로 운이 호조를 띠고 있으나 마음 한 구석이 공허하니 이를 기쁘게 받아들이지 못할 수 있겠습니다. 이러한 자세는 들어오던 복도 쫓아낼 수 있으니 마음을 가볍게 가지고 일을 있는 그대로 좋게 받아들이는 열린 마음 가짐을 가져야 한다 하겠습니다. 인간관계에 있어 사소한 일로 의견의 충돌이 발생할 수 있으니 이를 유의하여 언행을 삼가도록 하는 자세가 필요하겠습니다. 건강운과 재물운이 호조를 띠고 있으니 하고자 하는 일은 무엇이든 할 수 있으므로 마음을 기쁘게 먹을 수 있도록 주의하여야 할 것입니다. 마음에 두고 있는 일이 있었다면 일을 시도하기에 적합한 시기이므로 발로 뛰어 좋은 조건을 찾아낼 수 있도록 하고 여러 사람들에게 의견을 물어 종합적으로 판단을 내려야 하겠습니다. 가장 중요한 것은 자신의 신념이니 다른 사람의 말에 너무 휘둘리지 않도록 하는 것이 중요합니다. 사업 상의 문제로 인하여 다른 이들과 경쟁을 하게 되겠으나 경쟁에서 성공하게 되는 것은 당신이니 너무 조바심내지 않도록 하십시오. 그러나 준비가 미비할 경우 일을 얻어내지 못할 수 있으니 중요한 것은 신념에 맞추어 자신의 성실성을 보여주는 일이라 하겠습니다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000589_500.jpg', 'dog'),

('주변 사람들과 다툼이 잦아지는 한 달 입니다. 대개 당신의 잘못으로부터 비롯된 다툼은 아니나, 너그러운 아량으로 용서하는 자세가 필요한 한 달입니다. 지는 게 이기는 것이라는 말도 있듯이, 비단 모르는 사람뿐 아니라 가까운 사람일수록 양보와 용서가 미덕인 법입니다. 소모적인 감정싸움은 당신에게 어떠한 도움도 되지 않는 것이 그 이유이니 자칫 소중한 시간과 에너지를 쓸데없는 곳에 쏟아 붇게 되기 때문입니다. 따라서 다소의 부당한 대우와 짜증쯤은 아무렇지 않게 넘기는 호기가 필요할 것입니다. 주변 사람의 용서에 인색하면 안 되는 한 달이니, 아무리 큰 잘못을 저질렀다 하더라도 자신의 잘못을 깨닫고 있다면 마지못한 척 들어주는 것이 좋습니다. 워낙 많은 사람과 부대끼게 되는 한 달이니, 늘 여유와 용서를 마음에 간직한 채 살아가야 할 것입니다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000589_500.jpg', 'dog'),

('사술(巳戌)원진이 드는 해이므로 웃어른이나 가정에 우환이나 상복 입을 수이니 세심한 보살핌이 필요라고 매사 신중을 기할 것. 사업은 반흉반길한 운세이고 재수도 반길반흉한이며 애정은 불화가 있을 듯. 특히 관재나 송사에 휘말리지 않도록 주의가 요망됨. 운전을 주의하고 술을 조심할 것. 남들과의 말싸움을 특히 조심해서 기분 나쁜 상황을 만들지 않는 것이 중요하다. 음력 1, 2, 3월은 양적인 증가보다 질적 향상에 주력하라. 특히 여행이나 이사하면 길할 수. 4, 5, 6월은 타인으로부터 원성을 듣고 협조를 받지 못하니 자력으로 밀고 나가야 할 수로 부동산 쪽으로 관심을 두면 길하다. 7, 8, 9월은 사철에 푸른 대나무와 같은 성격을 가진 격으로 일에 몰두하니 소기의 목적 달성할 수. 10, 11, 12월은 도로는 넓어도 행인과 차량이 없는 격으로 자기반성해야 하고 앞뒤를 되돌아보고 정리해야 길할 운.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000589_500.jpg', 'dog'),

('재물운이 잠시 쇠하여 금전적인 손실을 입게 될 것으로 보입니다. 용돈을 받는 사람이라면 용돈이 끊길 수 있고 아르바이트 하는 곳이나 직장에서 지급되는 돈이 한 두달 정도 늦어질 수 있으니 돈을 계획적으로 사용하고 비상금을 남겨두는 것이 좋겠습니다. 또한 문서와 관련된 일을 체결하는 데에도 큰 주의를 기해야 하겠습니다. 이 시기에 투자를 행하는 일은 위험이 따를 것 같습니다. 중요한 계약이라면 가족이나 주변 사람들과 상의하여 신중한 선택을 하시기 바랍니다. 반면 건강운이 따라주어 정신과 마음이 풍요롭고 몸도 고단함을 덜게 되어 활기차고 능동적인 자세로 생활에 임하시게 될 것입니다. 긍정적인 마인드를 가지고 매사에 임하신다면 주변 사람들과의 관계 개선에도 긍정적인 효과를 끼칠 것입니다. 학업운 역시 나쁘지 않아 시험이나 관운이 있을 것으로 보이니 그 동안 미뤄왔던 시험에 도전해보는 것도 괜찮겠습니다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000590_500.jpg', 'pig'),

('당신의 지나친 호승심과 과욕은 주변 사람들을 힘들게 만들 것입니다. 당신의 지칠 줄 모르는 질주는 동료들을 힘들게 합니다. 의욕과 활력은 늘 샘처럼 솟구치는 것이 아니니 움직일 때와 그칠 때를 잘 안배해주지 못한다면 모두 지쳐버리고 말 것입니다. 당신이 중요한 위치에 있으면 있을 수록 당신의 사람들을 잘 살피는 자세가 필요하니, 그것은 진취적인 태도만큼이나 중요한 리더로서의 자질입니다. 그칠 줄 모르는 당신의 욕심이 타인에게 좋지 않은 인상을 줄 지도 모릅니다. 대상이 무엇이 되었든 과욕은 언제나 좋지 않은 결과를 부르니, 스스로 냉정히 판단하여 정도 이상의 것은 원하지 않는 절제가 필요합니다. 무리한 결과를 바라고 일을 벌이다가 많은 것을 놓칠 수 있는 한 달이니 현명하고 성숙한 자세가 요구되는 한 달입니다.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000590_500.jpg', 'pig'),

('사해(巳亥)충살과 삼재가 함께 드는 해이니, 그동안에 묵혀 있던 문제들이 겉으로 드러나면서 마무리가 된다. 인간관계와 재정적인 부분, 건강상의 문제, 직위 등이 해당되며 경거망동하지 말고 항상 겸손한 자세를 취해야 이로울 것이다. 여행이나 출장등 이동수에는 사고의 위험이 있으므로 항시 경계하는 태도를 유지해야 하며 정신상태를 방해하는 것들을 원천 차단하는 방식을 취하는 것도 방법이다. 시비 다툼수 또한 자리를 피하는 등 미리 미리 예방 조치를 해야 한다. 음력 1, 2, 3월은 피로한 가운데 길고 먼 여정을 마치고 집으로 돌아와 잠시 휴식이 필요한 운세. 4, 5, 6월은 무엇보다도 현실에 충실하지 않으면 막대한 손해가 예상되고 애정은 갈등이 생길 수 있으니 사전에 방지하는 것이 길하다. 7, 8, 9월은 지나친 관심은 오히려 해가 될 수도. 애정은 대체로 길할 수. 10, 11, 12월은 원수와 화해하니 매사가 원만해지고 문서나 직업으로 고민이 해결되리라.',
'https://image.utoimage.com/preview/cp780416/2014/12/201412000590_500.jpg', 'pig');
